css:
@-moz-document domain("www.mywriteclub.com") {
body {
    background: url('http://i.imgur.com/TQFBvo4.jpg') no-repeat center center fixed;
}
.banner {
    background: #363330;
}
.breadcrumb-container {
    background: rgba(255, 255, 255, 0.9);
}
.main-body-container > .inner-container-app,
.main-body-container > .inner-container-mktg,
.jumbotron {
    background: rgba(255, 255, 255, 0.8);
    color: #000000;
    padding-top: 10px;
    padding-bottom: 10px;
    overflow: auto;
}
.main-body-container > .inner-container-app {
    padding-left: 30px;
    padding-right: 30px;
}
/* get rid of ugly bg on main domain */
.inner-container-mktg .jumbotron {
    background: none;
}
#work-area-settings .btn,
#cloud-storage {
    margin: 0px;
    background: rgba(255, 255, 255, 0.7);
    padding: 1px 3px;
    border-radius: 3px;
}
#cloud-storage {
    padding: 1px 6px 1px 0px;
}
span.fa-wrench,
span.fa-dropbox,
span.fa-question-circle {
    color: #4d4d4d;
}
span.fa-wrench:hover,
span.fa-dropbox:hover,
span.fa-question-circle:hover {
    color: #025454;
}
.ta-text {
    background: white;
    color: #000000;
    font-family: Open Sans, sans-serif;
    font-size: 1em;
    line-height: 1.3em;
    text-align: justify;
}
.ta-text p {
    text-indent: 2em;
}
h4,
section.help-section,
div.footer,
div.word-count,
span.sprinter-name,
button.sprinter-status,
span.sprinter-status,
span.progress-star,
div.comment-footer form input[type="Submit"] {
    color: #FFFFFF !important;
}
section.help-section a {
    color: #FFFFFF;
    text-decoration: underline;
}
section.help-section a:hover {
    color: #009999;
    text-decoration: underline;
}
h1,
h2,
legend {
    color: #363330 !important;
}
legend {
    border-bottom: 1px solid #999999;
}
}
