css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mastodon.social")
{  
  /* General */
  body
  {
    color: #606984;
  }
 
  .ui
  {
    background-color: #353643;
  }
 
  .getting-started p
  {
    color: #606984;
  }
 
  /* Drawer */
  div.drawer__header
  {
    background-color: #353643;
  }
 
  a.drawer__tab
  {
    color: #5DA7C2;
  }
 
  a.drawer__tab:hover
  {
    background-color: #F4F4F2;
    border-radius: 60px;
  }
 
  div.drawer__inner
  {
    background: #F4F4F2;
  }
 
  .drawer__inner
  {
    background-color: #A3D6E8;
    border-radius: 10px 10px 10px 10px;
  }
  
  .drawer__inner.darker
  {
    background-color: #F4F4F2;
  }
  
  /* Status */
  .status
  {
    color: #555555;
    border-bottom: 1px solid #DADADA;
  }
  
  .status__content .status__content__spoiler-link, .reply-indicator__content .status__content__spoiler-link
  {
    background: #DADADA;
  }
 
  .status__content .status__content__spoiler-link:hover, .reply-indicator__content .status__content__spoiler-link:hover
  {
    background: #BBBBBB;
  }
  
  .detailed-status, .detailed-status__action-bar
  {
    background: #F4F4F2;
  }
 
  .detailed-status__action-bar
  {
    color: #F4F4F2;
    border-top: 1px solid #DADADA;
    border-bottom: 1px solid #DADADA;
  }
 
  .status__display-name strong, .account__display-name strong, .detailed-status__display-name strong
  {
    color: #5DA7C2;
  }
 
  .detailed-status__display-name span
  {
    color: #606984;
  }
 
  .status__content a, .reply-indicator__content a
  {
    color: #5DA7C2;
  }
  
  .reply-indicator
  {
    background-color: #BACED9;
  }
  
  /* Search */
  .search__icon .fa.active
  {
    opacity: 0.5;
  }
  
  .search__icon .fa
  {
    color: #353643;
  }
  
  .search__input
  {
    border-radius: 5px 5px 5px 5px;
    color: #353643;
    background: #F4F4F2;
  }
 
  .search__input:focus
  {
    background: white;
    box-shadow: 0px 0px 5px 0px rgba(150,225,255,0.75);
  }
  
  .search-results__header
  {
    background: #5DA7C2;
    color: #F4F4F2;
    border-bottom: 1px solid #DADADA;
  }
  
  .search-results__section
  {
    background: #F4F4F2;
  }
  
  .search-results__hashtag, .search-results__hashtag:hover
  {
    color: #5DA7C2;
  }

  /* Column */
  .column
  {
    width: 400px;
  }
 
  div.column-header
  {
    border-radius: 10px 10px 0px 0px;
    background: #5DA7C2;
    color: #F4F4F2;
  }
 
  .column-link
  {
    background: #f4f4f2;
    color: #353643;
  }
 
  .column-link:hover
  {
    background: #96E1FF;
    color: #353643;
  }
 
  .collapsable-collapsed, .column-back-button
  {
    border-radius: 10px 10px 0px 0px;
    background: #5DA7C2;
    color: #F4F4F2;
  }
 
  .column-icon-clear
  {
    background: #5DA7C2;
    color: #F4F4F2;
  }
 
  .column > .scrollable
  {
    background: #F4F4F2;
    border-radius: 0px 0px 10px 10px;
  }
 
  .scrollable.optionally-scrollable
  {
    background: linear-gradient(#F4F4F2, #5DA7C2);
  }
 
  /* Account */
  .account
  {
    border-bottom: 1px solid #DADADA;
  }
 
  .account__action-bar__tab strong
  {
    color: #606984;
  }
 
  .account__action-bar
  {
    border-top: 1px solid #DADADA;
    border-bottom: 1px solid #DADADA;
  }
 
  .account__action-bar__tab
  {
    border-left: 1px solid #DADADA;
  }
 
  .notification__display-name:hover
  {
    color: #555555;
  }
 
  .icon-button.active
  {
    color: #5DA7C2;
  }
 
  .account__header, .account__header__username
  {
    color: red;
  }
 
  .navigation-bar strong
  {
    color: #555555;
  }
 
  /* Button */
  .button
  {
    background: #5DA7C2;
  }
 
  .button:active
  {
    background: #5DA7C2;
  }
 
  .button:hover
  {
    background: #7CC2DB;
  }
 
  .button:focus
  {
    background: #5DA7C2;
    box-shadow: 0px 0px 3px 0px rgba(0,0,0,0.5);
  }
  
  /* Dropdown */
  .column-settings--outer
  {
	  background: #5DA7C2;
  }
  
  .column-settings--section
  {
    color: #F4F4F2;
  }
  
  .setting-toggle
  {
    color: #F4F4F2;
  }
  
  .collapsable
  {
    background: #5DA7C2;
    border-radius: 0px 10px 0px 0px;
  }
  
  .collapsable:hover
  {
    background: #74BFDB;
    border-radius: 0px 10px 0px 0px;
  }
  
  .setting-text
  {
    color: #DADADA;
  }
}
