css:
@-moz-document url-prefix("http://tazzon.free.fr/dactylotest/bepodactyl/") {
/*esthétique*/
	/*en haut+texte*/

body {
    background-color: #282830;
}
h1 {
    font-size: 0px;
    visibility: hidden;
    margin-bottom: -45px;
    margin-top: 20px
}
h1::after {
    content: '/*[[titre]]*/"';
    color: white;
    font-size: 49px;
    font-style: oblique;
    font-weight: normal;
    visibility: visible;
    text-shadow: 3px 3px #953163;
}
.head {
    visibility: hidden;
    font-size: 0px;
}
	/*couleur des cases*/

.key {
    -webkit-filter: brightness(55%) hue-rotate(235deg)!important;
}
.rd_txt {
        -webkit-filter: brightness(55%) hue-rotate(235deg)!important;
    max-width:420px;
}
.rd_txt span{
    color:white !important;
}
.main {
    /*boîte + écriture*/
    
    background-color: white;
    font-family: noto;
    padding: 20px;
    border-radius: 20px;
}
.result {
    /*écriture*/
    
    font-family: noto;
    font-style: oblique;
}
p:first-line {
    content: 'a'
}
input[type=button] {
    margin-left: 5px;
    margin-right: 5px;
    font-family: noto;
    background-color: #eee;
    border: 1px transparent solid !important;
    border-radius: 15px;
}
	/*textbox milieu*/

p.rd_txt > span:first-child { 
	display: block; 
    margin-bottom: 20px; 
}
div.ici#ici > p:nth-child(2) { 
	position: relative; 
    top: -55px; 
}
input[type=text] {
	margin-right:12px;
    border: 1px solid black;
    border-radius: 3px;
}
	/*options et aide*/

#options {
    background-color: white;
    font-family: noto;
    margin-top: -29px;
    padding: 20px;
    border-radius: 20px;
    border-top-left-radius: 0px;
    border-top-right-radius: 0px;
    border: solid transparent 0px;
    width: 400px;
    left: 0px;
    margin-left: auto;
    margin-right: auto;
    position: relative;
    top: 0px;
    float: none;
}
#help {
    background-color: white;
    font-family: noto;
    margin-top: -29px;
    padding: 20px;
    border-radius: 20px;
    border-top-left-radius: 0px;
    border-top-right-radius: 0px;
    border: solid transparent 0px;
    width: 400px;
    left: 0px;
    text-align: center;
    margin-left: auto;
    margin-right: auto;
    position: relative;
    top: 0px;
}
.voile {
    display: none !important;
}
/*centrage largeage*/

.bar {
    display: none;
}
.main {
    text-align: center;
    width: 400px;
}
#aff_kb {
    width: 355px;
    margin-left: auto;
    margin-right: auto;
}
#resultats {
    width: 385px !important;
    margin-left: auto;
    margin-right: auto;
}
#rd_txt {
    margin-left: auto;
    margin-right: auto;
}
.txt {
    width: 358px;
}
strong {
    float: none !important;
}
}
