@-moz-document domain("discordapp.com") {

body {
    font-weight: normal;
}
a, abbr, acronym, address, applet, big, blockquote, body, caption, cite, code, dd, del, dfn, div, dl, dt, em, fieldset, form, h1, h2, h3, h4, h5, h6, html, iframe, img, ins, kbd, label, legend, li, object, ol, p, pre, q, s, samp, small, span, strike, strong, sub, sup, table, tbody, td, tfoot, th, thead, tr, tt, ul, var {
    font-weight: normal;
}



* {
    font-family: Lato, Source Sans Pro, Whitney,Helvetica Neue,Helvetica,Arial,sans-serif !important;
}
.message-group .comment .markup pre , .message-group .comment .markup code.inline , .markdown-modal pre , .markdown-modal code.inline , .user-settings-modal-security .backup-codes .code , code , .user-settings-security .checkbox-group .code {
    font-family: monospace !important;
}
code {
    font-size: 11.9px !important;
}


/* #### */


.theme-dark .channel-textarea-inner textarea {
    color: #ddd;
}

.theme-dark .message-group .comment .markup {
    color: #ddd !important;
}

.guild-channels .channel a {
/*     opacity: 0.8; */
	opacity: 1;
    color: #ccc
}

.theme-dark .chat .title-wrap .topic {
    color: #ddd;
}

div.friends-online {
    color: #ccc;
}

.guild-channels .channel-text.selected a *,
.guild-channels .channel-text.unread:not(.selected):not(.channel-muted) a * {
/*     font-weight: 500 !important; */
}



* {
