css:
@-moz-document domain("livedoor.com") {
    
  
body{
    background: url('https://s20.postimg.cc/flcqv3z8t/astroboy.jpg') center top fixed !important;
    background-size:cover !important;
    background-repeat: no-repeat !important;
}  


section#container{
    background: rgba(255,255,255,0.7) !important;
}


.footer-inner{
    padding:30px !important;
    background: rgba(255,255,255,0.7) !important;    
}


body > div{
    background: url('https://s20.postimg.cc/flcqv3z8t/astroboy.jpg') center top fixed !important;
    background-size:cover !important;
    background-repeat: no-repeat !important;        
}


body > div.LyWrap > div{
    background: rgba(255,255,255,0.7) !important;          
}


.slider{
    display:none !important;    
}
    
body > div#mainContent{
    background: rgba(255,255,255,0.7) !important;                  
}    
    
    #container > div#content{
    background: transparent !important;            
    }    
    
    .contentInner > div#main{
    background: rgba(255,255,255,0.7) !important;                          
    }    
    
    #container > div#content > div.contentInner > div#sub{
    background: rgba(255,255,255,0.7) !important;           
    }    
    
#globalFooter{
    background: rgba(255,255,255,0.8) !important;                   
    }    
    
    

}
