css:
/* i really want this to be global */

.navbar-brand img {
    content: url("https://t7.rbxcdn.com/2bbefc76428f2bfde1fcd567cd1382fa");
}
.Button:hover,
.Button:active {
    background-color: #6e99c9;
    border: solid 1px #000;
    color: #fff;
}
html {
    position: relative;
    min-height: 100%;
}
.gm {
    color: red;
}
h1,
h2,
h3,
h4,
h5,
h6 {
    color: black;
}
h3 {
    font-family: "Comic Sans MS", Comic Sans;
}
a {
    font-family: "Comic Sans MS", Comic Sans;
}
.panel-primary>.panel-heading {
    background-color: #6e99c9;
}
.navbar,
.navbar-default {
    background-color: #6e99c9;
}
.panel-body {
    border: 3px solid #6e99c9;
}
.panel-heading {
    padding: 10px 15px;
    border-bottom: 0px solid transparent;
    border-top-left-radius: 0;
    border-top-right-radius: 0;
}
body {
    font-family: 'Comic Sans MS', Comic Sans MS;
    -webkit-font-smoothing: antialiased;
    background: #fff;
    -moz-background-size: cover;
    background-size: cover;
    -o-background-size: cover;
    -webkit-background-size: cover;
    background-image: url("http://i.imgur.com/6usqJz6.jpg")
}
/*-----NAVBAR-----*/

.navbar,
.navbar-default {
    background-color: #6460DA;
    color: rgba(255, 255, 255, .84);
}
.navbar,
.navbar-default-default.header-cont[style] {
    box-shadow: none !important;
    margin: auto!important;
}
.navbar .navbar-nav>li>a:hover {
    color: white;
    padding-top: 20px;
    padding-bottom: 20px;
    font-family: Comic Sans MS;
    font-weight: normal;
    font-size: 1.25em;
    text-decoration: underline;
}
.navbar .navbar-nav>li>a {
    color: white;
    padding-top: 20px;
    padding-bottom: 20px;
    font-family: Comic Sans MS;
    font-weight: normal;
    font-size: 1.25em;
}
.navbar .navbar-brand {
    height: 60px;
    line-height: 30px;
    font-family: Comic Sans MS;
    color: #FFF;
}
.navbar .navbar-brand>hover {
    color: grey;
    padding-top: 20px;
    padding-bottom: 20px;
    font-family: Comic Sans MS;
    font-weight: normal;
    font-size: 1.25em;
    text-decoration: underline;
}
* {
    font-family: Comic Sans MS;
}
.center User {
    display: none;
}
span.panel-panel-primary > .panel-heading-center {
    display: none;
}
span.glyphcon.glyphcon-comment {
    display: none;
}
a:focus {
    color: #0000FF;
}
a:hover {
    color: #DD6900;
}
