css:
@-moz-document domain("youtube.com") {
@media (max-width: 450px) {
    #merch-shelf,
    #masthead-positioner,
    #masthead-positioner-height-offset,
    #watch-header,
    #action-panel-details,
    #watch7-sidebar,
    #watch7-container,
    #footer-container,
    #masthead-container,
    #toast,
    #comments,
    #header-top-row,
    #playlist #overflow-menu,
    ytd-live-chat-frame,
    .ytp-size-button.ytp-button,
    .ytp-miniplayer-button.ytp-button,
    div.video-annotations,
    div.branding-img-container {
        display: none !important;
    }
    
    ytd-live-chat-frame + template + div + div + div + div {
        border-bottom: 46px solid transparent !important;
        margin-bottom: 10px !important;
    }
    
    ytd-watch-flexy[theater] #player-theater-container.ytd-watch-flexy {
        min-height: 240px !important;
    }
    
    .html5-video-player .video-stream {
        top: 0px !important;
    }

    #container > .header {
        padding-top: 4px !important;
        padding-bottom: 3px;
    }
    
    #primary {
        padding: 0 !important;
        margin: 0 !important;
    }

    #page-manager.ytd-app,
    #top.ytd-watch {
        margin-top: 0 !important;
    }

    #playlist {
        height: calc(100vw - 20px) !important;
        margin-top: 0 !important;
        min-height: 0 !important;
    }
    
    #primary-inner {
        display: flex;
        flex-direction: column;
    }
    
    #primary-inner > * {
        order: 3;
    }

    #info {
        order: 1;
        padding-left: 10px !important;
        padding-right: 10px !important;
        background: rgba(136, 136, 136, 0.4);
    }
    
    #info-contents #container {
        position: relative;
    }
    
    ytd-video-primary-info-renderer h1 {
        white-space: nowrap;
        overflow: hidden;
        text-overflow: ellipsis;
        max-width: calc(100vw - 19px);
        display: inline-block;
    }
    
    ytd-add-to-playlist-renderer,
    #info #info {
        zoom: 0.8;
    }
    
    #info #info {
        position: absolute;
        right: 0;
        bottom: -55px;
        background: transparent;
    }

    #playlist {
        order: 2;
    }
    
    #head {
        padding: 14px 10px;
        background: rgba(136, 136, 136, 0.4);
        z-index: 1000000000;
    }
    
    ytd-watch-next-secondary-results-renderer ytd-compact-video-renderer,
    ytd-watch-next-secondary-results-renderer ytd-compact-radio-renderer {
        padding-left: 10px
    }
    
    #autoplay,
    #improved-toggle,
    #meta-contents,
    #header-description,
    #count.ytd-video-primary-info-renderer,
    #sentiment,
    #info-contents #container > yt-formatted-string {
        display: none;
    }
    
    .header.ytd-playlist-panel-renderer {
        padding: 4px 8px 4px 8px
    }
    
    #info-contents > ytd-video-primary-info-renderer {
        padding-bottom: 4px;
        padding-top: 8px;
    }

    h1.title {
        font-size: 1.6em !important;
    }

    ytd-video-primary-info-renderer.ytd-watch {
        min-height: 34px !important;
    }

    #watch-appbar-playlist {
        height: calc(100vh - 241px) !important;
    }

    #player {
        top: 0px !important;
    }

    #body-container {
        padding-bottom: 0 !important;
    }

    body {
        overflow: hidden !important;
    }
}
}
