css:
@-moz-document domain("mlcboard.com"), domain("mlcboard.to") {
/* vBulletin 3 CSS For Style 'Default Style' (styleid: 1) */body
{
    background: #1b1b1b !important;
    color: #A4A4A4 !important;
    font: 9pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
    margin: 0px auto !important;
    
}
/* *** Correct Fixed Width *** */
#content-all { width: 95% !important;  margin-right: auto; margin-left: auto; }
/* *** Block Adds *** */
#slickboxbanner, #nav1, #nav2, #nav-atomix,
div[style="display: block; height: 100px;"],
div[style="margin: 10px auto 0px; width: 728px;"],
table[width="990"]
{ Display:none !important; }
/* *** Left Color *** */
tr[valign="top"] > td.alt2, tr[valign="top"] + tr > td.alt2
{
    width: 200px !important;
    background: #2c2c2c !important;
    color: #A4A4A4 !important;
    font-family: 9pt tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
    border-left: 1px solid #FFFFFF !important;
    border-top:1px solid #2c2c2c !important;
}
/* *** Quote *** */
td.alt1 > div > div > table > tbody > tr > td.alt2
{
    background: #333333 !important;
    color: #A4A4A4 !important;
    font-family: 9pt tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
    border-top: 1px solid #333333 !important;
    border-left: 1px solid #333333 !important;
    border-right: 1px solid #777777 !important;
    border-bottom: 1px solid #777777 !important;
}


/* ***** Image fiting***** */


td.alt1 > div[id^="post_message_"] img 
{
max-width: 800px;
cursor: pointer;
}


td.alt1 > div[id^="post_message_"] img:active
{
max-width: 2500px;
}






/* ***** Block color post **** */
font > font > b,
font > b > font,
b > font > font,
td.alt1 > div > b,
td.alt1 > div > font > b,
td.alt1 > div > b > font,
td.alt1 > hr > div > b,
td.alt1 > hr > div > font > b,
td.alt1 > hr > div > b > font,
td.alt1 > div > div > table > tbody > tr > td.alt2 > b > font,
td.alt1 > div > div > table > tbody > tr > td.alt2 > font > b,
td.alt1 > div > div > table > tbody > tr > td.alt2 > div > b,
td.alt1 > div > div > table > tbody > tr > td.alt2 > div > font > b,
td.alt1 > div > div > table > tbody > tr > td.alt2 > div > b > font,
td.alt1 > hr > div > div > table > tbody > tr > td.alt2 > b > font,
td.alt1 > hr > div > div > table > tbody > tr > td.alt2 > font > b,
td.alt1 > hr > div > div > table > tbody > tr > td.alt2 > div > b,
td.alt1 > hr > div > div > table > tbody > tr > td.alt2 > div > font > b,
td.alt1 > hr > div > div > table > tbody > tr > td.alt2 > div > b > font
{
    color: #CBCBCB !important;
    text-decoration: none !important;
}


td.alt1 > div > font,
td.alt1 > hr > div > font,
td.alt1 > div > div > table > tbody > tr > td.alt2 > font,
td.alt1 > hr > div > div > table > tbody > tr > td.alt2 > font,
td.alt1 > div > div > table > tbody > tr > td.alt2 > div > font,
td.alt1 > hr > div > div > table > tbody > tr > td.alt2 > div > font
{    
    color: #ABABAB !important;
    text-decoration: none !important;
}


font[color*="red"] {color: #ff2c2c !important;}
font[color*="blue"] {color: #2c2cff !important;}
font[color*="#000080"] {color: #000080 !important;}
font[color*="#ff66ff"] {color: #ff88ff !important;}






a:link
{
    color: #fa9422 !important;
    text-decoration: none !important;
}


a:visited
{
    color: #ea7412 !important;
    text-decoration: none !important;
}
a:hover, a:active
{
    color: #ff7502 !important;
    text-decoration: underline !important;
}
.page
{
    background: #1b1b1b !important;
    color: #A4A4A4 !important;
    width: 95% !important;
}
.page a:link
{
    text-decoration: none !important;
}
.page a:visited
{
    text-decoration: none !important;
}
.page a:hover, .page a:active
{
    text-decoration: underline !important;
}
td, th, p, li
{
    font: 9pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}
.tborder
{
    background: #1f1f1f !important;
    color: #A4A4A4 !important;
    border: 1px solid #363636 !important;
}
