css:
@-moz-document url-prefix("http://vgpro.gg/players"), url-prefix("https://vgpro.gg/players") {
/*PLAYER PROFILE*/
.alert{
    display: none;
}

:not( .point):not(.search):not(.match_result) {
    border-radius: 0 !important;
}

.content .sidebar .allroles .eachrole .role_img#jungler {
    background-color: #6ab70b !important;
} 

.viewmore {
    display: none;
}

.content .match_history .match_history_box .hero_img {
    border-radius: 100% !important;
    box-shadow: 0 0px 6px 2px rgba(0,0,0,0.45);
}

.content .match_history .match_history_box .match_history_title#v{
    background-color: #5285c9;
    color: #afc7e6;
}

.content .match_history .match_history_box .match_history_title#v::after {
    color: #fff;
}

.content .match_history .match_history_box .match_history_title#d{
    background-color: #c95257;
    color: #e6afb1;
}
.content .match_history .match_history_box .match_history_title#d::after{
    color: #fff;
}

/*More Info*/
.team_stats {
    width: 100% !important;
    margin: 0 !important;
    border: none !important;
    margin-bottom: -5px !important;
    margin-top: 5px !important;
    text-align: center;
    background-color: #e0e0e0 !important;
    color: #1e1e1e;
}

.tier .tier_img {
    border: 1px solid #cdcdcd !important;
}

.heroes_build .title{
    margin-top: -5px !important;
    border: none;
    border-top: 1px solid #aaa;
    border-bottom: 1px solid #aaa;
}

.heroes_build .skill_wrap{
	padding-bottom: 10px !important;
}

.content_builds .heroes_build .skill_wrap .skill_order .level {
    border: 1px solid #f2f2f2 !important;
    border-radius: 100% !important;
    width: 30px !important;
    height: 32px !important;
    padding: 0 !important;
    padding-top: 7px !important;
    text-align: center !important;
}
.content_builds .heroes_build .skill_wrap .skill_order .level:after{
    width: 1px;
}
.heroes_build .skill_wrap .skill_order .levelup {
    background-color: hsl(47, 83%, 65%) !important;
    color: #000 !important;
    border-color: hsl(47, 83%, 65%);
}

.content .match_history .match_history_more .sidestats div.damage .bar{
    border: 1px solid #ccc;
    background-color: #ddd;
}

.content .match_history .match_history_more .sidestats div.damage .bar .fill{
    border: 1px solid hsl(3, 54%, 51%);
    background-color: hsl(3, 82%, 63%);
    position: relative;
    top: -1px;
}

.content .match_history .match_history_more .sidestats div.each .tier .tier_img{
    background-size: 145%;
    background-position: center -3px;
}

div#d.match-result {
    background-color: rgba(0,0,0,0) !important;
}
}

@-moz-document url("https://vgpro.gg"), url("http://vgpro.gg"), url-prefix("http://vgpro.gg/home"), url-prefix("https://vgpro.gg/home") {
/*HOMEPAGE*/
.alert {
    display:none;
}

.pro-feed, .leaderbox {
	border: none;
    -webkit-box-shadow: 0px 0px 29px -3px rgba(0,0,0,0.3);
	-moz-box-shadow: 0px 0px 29px -3px rgba(0,0,0,0.3);
	box-shadow: 0px 0px 29px -3px rgba(0,0,0,0.3);
}

.pro-feed .header, .subtitle{display:none;}

.pro-feed{margin-left: -15px;}

.leaderbox{margin-left: 15px;}

.pro-feed .match {
	border-top: none;
}

.pro-feed_history a:nth-child(2n-1){
    background-color: rgb(238,238,238) !important;
}

.pro-feed_history a:nth-child(2n){
    background-color: rgb(248,248,248) !important;
}

.pro-feed .match .reg span,
.leaderbox .lead_content .eachbox .number span,
.leaderbox .lead_content .eachbox .reg span,
.leaderbox .lead_content .eachbox .player_name span {
    border: none;
    color: #fff;
    background-color: rgb(190,190,190);
    padding: 2px 5px;
    border-radius: 4px;
    font-family: "Roboto",sans-serif;
    font-size: 12px;
}

.leaderbox .lead_content .eachbox .player_name span { font-size: 10px; padding: 2px 3px; margin-right: 3px; }

.pro-feed .match .pro_img{
	background-color: hsl(161,100%,21%);
}

.pro-feed .match .victory#Win{
    position: relative;
    left: calc(-50% + 6px + 2px);
    background-color: hsl(205, 86%, 65%);
    font-size: 0;
    width:12px;
}
.pro-feed .match .victory#Loss{
    position: relative;
    left: calc(-50% + 6px + 2px);
    background-color: hsl(6, 86%, 65%);
    font-size: 0;
    width:12px;
}

#all > a:nth-child(odd) > div, /*Leaderboard*/
#winrate > div.box_content_winrate > div:nth-child(odd), /*Hero winrate*/
#pickrate > div.box_content_pickrate > div:nth-child(even) { /*Hero pickrate*/
    background-color: rgb(238,238,238) !important;
    margin:0;
}

#all > a:nth-child(even) > div, /*Leaderboard*/
#winrate > div.box_content_winrate > div:nth-child(even), /*Hero winrate*/
#pickrate > div.box_content_pickrate > div:nth-child(even) { /*Hero pickrate*/
    margin:0;
}
}
