css:
@-moz-document url-prefix("https://lodash.com/docs") {
  /* Code pre */
  .highlight {
      background-color: #2a2f33;
      border: 1px solid rgba(0, 0, 0, 0.12);
      margin: 0;
      padding: 1.6rem;
  }

  /* Color Scheme: Font and Backgrounds */
  html.docs {
      color: #eee;
      background-color: #363c42;
      font-size: 50%;
  }

  html.docs header {
      background-color: #363c42;
      box-shadow: 0 0 2.25rem rgba(0, 0, 0, 0.5);
  }

  html.docs header .logo a {
      background: url(data:image/svg+xml;base64,);
  }

  html.docs .doc-main {
      background-color: #363c42;
  }

  h1, h2, h3, h4 {
      color: #8c9fb3;
  }

  html.docs .doc-container {
      padding: inherit;
  }

  html.docs .doc-container>div>h2 {
      padding: 4.8rem;
      margin: 0;
  }

  html.docs .btn-repl {
      bottom: 6.0rem;
      right: 6.0rem;
  }

  html.docs .doc-container>div>div {
      padding: 0 4.8rem 4.8rem;
      margin: 0;
      box-shadow: inset 0px 12px 4px -10px rgba(0, 0, 0, .75);
  }
  html.docs .doc-container>div>div:hover:after {
      content: "";
      position: absolute;
      top: 0;
      left: 0;
      width: 7px;
      height: 100%;
      background-color: rgba(75, 113, 202, 0.84);
      box-shadow: inset 2px 2px 4px -2px rgba(0, 0, 0, .75);
  }


  /* Function Names (_.countBy, etc... )  */

  html.docs .doc-container h3 {
      background-color: rgb(74, 79, 85);
      box-shadow: inset 0px 11px 5px -10px rgba(0, 0, 0, .75);
      color: #eee;
      padding: 2.4rem;
      margin: 0 -4.8rem;
      border-bottom: 1px solid rgba(255,255,255,0.04);
  }

  html.docs .doc-main .toc-container {
      background-color: #363c42;
      box-shadow: 0 0 0.5rem rgba(0, 0, 0, 0.5);
      padding: 0 2.4rem 2.4rem;
  }

  html.docs .doc-main .toc-container a {
      color: #eee;
  }

  html.docs .doc-main .toc-container a:hover, html.docs .doc-main .toc-container a:active, html.docs .doc-main .toc-container a:focus {
      color: #3492ff;
  }
  /* Left Scoll Bar */

  html.docs .toc-container::-webkit-scrollbar {
      -webkit-appearance: none !important;
      width: 3px !important;
      background-color: rgba(0, 0, 0, 0.05) !important;
      visibility: hidden !important;
  }

  html.docs .toc-container::-webkit-scrollbar-thumb {
      background-color: rgb(74, 79, 85) !important;
      border: none !important;
      border-radius: 0 !important;
  }
  /* Move the Search Bar to the Floating Bar */

  html.docs .toc-container .search {
      background: #363c42;
      z-index: 1000;
      position: sticky !important;
      box-shadow: -1px 0px 8px 0px rgba(0, 0, 0, 0.38);
      left: 0;
      top: 0;
      width: calc(100% + 4.8rem);
      margin: 0 -2.4rem;
      padding: 0;
      color: rgba(255, 255, 255, 0.5);
  }

  html.docs .toc-container .search:hover {
      color: rgba(255, 255, 255, 0.75)
  }

  html.docs .toc-container .search input:active, html.docs .toc-container .search input:focus {
      color: rgb(255, 255, 255)
  }

  html.docs .toc-container .search .fa-search {
      color: inherit;
      font-size: 1.4em;
      display: inline-block;
      left: 0;
      bottom: 0;
      top: 0;
      padding: 1.4rem;
      padding-left: 1.4rem;
  }

  html.docs .toc-container .search input {
      background-color: rgba(255, 255, 255, 0.95);
      background: none;
      color: inherit;
      border-bottom: none;
      display: inline-block;
      top: 0;
      left: 0;
      bottom: 0;
      padding: 1rem;
      font-size: 1.4em;
      text-indent: 0;
      padding-left: 4.5rem;
  }
}
