css:
@-moz-document domain("kotaku.com"), 
domain("io9.com"), 
domain("deadspin.com"), 
domain("gawker.com"), 
domain("gizmodo.com"), 
domain("jalopnik.com"), 
domain("jezebel.com"), 
domain("lifehacker.com"), 
domain("kinja.com"), 
domain("gawker-labs.com"),
domain("theroot.com"),
domain("fusion.net"),
domain("theonion.com"),
domain("avclub.com"),
domain("earther.com"),
domain("splinternews.com"),
domain("thetakeout.com"),
domain("clickhole.com"),
domain("theinventory.com")
{
/*Dark Background*/
:not(hr):not,
div::after,
div {
    background-color: transparent !important;
}
html,
body,
img,
.js_discussion__button,
.dui-menu,
.featured-post__meta.reverse-meta-colors,
.review-box__content,
.item,
.section-nav,
div[class^="header__GlobalNav"],
.iYoMhr,
.discussion-empty-state,
.js_share_tools_meta,
.js_share-tools,
.share-toolbar,
.editor,
.editor * {
    background-color: #222 !important;
}
:not(.js_hovernav__trigger):not([data-type="BlockQuote"]) {
    border-color: #666 !important;
}
.inset--story,
.inset--story *,
.js_header,
.js_header *,
.js_topbar,
.js_topbar *,
.navwrap-header *,
.navwrap-headerv2,
.navwrap-headerv2 *,
div[class^="header__GlobalNav"],
button,
.js_discussion-sorting-wrapper,
.dui-modal-wrapper,
.f-dropdown,
.button-container a,
.js_lightbox-container,
.modal__Container-zpplwr-3,
div[class^="select__SelectControl-sc"],
div[class^="select__SelectOptionsList-sc"],
input,
.sc-17x7c2u-0 {
    background-color: #111 !important;
}
.hfcOOD {
    display: none !important;
}
.post-cutoff__gradient {
    background-image: none !important;
}

/* Light text */
:not(.textbox-shadow) {
    color: #e5e5e5 !important;
}
.page svg,
.main svg {
    stroke: #e5e5e5 !important;
}
#search-bar,
.spinner.bounce > span {
    background-color: #e6e6e6 !important;
}


/* Per site colors */
.kotaku a,
.kotaku h1,
.kotaku em,
.kotaku i,
.kotaku strong {
    color: #FBC000 !important;
}

.deadspin a,
.deadspin h1,
.deadspin em,
.deadspin i,
.deadspin strong {
    color: #00b3b3 !important;
}

.jezebel a,
.jezebel h1,
.jezebel em,
.jezebel i,
.jezebel strong {
    color: #EC1238 !important;
}
.jalopnik a,
.jalopnik h1,
.jalopnik em,
.jalopnik i,
.jalopnik strong {
    color: #FF4D00 !important;
}
.lifehacker a,
.lifehacker h1,
.lifehacker em,
.lifehacker i,
.lifehacker strong {
    color: #94B330 !important;
}

.anitay a,
.anitay h1,
.anitay em,
.anitay i,
.anitay strong {
    color: #b30000 !important;
}

.theroot a,
.theroot h1,
.theroot em,
.theroot i,
.theroot strong {
    color: #509B22 !important;
}

.fusion a,
.fusion h1,
.fusion em,
.fusion i,
.fusion strong {
    color: #5c5cd6 !important;
}

.gizmodo a,
.gizmodo h1,
.gizmodo em,
.gizmodo i,
.gizmodo strong {
    color: #72CAFF !important;
}

.io9 a,
.io9 h1,
.io9 em,
.io9 i,
.io9 strong {
    color: #B384AB !important;
}

.theonion a,
.theonion h1,
.theonion em,
.theonion i,
.theonion strong {
    color: #00a85b!important;
}

.avclub a,
.avclub h1,
.avclub em,
.avclub i,
.avclub strong {
    color: #5575ba !important;
}

.earther a,
.earther h1,
.earther em,
.earther i,
.earther strong {
    color: #0086cc !important;
}

.splinter a,
.splinter h1,
.splinter em,
.splinter i,
.splinter strong {
    color: #F85637 !important;
}

.thetakeout a,
.thetakeout h1,
.thetakeout em,
.thetakeout i,
.thetakeout strong {
    color: #F9DB5D !important;
}
.clickhole a,
.clickhole h1,
.clickhole em,
.clickhole i,
.clickhole strong,
.clickholenews a,
.clickholenews h1,
.clickholenews em,
.clickholenews i,
.clickholenews strong {
    color: #FE6600 !important;
}
.theinventory a,
.theinventory h1,
.theinventory em,
.theinventory i,
.theinventory strong {
    color: #bf0b7e !important;
}

/* Logo Fixes */
.gmg-logo--fusion {
    stroke: #5c5cd6 !important;
    stroke-width: 2 !important;
}
.gmg-logo--gizmodo,
.gmg-logo--kinja {
    stroke: #999999 !important;
    stroke-width: 2 !important;
}
.gmg-logo--theonion {
    stroke: #00a85b!important;
    stroke-width: 2 !important;
}
.gmg-logo--avclub {
    stroke: #5575ba !important;
    stroke-width: 2 !important;
}
.gmg-logo--splinter {
    stroke: #F85637 !important;
    stroke-width: 2 !important;
}
}
