css:
@-moz-document domain("memrise.com"), domain("decks.memrise.com") {
/* 
Memrise D4 by Hex - ヘクス
*/
:root
{
    --review-color: #5d5dff;
    --difficult-color: #ff7800;
    --theme-1: black;
    --theme-2: #111;
    --theme-3: #222;
    --theme-3: #333;
    --theme-4: #444;
    --theme-5: #555;
    --theme-6: rgba(255, 192, 0, 1)
}

*
{
    text-shadow: none !important;
}
.streak .streak-bubbles > .complete
{
    background-color: rgba(255, 192, 0, .43) !important;
}
body,
div#content
{
    background: var(--theme-2) !important;
    padding: 0;
}
goal-setter-progress
{
    background: #555555 !important;
}
.garden-box.end_of_session .course_box .bottom
{
    background: var(--theme-2);
    box-shadow: inset 0px 0px 13px 4px #ff0000;
}
.btn-pink
{
    background-color: inherit;
    color: inherit;
}
a.review_btn.btn.btn-primary.btn-large
{
    color: #2d2d2d
}
p#help-button
{
    color: #a5a5a5;
}
a.show-more-link.show-less
{
    color: var(--theme-1);
    padding: 0 0 0 0;
}
div.progress,
.progress-bar
{
    border-radius: 0px !important;
    border: 0px
}
div.course-progress-box,
.wrapper
{
    background-color: var(--theme-2) !important;
}
.course-card-container.js-course-card-container,
.card-top,
.card-main-container,
.course-progress,
select,
button.btn,
.progress-bar-container,
div.detail,
.profile-badge.empty,
footer.footer,
div.streak-row,
div.num,
div.text,
div.detail,
div.top-main,
div.card-top,
div.modal-content,
div.next_btn.btn.btn-primary.btn-large,
.course-card-container .goal-setter .streak-row .streak-fake-btn,
.course-card-container .course-progress-box .card-top .card-main-container .simple-btn,
.garden-box.multiple_choice ol.choices.grid li,
.garden-box.video-pre-presentation ol.choices.grid li,
.course-card-container .course-progress-box .card-top .card-main-container .goal-setter-btn.active,
.garden-box .typing-wrapper .typing-type-here,
.mem.garden img.mem-pic,
.mem.garden .mem-text,
card-top
{
    background-color: #222222 !important;
    color: #777777;
}
.garden-box.video-pre-presentation ol.choices li .index,
span.simple-btn,
span.simple-btn.edit-goal-btn,
.btn-transparent,
p#mute-audio,
.thing-show .columns .show-more-link,
.thing-show .columns .show-less
{
    background: #111111;
    color: #777;
}
course-card-container .goal-setter
{
    background: #111111;
    margin: 0 0 0;
}
.course-card-container .course-progress-box .card-top .card-main-container .course-progress .progress-bar-container,
.course-card-container .goal-setter,
.course-card-container .course-progress-box .card-top .card-main-container
{
    background: #222222;
    color: #555555;
    margin: 0 0 0;
    border-radius: 0;
    border-right: 0;
}
.course-card-container .goal-setter .streak-edit
{
    border-color: black;
}
.dashboard section .wrapper
{
    background-color: #222222 !important;
    background: #222222;
    color: gray;
    border-bottom: 20px;
}
.course-card-container .course-progress-box .card-bottom .course-actions
{
    background: #333333;
    text-align: center;
}
.sidebar-box,
.sidebar-profile .profile-content .content-stats,
.countdown-body .countdown-duration .time-block,
.tabbed-sidebar .leaderboard,
.leaderboard .leaderboard-content .leaderboard-rows,
.sidebar-premium .premium-content .text,
.header-v2,
div.footer-row,
.leaderboard .leaderboard-content .leaderboard-link a
{
    background-color: #000000;
    color: #777777;
    border: 0;
}
.leaderboard-row
{
    border-bottom: 0 !important;
    color: gray !important;
}
.course-card-container .course-progress-box .card-bottom
{
    padding: 0;
}
.header-profile-inner
{
    background: #222222;
}
.tabbed-main h1,
.dashboard-landscape,
.landscape,
#forest-banner,
.sun
{
    font-size: 0;
    height: 0 !important;
    color: gray !important;
    background-image: url(http:) !important;
}
#page-header
{
    height: 0 !important;
    background-image: url(http:) !important;
    color: gray !important;
    margin-top: 40px;
    margin-bottom: 80px;
}
.full-width.pattern-dark
{
    background-image: url(http:);
    background-color: #000000;
    padding: 0 20px;
}
.full-width.divide,
.thing-show .row
{
    box-shadow: 0 0 0 0;
    border-top: 0;
    padding: 0;
}
div.primary-value
{
    font-weight: normal;
}
.garden-box .shiny-box
{
    border: 2px solid;
    border-radius: 0;
}
.leaderboard .leaderboard-header,
.leaderboard-container.wide .leaderboard .leaderboard-rows
{
    background-color: #000000;
    border-radius: 0;
}
.garden-timer
{
    display: none;
    transition: 50ms;
}
.button,
.title a,
.simple-btn,
button,
.onoff,
.shiny-box,
next-icon,
.btn-primary,
btn
{
    color: #777 !important;
    background: inherit !important;
    border-radius: 0;
    border-color: inherit;
}
.progress
{
    height: 10px !important;
    background: inherit !important;
}
div.streak-edit
{
    border-right: 3px !important;
    border-color: gray !important;
}
.goal-setter-btn.done.active
{
    background: linear gradient(top, #f59e23 0%, #f57c23 100%) !important;
    border-radius: 0
}
.goal-setter-progress
{
    background: #333 !important;
    border-radius: 0 !important
}
.course-card-container .course-progress-box .card-top .card-main-container .goal-setter-btn.active
{
    background: #333 !important;
    border-radius: 0;
    box-shadow: none
}
.loading-item
{
    background: transparent !important;
    color: transparent !important
}
div.primary-value
{
    color: gray !important
}
div.wrapper
{
    background: inherit !important
}
body > div.course-tabs-wrap
{
    border-bottom: 1px solid gray;
    background: #222
}
#content > div > div > div.span9 > div.progress-box.progress-box-course
{
    background: inherit;
    margin-top: 20px
}
.bar
{
    background-image: -webkit-linear-gradient(left, black 10%, darkred, red, #f50, #f70, yellow) !important;
    border-radius: 0 !important;
    border: 0px !important;
    border-image: -webkit-linear-gradient(left, black 0%, white 100%) !important;
    background: inherit;
    margin: 0 !important;
}
div.progress
{
    background: gray !important;
}
body.levels .levels .level
{
    height: inherit;
    background-color: inherit;
    color: gray;
}
body.levels .levels .level .level-icon
{
    background: inherit;
    margin: 20px 0 -10px 0;
}
.leaderboard
{
    background-color: black !important
}
.course-card-container .course-progress-box .card-top .card-main-container .goal-setter-btn.done .onoff
{
    color: #f97424 !important
}
.reverse-header-ruled #page-head
{
    background: #111;
    border-bottom: 0px
}
* .table,
div.table
{
    background: inherit !important;
    color: gray !important
}
.btn.btn
{
    background: iherit !important;
    background-image: url(http:) !important;
    border-radius: 0;
    background-color: inherit
}
.leaderboard-row.current
{
    background: #222 !important;
    color: white !important
}
.hovercard
{
    background: inherit !important
}
.session-header .header-block.header-middle .course-details-wrapper .course-details .course-title
{
    color: inherit
}
.garden.speed-review-session #central-area.speed-central
{
    background: #222;
}
.question-row .extra-info,
.question-row .qquestion
{
    background: inherit;
    color: gray !important
}
.garden.speed-review-session #gardening-area
{
    margin: 20px
}
.garden.speed-review-session .speed-background-wrapper .speed-background
{
    background: #111;
    border: 2px solid #222;
}
#page-header.speed
{
    margin-bottom: 60px;
}
.garden-box.end_of_session .course_box .top .levels
{
    background: inherit !important
}
.header
{
    color: inherit !important
}
.analytics section h1.inline,
.analytics section h2.inline
{
    color: inherit
}
.whitebox
{
    padding: 0px 0px 0px 0px;
    background-color: #111111;
    color: gray !important;
}
#lazy-learning-insight > div > div.whitebox,
#chart-coursetime > div > div:nth-child(1),
#attendance-grid
{
    color: gray !important;
    background: #333 !important
}
.difficult-items .whitebox .wrapper .table
{
    background: inherit !important
}
.analytics section .wrapper .attendance-grid .day
{
    stroke: black
}
.q0
{
    fill: #111!important
}
.q1
{
    fill: #444 !important
}
.q2
{
    fill: #666 !important
}
.q3
{
    fill: #bbb !important
}
.profile-badge.premium
{
    color: black;
    background-image: -webkit-radial-gradient(black, purple 80%)
}
#page-head
{
    background: #222;
    margin-bottom: 20px;
    border-bottom: 0
}
#content > div > div.tabbed-main.page > section > div > div.courses-filter-container
{
    margin-top: 0
}
.full-width.alternate
{
    background: inherit !important;
    border-top: 0
}
.footer
{
    background: inherit !important;
}
.sidebar-profile .profile-header
{
    background-color: inherit;
}
img.rank-icon
{
    width: 0 !important;
}
#page-head .search-box input[type="text"],
.search-box button,
.infinite-scroller .infinite-scroller-trigger,
.categories-list
{
    background-color: #000;
    color: white;
    border-color: gray;
}
.courses-heading.courses-heading-intro,
.sidebar h3.category-header,
.course-box h3 .inner,
#page-head .inner .page-breadcrumb
{
    color: white;
}
div#footer.footer.full-width
{
    border: none;
    background-color: #000 !important;
    margin-top: 20px;
}
.course-box
{
    border: none;
    background: #000
}
.course-box .inner-wrap,
.course-box .details-wrapper .details
{
	border: none;   
    background-image: none;
    color: white
}
.course-box .details-wrapper .target-photo
{
    background-image: none;
    background-color: transparent;
 	color: white   
}
.course-box .details-wrapper .details .stat
{
    margin: 0;
    color: whitesmoke;
    border: none;
}
a.single-continue-button.single-continue-button
{
    
    border-radius: 0;
    border: solid 2px black;
}

.course-card-container .goal-setter .streak-row .streak .streak-bubbles .streak-bubble span,
#course-2022732 > div > div.card-bottom > div > div.card-counter-container > div:nth-child(1) > div:nth-child(1) > svg > g
{
    color: white !important;
}

.course-card-container .course-progress-box .card-top .card-main-container .goal-setter-btn.done.active .onoff,
.pull-right > *,
simple-btn goal-setter-btn inactive
{
    color: inherit !important;
}
.course-card-container .course-progress-box .card-top .card-image-col .img-crop
{
    Border-radius: 100%;
}
.course-card-container .card-counter .review, .course-card-container .card-counter .difficult
{
    color: #ffc000;
}
/*Review Icon*/
.course-card-container .card-counter div svg g path
{
    fill: var(--review-color) !important;
}
/*Difficult Icon*/
.course-card-container .card-counter .difficult-icon svg path
{
    fill: var(--difficult-color) !important;
}
.countdown-body .countdown-duration .time-block .time-num,
.sidebar-main-btn .simple,
.sidebar-premium .premium-header .premium-text, .sidebar-premium .premium-content .emphasized
{
    color: white;
}
.countdown-body .countdown-duration .time-block .time-num, .sidebar-main-btn .simple, .sidebar-premium .premium-header .premium-text, .sidebar-premium .premium-content .emphasized
{
    background-color: var(--theme-3);
    
}
.stats-breakdown>div
{
    background-color: transparent;
}
.wrapper,
span.leaderboard-icon
{
    color: white !important
}
.course-tabs-wrap
{
    background-color: var(--theme-3);
    border-bottom: none;
    margin-bottom: 15px;
}
.progress-box,
.things.clearfix
{
    background-color: var(--theme-3);
    color: white !important;
}
.progress-box *
{
    color: white !important;
}
ul.nav-pills.chromeless, .nav-pills.chromeless, .nav.nav-pills.chromeless
{
    background-color: white
}
.thing
{
    border-color: black;
}
.thing .col_a .text, .thing .col_b .text, .thing, col_a col text
{
     background-color: var(--theme-3) !important
}
.btn.btn
{
    background-color: #009d00;
}
.full-width.divide, .thing-show .row
{
    padding: 10px 0 10px;
    border-top: 2px #ffffff29 groove;
}
}
