css:
@-moz-document domain("telegra.ph") {
body {
	zoom: 1.1;
}

/* GRID:
    Blue Backgrounds:
        - #15161a hsb(222, 18, 10)
        - #1f2226 hsb(218, 18, 15)
        - #2a2e33 hsb(214, 18, 20)
        - #343a40 hsb(210, 18, 25)
        - #3f474d hsb(206, 18, 30)
        - #495359 hsb(202, 18, 35)
		- #546066 hsb(198, 18, 40)
		- #5e6e73 hsb(194, 18, 45)
		- #92aab3 hsb(196, 18, 70)

    Accent:
        - #e5ae5c hsb(36, 60, 90)
        - #f2bd61 hsb(38, 60, 95);

    Foreground:
        - #e9f2f2 hsb(180, 4, 95)
        - #dce5e5 hsb(184, 4, 90)
        - #d0d7d9 hsb(188, 4, 85)
        - #c4cacc hsb(192, 4, 80)
        - #b8bdbf hsb(196, 4, 75)
*/

:root {
    --color_bg1: #15161a;
    --color_bg2: #1f2226;
    --color_bg3: #2a2e33;
    --color_bg3_95p: rgba(42, 46, 51, 0.95);
    --color_bg4: #343a40;
    --color_bg5: #3f474d;
    --color_bg6: #495359;
    --color_bg7: #546066;
    --color_bg8: #5e6e73;
    --color_bg9: #92aab3;
                                            
    --color_accent1: #e5ae5c;
    --color_accent1_op50p: rgba(229, 174, 92, 0.75);
    --color_accent2: #f2bd61;
    
    --color_fg1: #e9f2f2;
    --color_fg2: #dce5e5;
    --color_fg3: #d0d7d9;
    --color_fg4: #c4cacc;
    --color_fg5: #b8bdbf;
}

/* Background */

body {
    background-color: var(--color_bg1);
}

.tl_page_wrap,
.button {
	background-color: var(--color_bg2);
}	

.tl_article ::selection {
    background-color: var(--color_bg8);
    text-shadow: none;
}

/* BORDER */

.tl_page_wrap {
	border: 1px solid var(--color_bg3);
}

.button {
	border-color: var(--color_bg3);
}

.tl_article .tl_article_content blockquote {
	border-left: 1px solid var(--color_fg4);
}

.tl_article a[href]  {
	border-bottom: 0.1em solid var(--color_accent1_op50p);
}

/* BLOCK SHADOW */

.tl_page_wrap {
	box-shadow: 0 0 12px 0 rgba(21, 22, 26, 0.35);
}

/* FOREGROUND */

.tl_article .tl_article_header,
.tl_article .tl_article_content h3, .tl_article .tl_article_content h4,
.button,
.tl_article .tl_article_content aside,
.tl_article .tl_article_content blockquote {
	color: var(--color_fg4);
}

.tl_article address, .tl_article address a,
.tl_article .tl_article_content figcaption, .tl_article .tl_article_content figcaption .editable_input {
	color: var(--color_fg5);
}

.tl_article .tl_article_content p,
.tl_article .tl_article_content ol li, .tl_article .tl_article_content ul li,
.tl_article.tl_article_edit [data-placeholder].empty,
.tl_article.tl_article_edit [data-placeholder].empty:before,
.tl_article .tl_article_header, .tl_article.tl_article_edit .tl_article_content address, .tl_article.tl_article_edit .tl_article_content h1,
.tl_article.tl_article_edit [data-label]:after, .tl_article.tl_article_edit.title_focused [data-label].empty:after {
    color: var(--color_fg5);
}

/* ACCENT */ 

.tl_article a[href] {
	color: var(--color_accent1);
}
}

@-moz-document domain("telegra.ph") {
/* FONT */
@font-face {
	font-family: 'Lato';
	font-style: normal;
	font-weight: 300;
	src: 	local('Lato Light'), 
			url(https://apushkarev.github.io/font/Lato-Light.woff2) format('woff2');
}

@font-face {
	font-family: 'Lato';
	font-style: italic;
	font-weight: 300;
	src: 	local('Lato Light Italic'), 
			url(https://apushkarev.github.io/font/Lato-LightItalic.woff2) format('woff2');
}

@font-face {
	font-family: 'Lato';
	font-style: normal;
	font-weight: 400;
	src: 	local('Lato Regular'), 
			url(https://apushkarev.github.io/font/Lato-Regular.woff2) format('woff2');
}

@font-face {
	font-family: 'Lato';
	font-style: italic;
	font-weight: 400;
	src: 	local('Lato Italic'), 
			url(https://apushkarev.github.io/font/Lato-Italic.woff2) format('woff2');
}

@font-face {
	font-family: 'Lato';
	font-style: normal;
	font-weight: 700;
	src: 	local('Lato Bold'), 
			url(https://apushkarev.github.io/font/Lato-Bold.woff2) format('woff2');
}

@font-face {
	font-family: 'Lato';
	font-style: italic;
	font-weight: 700;
	src: 	local('Lato Bold Italic'), 
			url(https://apushkarev.github.io/font/Lato-BoldItalic.woff2) format('woff2');
}

* {
	font-family: Lato !important;
}

* {
	 text-shadow: 0.7px 0.7px 0 rgba(0, 0, 0, 0.3) !important;
}

.tl_article address, .tl_article address a, .tl_article .tl_article_content figcaption, .tl_article .tl_article_content figcaption .editable_input,
.tl_article .tl_article_content p,
.tl_article .tl_article_content aside, 
.tl_article .tl_article_content blockquote {
	font-weight: 300;
}

.tl_article .tl_article_content ol li, .tl_article .tl_article_content ul li,
.tl_article .tl_article_content h4,
.tl_article .tl_article_content aside strong {
	font-weight: 400;
}
}

@-moz-document domain("telegra.ph") {
/* LAYOUT */

.tl_article_content em {
	display: inline-block;
    padding-left: 1em;
    border-left: 2px solid var(--color_fg5);
}
}
