css:
@keyframes z {
 0%{
    transform: scale(1, 1);     
    animation-timing-function: ease-in;
    border: 2px solid rgba(100,100,100,1);
          }
 50%{
    transform: scale(1.05,1.05);
    border: 40px solid rgba(50,50,50,.5);
    animation-timing-function: ease-out;
          }
  100%{
    transform: scale(1,1);
    border: 2px solid rgba(100,100,100,1);
          }
    
}

@-moz-document domain("khanacademy.org") {

.task-container .task-header .completion-progress-container .grid.incorrect {
animation: z 3s 5s infinite alternate;
    color: #fff;

    background-color: #e00;

    border-color: #e00;

}

.task-container .task-header .completion-progress-container .grid.correct {
animation: z 3s 5s infinite alternate;
    color: #fff;

    background-color: #0e0;

    border-color: #0e0;
animation: z 1s 1s infinite alternate;

}

input.simple-button.green {
animation: z 3s 5s infinite alternate;
}

input.simple-button.blue {
animation: z 3s 5s infinite alternate;
}

input.simple-button.orange.full-width {
animation: z 3s 5s infinite alternate
}



h4.info-box-header {
    text-transform: capitalize;
 animation: z 10s 10s infinite alternate
}

div.perseus-renderer.perseus-renderer-responsive {
    text-decoration: overline;
animation: z 10s 10s infinite alternate
}




}
