/*-------------KYAAAAAAA-NO-PEEKING!---------------*/
@font-face {
  font-family: 'Varela Round';
  font-style: normal;
  font-weight: 400;
  src: local('Varela Round'), local('VarelaRound-Regular'), url(https://fonts.gstatic.com/s/varelaround/v7/APH4jr0uSos5wiut5cpjrugdm0LZdjqr5-oayXSOefg.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
}
.settings__apps .app-list {
	background: #212121!important;
}
::-webkit-scrollbar {
    width: 7px!important;
}
::-webkit-scrollbar-thumb {
    background: #444!important; 
}
p, input, textarea {
    font-family: "Varela Round", Meiryo!important;
}
