}
.recents {
    background: #232323;
    opacity: 0.1;
    transition: 0.3s;
    height: 170px;
}
.recents:hover {
    opacity: 1;
}
.recents a {
    width: 88px;
}
.footer .content {
    color: #e0e0e0!important;
    background: #131313!important;
}
.comment__mod {
    background-color: rgba(19, 19, 19, 0.5)!important;
    color: #e0e0e0!important;
}
b, strong {
    color: #fff!important;
    font-family: "Open Sans", Meiryo!important;
}
.thread__pagination .current-page {
