}
ul.commentDepth0.branch, ul.commentDepth2.branch, ul.commentDepth4.branch, ul.commentDepth6.branch, ul.commentDepth8.branch, ul.commentDepth10.branch, ul.commentDepth12.branch, ul.commentDepth14.branch, ul.commentDepth16.branch, ul.commentDepth18.branch, ul.commentDepth20.branch, ul.commentDepth22.branch, ul.commentDepth24.branch, ul.commentDepth26.branch, ul.commentDepth28.branch, ul.commentDepth30.branch, ul.commentDepth32.branch {
    background: #232323!important;
}
ul.commentDepth34.branch, ul.commentDepth36.branch, ul.commentDepth38.branch, ul.commentDepth40.branch, ul.commentDepth42.branch, ul.commentDepth44.branch, ul.commentDepth46.branch, ul.commentDepth48.branch, ul.commentDepth50.branch, ul.commentDepth52.branch, ul.commentDepth54.branch, ul.commentDepth56.branch, ul.commentDepth58.branch, ul.commentDepth60.branch, ul.commentDepth62.branch, ul.commentDepth64.branch, ul.commentDepth66.branch {
