body, a, label, .misc {
    font-size: 14px !important;
}
/* datum */
.date {
    font-size: 12px;
}
LI.wide {
   line-height: 15px !important;
}

/* menu */
NAV UL {
    margin-top:0;
}
/* hlavni slozky */
NAV UL:first-of-type {
    margin-bottom: .3em !important;
    margin-top: 1em;
}

/* A */
NAV LI A {
    line-height: 23px;
    border-bottom: 0px solid white;
    font-size: 13px !important;
}
NAV UL:first-of-type LI A {
    line-height: 29px;
    border-bottom: 0px solid white;
    font-size: 15px !important;
}
NAV UL:nth-of-type(2) LI:first-of-type A {
    line-height: 16px;
    font-size: 10px !important;
    color: silver;
}
.icon.icon-folder {
    height: 13px;
}
.popup.move.select {
    font-size:12px;
}
.popup.move.select DIV {
   line-height: 22px;
}
.popup.move.select .icon {
    width:16px;
}
}
