css:
@-moz-document url("http://free.ishadow.online/") {
body>div:not(#portfolio),
nav,
header,
.section-title.text-center.center,
.container>div.row:first-child,
.ssr,
.row.text-center.center
{
    display: none;
}

.portfolio-items.isotope
{
    height: 800px !important;
    margin-top: 50px;
}

#portfolio
{
    padding-bottom: 0;
}

.hover-bg .hover-text>h4,
.hover-bg .hover-text
{
    opacity: 1;
}

.hover-bg .hover-text>h4
{
    transform: none;
}

.hover-bg .hover-text
{
    padding-top: 15%;
}

.hover-text>h4:last-child,
.hover-bg>img
{
    visibility: hidden;
}
}
