css:
@-moz-document url-prefix("https://kirakiratter.com") {
/*
  Mastodon: Kirakiratter Custom CSS V9.0
  By Salil Gupta (@MasterDalK)
  Other Contributors: @pomo, @sumire, @bitmap, @hisagi, @lae, @takenoko, @unarist
*/

/*[[mui]]*/
/*[[columnsize]]*/
/*[[replyindicator]]*/

/* BACKGROUNDS */
body /* user */,
.about-body /* about */,
.admin-wrapper /* settings */,
.ui /* timeline */ {
  background-color: #e46bac;
  background-image: url("data:image/svg+xml;base64,");
  background-repeat: repeat;
  background-size: auto; /* for user page */
}

/* KKT Banner */
.ui::before {
  position: absolute;
  z-index: 100;
  display: inline;
  content: "";
  min-width: 120px;
  max-width: 30%;
  min-height: 50px;
  pointer-events: none;
  background-image: url('data:image/svg+xml;base64,');
  background-repeat: no-repeat;
  background-size: contain;
}

@media screen and (min-width: 768px) {
  .ui::before {
    top: 9px;
    left: 6px;
  }
}

@media screen and (min-width: 1025px) {
  .ui::before {
    top: 10px;
    left: 6px;
  }
}

/* Banner -- END */

.drawer__header,
.drawer__inner,
.drawer__inner.darker,
.tabs-bar,
.onboarding-modal__page .figure {
  background-color: #e56cac;
  background-image: url('data:image/svg+xml;base64,PHN2ZyB4bWxucz0iaHR0cDovL3d3dy53My5vcmcvMjAwMC9zdmciIGhlaWdodD0iMjgiIHdpZHRoPSIyMCI+PHJlY3QgaGVpZ2h0PSIyOCIgd2lkdGg9IjIwIiB5PSIwIiB4PSIwIiBmaWxsPSIjZTQ2YmFjIi8+PHBhdGggZD0ibTAgMTQgMTAtMTQgMTAgMTQtMTAgMTR6IiBmaWxsPSIjZGY1N2E0Ii8+PC9zdmc+');
}

.column-header,
.column-icon,
.column-back-button,
.column-link {
  background: #d0e093;
}

.column-subheading {
  background: #dced91;
}

@media screen and (min-width: 320px) {
    .column-subheading {
      padding: 4px 20px;
  }

  .confirmation-modal {
    max-width: 300px;
  }
}

@media screen and (min-width: 768px) {
    .column-subheading {
      padding: 7px 20px;
  }

  .confirmation-modal {
    max-width: 380px;
  }
}

.column > .scrollable,
.empty-column-indicator {
  background: rgba(212, 220, 171, 0.5);
}

.media-spoiler {
  background: rgba(0, 0, 0, 0.1);
}

.activity-stream .media-spoiler,
.activity-stream .media-spoiler:hover {
  background: #e2e2c9;
  margin-top: 0px;
}

.account__header > div {
  background: linear-gradient(rgba(0,0,0,0.2), rgba(0,0,0,0.5) 50%, rgba(0,0,0,0.7) 65%, rgba(0,0,0,0.8));
}

.card:after {
  background: linear-gradient(rgba(0,0,0,0.2), rgba(0,0,0,0.4) 60%, rgba(0,0,0,0.6) 75%, rgba(0,0,0,0.8));
}

.status__content .status__content__spoiler-link,
.reply-indicator__content .status__content__spoiler-link,
.activity-stream .status.light .status__content a.status__content__spoiler-link,
.activity-stream .detailed-status.light .status__content a.status__content__spoiler-link {
  background: rgba(237, 42, 144, 0.6);
}

.muted a.status__content__spoiler-link {
  background: #eda1c8;
}

.dropdown--active .dropdown__content > ul,
.dropdown--active .dropdown__content > ul > li > a {
  background: #e3a4ad;
}

.muted a.status__content__spoiler-link:hover,
.status__content .status__content__spoiler-link:hover,
.reply-indicator__content .status__content__spoiler-link:hover,
.activity-stream .status.light .status__content a.status__content__spoiler-link:hover,
.activity-stream .detailed-status.light .status__content a.status__content__spoiler-link:hover,
.dropdown--active .dropdown__content > ul > li > a:hover {
  background: #f5b9c2;
}

.dropdown-active .dropdown__content.dropdown__left > ul {
  left: -14em;
  display: inline-block;
}

.dropdown-active .dropdown__content.dropdown__left > ul li {
  width: 16em;
}

.dropdown__sep {
  border-color: #e57cb1;
}

.dropdown--active .dropdown__content:before {
  border-color: transparent transparent #e3a4ad transparent;
}

.scrollable .status:first-child,
.scrollable .notification:first-child,
.account-timeline__header + .status {
  margin-top: 6px;
}

.status,
.notification,
.onboarding-modal__pager,
.confirmation-modal,
.boost-modal,
.boost-modal__container .status {
  background: linear-gradient(to bottom, rgba(252, 252, 225, 1) 0%, rgba(252, 252, 225, 1) 65%, rgba(252, 252, 225, 1) 65%, rgba(249, 240, 174, 1) 100%);
  border-radius: 8px;
  border: solid 2px #e1c152;
  box-shadow: 0 1px 1px rgba(0, 0, 0, 0.4);
}

.status,
.notification {
  margin-left: 64px;
  margin-right: 2px;
  padding-left: 8px;
  margin-bottom: 8px;
}

.notification {
  margin-left: 1px;   /* Old browsers */
  background: linear-gradient(to bottom, rgba(252, 252, 225, 0.8) 0%, rgba(252, 252, 225, 0.8) 65%, rgba(252, 252, 225, 0.8) 65%, rgba(249, 240, 174, 0.8) 100%);
}

.notification__message {
  margin-left: 25px;
  padding: 6px 0 6px 0;
}

.onboarding-modal,
.boost-modal__action-bar,
.confirmation-modal__action-bar {
  background: none;
}

.account {
  border: none;
}

.detailed-status,
.activity-stream .entry .detailed-status.light,
.status-card, .status-card:hover {
  background: linear-gradient(to bottom, rgba(252, 252, 225, 1) 0%, rgba(252, 252, 225, 1) 65%, rgba(252, 252, 225, 1) 65%, rgba(249, 240, 174, 1) 100%);
  border-radius: 8px;
  margin-bottom: 8px;
  border: solid 2px #e1c152;
  box-shadow: 0 1px 1px rgba(0, 0, 0, 0.4);
}

/* status balloon tail */

.status::before,
.notification::before {
  content: "";
  display: block;
  width: 12px;
  height: 14px;
  position: absolute;
  left: -11px;
  background: url(data:image/svg+xml;base64,);
}

.status.muted::before,
.boost-modal__container .status::before {
  background:
  url(data:image/svg+xml;base64,);
  /*url(data:image/svg+xml;base64,);*/
}

.status.muted,
.boost-modal__container .status {
  box-shadow: none;
  border: solid 2px #e8d17f;
}

.status.muted {
  left: -4px;
}

.muted .status__action-bar .fa {
  opacity: 0.75;
  transition: opacity 300ms;
}

.muted .status__action-bar .fa:hover {
  opacity: 1;
  transition: opacity 300ms;
}

/* avatar */

.status .account__avatar,
.notification .account__avatar,
.status .avatar,
.notification .avatar,
.boost-modal__status-header .account__avatar,
.notification-follow .account__avatar {
  border: solid 2px #e1c152;
}

.status .avatar,
.status .account__avatar {
  box-shadow: 0 1px 1px rgba(0, 0, 0, 0.4);
}

.status.muted .avatar,
.status.muted .account__avatar {
  box-shadow: none;
}

.boost-modal__status-header .account__avatar {
  opacity: 0.65;
}

.status__avatar {
  margin-left: -74px;
  top: 0 !important;
}

.notification-follow .avatar,
.notification-follow .account__avatar {
  left: 0;
  top: 0;
}

/* COLOUR STYLING */
/* Brown */
p, /* Katswoots -- Posts, Tweets, Katsus, TOOTS, take your pick. */
.status__content,
.status-card__description,
.column-subheading, /* Getting Started */
.confirmation-modal__container, /* Modals */
.onboarding-modal__page p span,
.onboarding-modal__page h1 span,
.simple_form .input.file label, /* Forms */
.simple_form .input.select label,
.simple_form .input.radio_buttons label,
.simple_form .input.radio_buttons .radio label,
.simple_form .input.boolean label,
.simple_form input[type=text],
.simple_form input[type=number],
.simple_form input[type=email],
.simple_form input[type=password],
.simple_form textarea,
.simple_form input[type=file],
.flash-message strong, /* 2FA And Settings */
.recovery-codes,
.oauth-prompt,
.follow-prompt,
.admin-wrapper .table,
.filters .filter-subset strong,
.filters .filter-subset a,
.table a.table-action-link,
.about-body .wrapper, /* Landing Page */
.screenshot-with-signup .simple_form .info a,
.screenshot-with-signup .closed-registrations-message .info a,
.landing-strip, /* Profiles */
.accounts-grid .account-grid-card,
.pagination .prev,
.pagination .next,
.pagination .prev a,
.pagination .next a,
.pagination .page a,
.pagination .page.gap,
.report__target strong, /* User Reports */
.report__textarea,
.account-authorize .account__header__content /* Follow Requests */ {
  color: #8b6d5d;
}

/* Lighter Brown */
.muted .status__content p,
.boost-modal__container p,
.loading-indicator {
  color: #ad9588;
}

/* Darker Brown */
.drawer__tab,
.icon-button,
.icon-button.active,
.column-icon,
.column-header,
.column-link,
.column-back-button,
.column-back-button span,
.compose-form__warning span,
.icon-button:active,
.icon-button:focus {
  color: #6a4643;
}

/* User @ Text & Links */

/* a(pink underline) > span(green text) */
/* Pink */
.status__display-name strong, /* Katswoots -- Posts, Tweets, Katsus, TOOTS, take your pick. */
.detailed-status__display-name strong,
.account__display-name strong,
.status__content a,
.status__content .h-card span,
.mention span,
.mention.hashtag span,
.reply-indicator__content a, /* Notifications & Reply Indicator */
.compose-form__warning a,
.h-card,
.h-card span,
.h-card .u-url.mention,
.h-card .u-url.mention span,
.information-board .section strong, /* Landing Page */
.about-body h1,
.contact-email strong,
.owner .name .display_name,
a[href*='/tags/'] span, /* Hashtag Fix */
.status__prepend .status__display-name strong, /* boost/favourited preprend */
.activity-stream .detailed-status.light .detailed-status__display-name .display-name strong, /* Standalone Pages */
.activity-stream .status.light .display-name strong,
.activity-stream .status.light .status__content a,
.activity-stream .pre-header .status__display-name.muted strong,
.activity-stream .detailed-status.light .status__content a,
.activity-stream .detailed-status.light .status__content a.hashtag,
.activity-stream .detailed-status.light .status__content a.mention,
.account__header__content a, /* Profile */
.accounts-grid .account-grid-card .display_name,
.confirmation-modal__container strong, /* Modals */
.onboarding-modal__page p strong,
.table a, /* Settings */
.compact-header h1 a,
.oauth-prompt strong, /* 2FA */
.follow-prompt strong,
.filters .filter-subset a.selected {
  color: #ed649c;
  text-decoration: none;
}

.loading-bar {
  background-color: #ed649c;
}

/* Link Underlines on Hover */
.status__display-name strong:hover,
.detailed-status__display-name strong:hover,
.account__display-name strong:hover,
.status__content a:hover,
.reply-indicator__content a:hover,
.h-card.u-url.p-nickname.mention:hover,
.h-card.u-url.p-nickname.mention span:hover,
.status__content .h-card span:hover,
.mention span:hover,
.mention.hashtag span:hover,
.account__header__content a:hover,
a[href*='/tags/'] span:hover,
.status__prepend .status__display-name strong:hover,
.activity-stream .detailed-status.light .detailed-status__display-name .display-name strong:hover,
.activity-stream .status.light .display-name strong:hover,
.activity-stream .status.light .status__content a:hover,
.activity-stream .detailed-status.light .status__content a:hover,
.activity-stream .pre-header .status__display-name.muted strong:hover,
.activity-stream .detailed-status.light .status__content a.hashtag:hover,
.compose-form__warning a:hover {
  text-decoration: underline;
}

/* Lighter Pink */
.muted .status__display-name strong,
.boost-modal__container .status.light .display-name strong,
.compact-header h1 small,
.muted .status__content a,
.muted .status__content .u-url,
.muted .status__content .u-url span,
.muted .status__content .permalink.mention span {
  color: #f086b1;
}

/* Green */
a span, /* Toots */
.notification__display-name:hover,
.compose-form__warning span strong,
.status__relative-time:hover,
.notification__message,
.detailed-status__meta,
.status-card__title,
.getting-started p, /* Columns */
.status__prepend,
.empty-column-indicator,
.media-spoiler,
.activity-stream .media-spoiler,
.activity-stream .media-spoiler:hover,
.nothing-here,
.footer,
.column-settings__section,
.setting-toggle,
.boost-modal__action-bar>div, /* Modals */
.confirmation-modal__action-bar>div,
.status.light .display-name span,
.activity-stream .pre-header, /* Standalones */
.activity-stream .detailed-status.light .detailed-status__meta,
.activity-stream .detailed-status.light .detailed-status__meta a,
.activity-stream .detailed-status.light .detailed-status__meta .detailed-status__application,
.activity-stream .status.light .display-name span,
.activity-stream .status.light .status__header .status__meta .status__relative-time,
.activity-stream .detailed-status.light .detailed-status__display-name .display-name span,
.account__header__content a span,
.simple_form span.hint, /* Settings */
.report__target {
  color: #8e9956;
}

/* Green - Links turned into not so important links */
.about-body p a,
.owner .name .username,
.about-body .actions .info a,
.screenshot-with-signup .simple_form .info a,
.screenshot-with-signup .closed-registrations-message .info a,
.container .form-footer ul.no-list a,
.accounts-grid .account-grid-card .username,
.compose-form__warning a span {
  color: #8e9956;
	text-decoration: none;
}

/* White */
.status__content__spoiler-link span,
.activity-stream .status.light .status__content a.status__content__spoiler-link ,
.activity-stream .detailed-status.light .status__content a.status__content__spoiler-link {
  /* "!important" is need for a.status__content__spoiler-link to override ".status__content a { color: !important }" in our CSS */
  color: #fff !important;
}

.column-header,
.column-back-button,
.column-link {
  font-family: "Segoe UI", "Roboto";
  font-weight: bold;
}

/* Getting Started */
/* span selector overrides `a > span { color: !important; }` */

.getting-started a,
.getting-started a span {
  color: #707844; /* darken(#8e9956, 10%) */
}
.getting-started a:hover,
.getting-started a:hover span {
  color: #515831; /* darken(#8e9956, 20%) */
}

/* Embed cards */

.status-card:hover {
  border: solid 2px #bc9c2b;
}

.status-card__image {
  background: none;
}

/* ScrollBar -- WebKit only (Chrome/Opera/Vivaldi/Safari) */
::-webkit-scrollbar-track:active,
::-webkit-scrollbar-track:hover {
  background: rgba(0, 0, 0, 0.1);
}

::-webkit-scrollbar-thumb,
::-webkit-scrollbar-thumb:hover {
  background: #6a4643;
}

/* KATSU! */
.button {
  background: #9bc430;
}

.button:hover,
.button:active,
.button:focus {
  background: #85a13b;
}

.button:disabled {
  background: hsla(77, 20%, 60%, 1);
}

.button span {
  white-space: nowrap;
  overflow: hidden;
}

/* Buttons & Tabs that aren't the KATSU! button */
.icon-button.active:hover,
.drawer__tab:hover,
.icon-button:hover,
.column-icon:hover,
.column-header:hover,
.column-link:hover,
.status__prepend div i {
  color: #8b6461;
}

.drawer__tab,
.icon-button.active:hover,
.icon-button:hover,
button.icon-button i.fa-retweet:hover {
  text-shadow: -1px -1px 0 #f5e33a, 1px -1px 0 #f5e33a, -1px 1px 0 #f5e33a, 1px 1px 0 #f5e33a;
}

.icon-button.disabled {
  color: #9c7a78;
}

.icon-button.disabled:hover {
  text-shadow: none;
}

/* Column Update Colours */
.column-header.active {
  box-shadow: 0 1px 0 #ed649c;
}

.column-header.active .fa {
  color: #ed649c;
  text-shadow: 0 0 10px #ed649c;
}

.status-list__unread-indicator,
.notifications__unread-indicator {
  background: radial-gradient(ellipse, rgba(237, 100, 156,0.23) 0%, rgba(43,144,217,0) 60%);
}

/* Katsu Drawer & Search */
.navigation-bar {
  background: hsla(325, 58%, 48%, 0.6);
}

.privacy-dropdown__option__content strong,
.privacy-dropdown__option {
  color: hsl(77, 25%, 20%);
}

.privacy-dropdown__option__content {
  color: hsl(77, 25%, 45%);
}

.icon-button.inverted.active,
.text-icon-button.active {
  color: #96b34c; /* green */
}

.privacy-dropdown__option:hover,
.privacy-dropdown__option.active {
  background: hsla(77, 40%, 50%, 1); /* green */
}
.privacy-dropdown__option.active:hover {
  background: hsla(77, 40%, 55%, 1); /* green */
}

.navigation-bar strong {
  color: #efcfcf !important;
  font-weight: 900 !important;
}

.navigation-bar a[href='/settings/profile'] span {
  color: #dfafaf !important;
  font-weight: 900;
}

.navigation-bar a[href='/settings/profile'] span:hover {
  text-decoration: underline;
}

.tabs-bar__link.active,
.column-settings--outer,
.column-settings__outer,
.account__action-bar,
.column-link:hover,
.collapsable:hover,
.load-more:hover {
  background: rgba(255, 255, 255, 0.7);
}

.drawer__tab:hover {
  text-shadow: none;
}

.column-header:focus,
.column-icon:focus,
.status__content__spoiler-link:focus,
.column-back-button:focus {
  outline: none;
}

.search-results__header {
  background: hsla(325, 58%, 48%, 0.6);
  border-bottom: solid 1px #e3a4ad;
}

.search-results__section {
  background: hsla(325, 58%, 48%, 0.6);
}

.compose-form__buttons .icon-button:hover {
  text-shadow: none;
}

.dropdown--active .dropdown__content > ul > li > a:hover {
  color: #fff;
}

div.drawer__inner div div div div span[style*="color: rgb(255, 80, 80)"],
.character-counter--over {
  color: #efcfcf !important; /* red character count */
}

div.drawer__inner div:not(.navigation-bar) div:last-child div:last-child div:nth-child(n+1) {
  padding-top: 5px !important;
}

.search-results__hashtag,
.search-results__hashtag:hover,
.search-results__hashtag:active,
.search-results__hashtag:focus,
.search-results .account__display-name strong,
.search-results__header span {
  color: #efcfcf !important;
}

.search-results .display-name span {
  color: #dfafaf;
}

.search__icon .fa.active {
  color: #fff;
  opacity: 1;
}

.search__icon .fa-times-circle.active {
  color: #fff;
  opacity: 1;
}

.search__input {
  background: #d0e093;
  color: #7c8658;
}

.search__input::-webkit-input-placeholder {
  color: #7c8658;
}
.search__input::-moz-placeholder {
  color: #7c8658;
}
.search__input:-ms-input-placeholder {
  color: #7c8658;
}
.search__input:-moz-placeholder {
  color: #7c8658;
}

.search__input:focus::-webkit-input-placeholder {
  color: #fff;
}
.search__input:focus::-moz-placeholder {
  color: #fff;
}
.search__input:focus:-ms-input-placeholder {
  color: #fff;
}
.search__input:focus:-moz-placeholder {
  color: #fff;
}

.search__input:focus {
  background: #7c8658;
  color: #fff;
}


.search__input, .autosuggest-textarea__textarea {
	font-size: 16px;
}

@media screen and (min-width: 1025px) {
	.search__input, .autosuggest-textarea__textarea {
		font-size: 14px;
	}
}

.compose-form__warning {
  background-color: #f5b9c2;
  border: 0;
}


/* iOS9 Fixes */

.column-collapsable,
.getting-started__wrapper {
  flex-shrink: 0;
}

.mastodon-column-container[aria-hidden="true"] {
 display: none;
}

.column-collapsable div:nth-child(2) {
  overflow: auto !important;
}

/* Column Settings */

.react-toggle-track {
  background: rgba(0, 0, 0, 0.5);
}

.react-toggle:hover:not(.react-toggle--disabled) .react-toggle-track {
  background: rgba(0, 0, 0, 0.3);
}

.react-toggle-thumb {
  border-color: rgba(0, 0, 0, 0.5);
}

.react-toggle--checked .react-toggle-track {
  background: #9bc530;
}

.react-toggle--checked:hover:not(.react-toggle--disabled) .react-toggle-track {
  background: #b4d75b;
}

.react-toggle--checked .react-toggle-thumb {
  border-color: #9bc530;
}

.setting-text {
  color: #777;
  outline: none;
  border-color: rgba(0, 0, 0, 0.3);
  border-radius: 2px 2px 0 0;
  padding: 7px 4px;
}

.setting-text:focus,
.setting-text:active {
  color: #1a1a1a;
  background: rgba(0, 0, 0, 0.05);
  border-color: rgba(0, 0, 0, 0.5);
}

/* Profile Column */
.dropdown {
  width: 32px;
}

.account__action-bar div:first-child {
  max-width: 32px;
}

.account__action-bar div:last-child {
  max-width: calc(86% - 32px);
  justify-content: space-between;
}

.account__action-bar {
  border-top: 1px solid rgba(0, 0, 0, 0.6);
  border-bottom: 1px solid rgba(0, 0, 0, 0.6);
}

.account__action-bar__tab {
  border-left: 1px solid rgba(0, 0, 0, 0.6);
}

/* Follow Requests */
.account--panel {
  background: rgba(255,255,255,0.4);
  border-color: #8b6d5d;
}

.account-authorize {
  background: rgba(212, 220, 171, 0.5);
}

/* User Report */

.scrollable.report {
  background: rgba(212, 220, 171, 0.5); /* transparentize(mix(#ecf5be, black, 90%), 0.5) */
}

.status-check-box .status__content {
  background: rgba(255, 255, 255, 0.7);
}

.status-check-box, .report__target {
  border-bottom: 1px solid rgba(0, 0, 0, 0.6);
}

.report__textarea {
  border-color: rgba(0, 0, 0, 0.3);
}

.report__textarea:active,
.report__textarea:focus {
  border-color: rgba(0, 0, 0, 0.5);
  background: rgba(0, 0, 0, 0.05);
}

/* Drag and drop to upload */

.upload-area {
  background: rgba(0, 0, 0, 0.3);
}

.upload-area__background {
  background: #E488B9;
}

.upload-area__content {
  color: #FFF;
  border-color: #D84A9D;
}

/* Onboarding Modal */

.onboarding-modal__pager {
  border-bottom-left-radius: 0;
  border-bottom-right-radius: 0;
  border: none;
}

@media screen and (max-width: 550px) {
  .onboarding-modal__pager {
    border-radius: 0;
    border: none;
    margin-bottom: -1px;
  }
}

.onboarding-modal__page p span a {
  color: #8e9956 !important;
  text-decoration: none;
}

.onboarding-modal__page p span a:hover {
  text-decoration: underline;
}

.onboarding-modal__page p strong {
  background: #f5b9c2;
}

.onboarding-modal__page .figure .pseudo-drawer {
  background: none;
}

.onboarding-modal__paginator {
  background: linear-gradient(to bottom, rgba(226,72,152,1) 0%,rgba(229,108,172,1) 100%);
}

.onboarding-modal__paginator > div {
  /* set Japanese prev/next text same width to align dots center */
  min-width: 5em;
  text-align: center;
}

.onboarding-modal__paginator a span {
  color: #efcfcf;
}

.onboarding-modal__dot {
  background: #6a4643;
}

.onboarding-modal__dot:hover {
  background: #8b6461;
}

.onboarding-modal__dot.active {
  background: #a07774;
  box-shadow: #f5e33a 0px 0px 5px 0px;
}

/* e.g. Safari on iPhone5 with top/bottom bar (320x460) */
@media screen and (max-width: 550px) and (max-height: 550px) {
  .onboarding-modal__page p {
    font-size: 12px;
  }
  .onboarding-modal__paginator {
    padding: 20px 25px;
  }
}

@media screen and (max-width: 550px) {
  .onboarding-modal__page-one {
    flex-direction: column;
  }

  .onboarding-modal__page-one__elephant-friend {
    width: 100%;
    height: 30vh;
    margin-bottom: 5vh;
    background-position: center;
  }
}

.onboarding-modal__page-two .non-interactive span {
  color: #ffffff;
}

.onboarding-modal__page-four__columns .column-header {
  color: #6a4643;
}

.onboarding-modal__page-four {
  line-height: 17px;
}

.onboarding-modal__page-six p span a[to] {
  color: #ed649c !important;
}

/* Confirmation Modals */

.boost-modal__action-bar,
.confirmation-modal__action-bar {
  padding-top: 0px;
}

.confirmation-modal__container {
  padding-bottom: 20px;
}

.boost-modal__container {
  overflow-x: auto;
}


/* Dropdown Menus */
.dropdown--active .dropdown__content > ul > li > a {
  width: unset;
}

.detailed-status__action-bar .dropdown--active .dropdown__content {
  position: relative;
}

@media screen and (min-width: 360px) {
  .status .dropdown--active .dropdown__content.dropdown__right > ul {
    left: -98px;
  }
}

@media screen and (min-width: 1500px) {
  .status .dropdown--active .dropdown__content.dropdown__right > ul {
    left: 0;
  }

  .detailed-status__action-bar .dropdown--active .dropdown__content.dropdown__left > ul {
    margin-left: 0;
  }
}

/* Adjust Drawer/Tab Buttons to fit with banner */

.drawer__header,
.tabs-bar {
  height: 50px;
  border-bottom: 2px solid #f5e33a;
}

@media screen and (min-width: 320px) {
  .tabs-bar {
    padding-left: 90px;
  }

  .tabs-bar__link {
    padding: 17px 4px;
  }
}

@media screen and (min-width: 768px) {
  .tabs-bar {
    padding-left: 75px;
  }

  .tabs-bar:first-child {
    margin-left: 10px;
  }

  .tabs-bar__link {
    padding: 16px 8px;
  }
}

@media screen and (min-width: 1025px) {
  .column,
  .drawer {
    padding-bottom: 0;
  }

  .drawer {
    width: 320px;
  }

  .column:last-child,
  .drawer:last-child {
    padding-right: 5px;
  }

  .drawer__header {
    padding-left: 83px;
  }

  .drawer__tab {
    padding: 15px 4px;
  }
}

/* Tab Selection on Mobile */
.tabs-bar__link {
  text-shadow: -1px -1px 0 #f5e33a, 1px -1px 0 #f5e33a, -1px 1px 0 #f5e33a, 1px 1px 0 #f5e33a;
}

.tabs-bar__link,
.drawer__tab {
  border-bottom: none;
}

.tabs-bar__link span,
.tabs-bar__link {
  color: #6a4643;
  opacity: 0.9;
}

.tabs-bar__link:hover,
.tabs-bar__link.active:hover {
  background: rgba(255, 255, 255, 0.7);
  opacity: 1;
  text-shadow: none;
}

.tabs-bar__link.active {
  border-bottom: none;
  text-shadow: none;
}

.tabs-bar__link.active span,
.tabs-bar__link.active {
  color: #6a4643;
  text-shadow: none;
}

@media screen and (min-width: 320px) {
  .columns-area {
    padding: 0;
  }

  .tabs-bar {
    margin: 0;
  }

  .search {
    margin-bottom: 0;
  }
}

@media screen and (min-width: 768px) {
  .columns-area {
    padding: 10px 10px 0 10px;
  }

  .tabs-bar {
    margin: 10px 10px 0 10px;
  }

  .search {
    margin-bottom: 10px;
  }
}

@media screen and (min-width: 1025px) {
  .columns-area {
    padding: 0;
  }
}
/* MOBILE -- END */

