button.icon-button.active i.fa-retweet {
  background-position: 0 100%;
  -webkit-transition-duration: 0.9s, 100ms;
  transition-duration: 0.9s, 100ms;
}

.drawer__header a:hover {
  background: rgba(255, 255, 255, 0.7);
}

.detailed-status__action-bar {
  background: transparent;
  border: none;
  padding-top: 0;
}

.icon-button.active .fa-star {
  color: rgb(202, 143, 4);
}

/* Video player width hack */
.status div[style*="width: 239px"] {
  width: 100% !important;
}

/* STANDALONE KKT PAGES */
.activity-stream {
  padding-top: 10px;
}

.activity-stream .entry .detailed-status.light,
.activity-stream .entry .status.light,
.activity-stream .entry:first-child .status.light,
.activity-stream .entry:last-child .status.light,
.activity-stream .entry:last-child .detailed-status.light,
.activity-stream .entry:first-child .status.light,
.activity-stream .entry:last-child .status.light,
.activity-stream .entry:last-child .detailed-status.light {
  border: solid 2px #e1c152;
  border-radius: 8px
}

.activity-stream .entry {
  background: none;
  padding: 2px 0 2px 0;
}

.activity-stream .status.light {
  padding: 8px 8px 8px 15px;
}

.activity-stream .entry .pre-header {
  margin-bottom: 0;
  padding: 0 0 0 76px;
}

.activity-stream .entry .status.light .status__avatar {
  border: none;
  top: 0;
  left: 10px;
}

.activity-stream .entry .status.light .status__avatar img {
  border: solid 2px #e1c152;
  box-shadow: 0 1px 1px rgba(0, 0, 0, 0.4);
}

/* --- admin / settings --- */

/* background */
/* We use colors darkened from #ecf5be with medium alpha values, to reduce contrast of background pattern. */

.admin-wrapper .sidebar-wrapper {
  background: rgba(173,186,121,0.5);
}
.content-wrapper {
  /* background: rgba(201,216,141,0.8); */
  background: linear-gradient(to bottom, rgba(252, 252, 225, 1) 0%, rgba(252, 252, 225, 1) 25%, rgba(252, 252, 225, 1) 65%, rgba(249, 240, 174, 1) 100%);
}

/* sidebar */

.admin-wrapper .sidebar ul ul,
.admin-wrapper .sidebar ul a.selected {
  background: #d0e093;
}

.admin-wrapper .sidebar ul a {
  color: #6a4643;
}
.admin-wrapper .sidebar ul a:hover {
  background: rgba(255, 255, 255, 0.7);
  color: #8b6461;
}

.admin-wrapper .sidebar ul ul a.selected {
  background: rgba(255,255,255,0.3);
  color: #6a4643;
}
.admin-wrapper .sidebar ul ul a.selected:hover {
  background: rgba(64,64,64,0.1);
  color: #6a4643;
}

.flash-message {
    background: rgba(255,255,255,0.7);
}

/* content */

.admin-wrapper .content > p{
  color: #6a4643;
}

.admin-wrapper .content h2 {
  color: #6a4643;
}

/* forms */
.simple_form input[type=text],
.simple_form input[type=number],
.simple_form input[type=email],
.simple_form input[type=password],
.simple_form textarea {
  border-bottom: 2px solid rgba(0,0,0,0.3);
}

.simple_form input[type=text]:active, .simple_form input[type=text]:focus,
.simple_form input[type=number]:active, .simple_form input[type=number]:focus,
.simple_form input[type=email]:active, .simple_form input[type=email]:focus,
.simple_form input[type=password]:active, .simple_form input[type=password]:focus,
.simple_form textarea:active, .simple_form textarea:focus {
  border-bottom: 2px solid rgba(0,0,0,0.5);
  background: rgba(0,0,0,0.05);
}

.simple_form button,
.simple_form .block-button {
  background: #9bc430;
}

.simple_form button:hover,
.simple_form button:active,
.simple_form button:focus,
.simple_form .block-button:hover,
.simple_form .block-button:active,
.simple_form .block-button:focus {
  background: #85a13b;
}

/* tables (used in authorized_applications, export) */

.table > tbody > tr:nth-child(odd) > td,
.table > tbody > tr:nth-child(odd) > th {
  background: rgba(255,255,255,0.5);
}

.table > thead > tr > th {
  border-bottom: 2px solid rgba(0,0,0,0.6);
}

.table th, .table td {
  border: none;
}

.table a:hover {
    text-decoration: underline;
}

a.table-action-link {
  text-decoration: none;
}

a.table-action-link:hover {
  color: #6c5549;
  text-decoration: underline;
}

/* --- about --- */

.about-body .wrapper,
.accounts-grid .account-grid-card,
.screenshot-with-signup .simple_form,
.screenshot-with-signup .closed-registrations-message {
  background: linear-gradient(to bottom, rgba(252, 252, 225, 1) 0%, rgba(252, 252, 225, 1) 65%, rgba(252, 252, 225, 1) 65%, rgba(249, 240, 174, 1) 100%);
  border-radius: 8px;
  border: solid 2px #e1c152;
}

.about-body .wrapper,
.accounts-grid .account-grid-card {
  border-radius: 8px;
  border: solid 2px #e1c152;
}

.accounts-grid .account-grid-card,
.screenshot-with-signup .simple_form,
.screenshot-with-signup .closed-registrations-message {
  padding: 25px 20px;
  margin: 25px auto;
}

@media screen and (min-width: 320px) {
  body .about-body {
    padding: 0;
  }

  .about-body .wrapper {
    margin: 0;
  }

  .wrapper h1 {
    font-size: 39px;
  }
}

@media screen and (min-width: 768px) {
  body .about-body {
    padding: 1em;
  }

  .about-body .wrapper {
    padding: 25px 20px;
    margin: 25px auto;
  }

  .wrapper h1 {
    font-size: 46px;
  }
}

.about-body h2,
.about-body h3 {
  color: #8b6461;
}

.about-body em {
  color: #6d4e4c; /* darken(#8b6461, 10%) */
  background-color: #f292b9; /* lighten(#ed649c, 10%) */
}

.screenshot-with-signup .simple_form,
.screenshot-with-signup .closed-registrations-message {
  box-shadow: 0 1px 1px rgba(0, 0, 0, 0.4);
}

/* /about/more */

.sidebar-layout .panel .panel-header {
  background: #C9D185;
}

.sidebar-layout .sidebar,
.information-board,
.sidebar-layout hr {
  border-color: #C9D185;
  border-top: none;
}

.sidebar-layout .panel .panel-list ul li a {
  color: #6a4643;
}

.sidebar-layout .panel .panel-list ul li a:hover {
  background: rgba(255, 255, 255, 0.7);
  color: #8b6461;
}

.information-board .section span {
  color: inherit;
}

.information-board .section span:last-child {
  color: inherit;
}

.about-body p a:hover,
.about-body .actions .info a:hover,
.screenshot-with-signup .simple_form .info a:hover,
.screenshot-with-signup .closed-registrations-message .info a:hover,
.container .form-footer ul.no-list a:hover {
    text-decoration: underline;
}

.owner .name .display_name,
.accounts-grid .account-grid-card .display_name {
    font-weight: bold;
}
/* --- User (/@hogehoge) --- */

.landing-strip {
  background: rgba(208,224,147,0.7);
}

.card {
  background: rgba(0, 0, 0, 0.3);
}

.card .name span {
  color: #d9e1e8;
}

.card .name small span {
  color: #2b90d9;
}

.account__header__content {
  color: #d9e1e8;
}

.activity-stream {
  box-shadow: none;
  background: rgba(0, 0, 0, 0.1);
}

.activity-stream:first-child {
  /* .activity-stream is also used in status page, without account header. */
  background: none;
}

.pagination .page.current {
  color: #FFF;
  background: #8b6d5d;
}

.pagination .page a:hover,
.pagination .prev a:hover,
.pagination .next a:hover {
  background-color: #fff;
  border-radius: 100px;
}

.card .counter,
.card .counter::after {
  border-color: #8b6d5d;
}

.card .counter.active:after {
  border-color: #f5e33a;
}

/* Followers & Following */

.accounts-grid {
  background: rgba(0,0,0,0.1);
  box-shadow: none;
}

.accounts-grid .account-grid-card {
  padding: 0;
  margin: 10px 0;
  box-shadow: 0 1px 1px rgba(0, 0, 0, 0.4);
}

.accounts-grid .account-grid-card .account-grid-card__header {
  border-bottom: 1px solid #e3a4ad;
}

.accounts-grid .account-grid-card .note {
    color: #8b6461
}

/* OAuth, Remote follow */

.form-container {
  background: linear-gradient(to bottom, rgba(252, 252, 225, 1) 0%, rgba(252, 252, 225, 1) 65%, rgba(252, 252, 225, 1) 65%, rgba(249, 240, 174, 1) 100%);
  border-radius: 8px;
  border: solid 2px #e1c152;
}

.form-container .flash-message code {
    word-wrap: break-word;
}

/* Admin */
/* /admin/accounts, etc. */

.filters .filter-subset a {
  border-bottom: #fcfce1;
}

.filters .filter-subset a:hover {
  color: #6c5549;
  border-color: rgba(0, 0, 0, 0.4);
}

.filters .filter-subset a.selected {
  border-color: #ed649c;
}

}

style_setting_options.value:
@font-face { font-family: 'Meiryo UI'; font-style: normal; font-weight: normal; letter-spacing: .1em; padding: 1em; }
@media screen and (-webkit-min-device-pixel-ratio:0) {
  @font-face { font-family: 'Meiryo UI'; letter-spacing: .1em; padding: 1em; }
}
body { font-family: "Roboto", "Meiryo UI"; font-style: normal; font-weight: normal; letter-spacing: .1em; padding: 1em; }
