css:
@-moz-document domain("www.tabloidpulsa.co.id") {
.module-title {
    color: red;
}
.allmode-text {
    color: black;
}
.clear-fix {
    background-color: darkblue;
}
#header-block {
    background-color: #2C3E50;
    margin: 0;
}
.jrFieldGroupTitle{
    color:black;
}
.top-a-pattern {
    position: static;
}
.header-bg-color {
    background-color: black;
}
#background-image {
    background-image: url(http://www.colorhexa.com/34495e.png)
}
.even {
    background-color: black;
}
div {
    background-color: #2C3E50;
    color: white;
}
span {
    color: white;
}
a {
    color: #C8F7C5;
}
h1 {
    color: white;
}
h2 {
    color: white;
}
.allmode {
    color: white;
}
h4 a {
    color: white;
}
.jrRoundedPanel {
    background-color: #95A5A6;
    background-image: none;
}
.jrFieldDiv {
    background-color: #95A5A6;
}
.jrLeft {
    background-color: #95A5A6;
}
.jrShadowBox {
    background-color: #2C3E50;
}
.toolbar-pattern,
#toolbar,
.float-left,
.dropdown-bg,
.column {
    background-color: #F64747;
}
.menu,
.menu-dropdown,
.level1 {
    color: white;
}
.nspArt h4.nspHeader a {
    text-decoration: none;
    font-weight: bold;
    color: white;
}
.allmode-default a {
    color: white !important;
}
.allmode-columns .allmode-item .allmode-text {
    /* Item TEXT */
    
    font-size: 13px;
    line-height: 14px;
    color: white;
}
.allmode-columns .allmode-item .allmode-info {
    /* Item Info DIV */
    
    margin-bottom: 8px;
    padding: 2px 6px;
    background-color: #2C3E50;
}
.allmode-columns .allmode-category,
.allmode-columns .allmode-hits,
.allmode-columns .allmode-author,
.allmode-columns .allmode-item .allmode-date {
    padding: 0 10px 0 17px;
    font-size: 11px;
    color: white;
}
.allmode-default .allmode-topitem .allmode-text {
    /* TOP Item TEXT */
    
    font: normal 12px/15px "Trebuchet MS", Arial, sans-serif;
    color: white;
}
div.catItemHeader h3.catItemTitle a,
h2.tagItemTitle a,
div.userItemHeader h3.userItemTitle a {
    color: antiquewhite;
}
}
