css:
@-moz-document domain("www.epicgames.com") {
/* Backgrounds */ 
.private-message-widget .main-pane .message-list.pm-notification-list .message-item.notification-unread {
background: #101010 !important;
}

body, #channel-tabbar, #header, .b-editor .cke_bottom, .view-mode .widget-tabs .widget-tabs-nav  {
background: #181818 !important;
}

#content, #breadcrumbs, #channel-tabbar .channel-tabbar-list li.current a, #channel-tabbar .channel-tabbar-list li.current a:hover, .edit-mode #channel-tabbar .channel-tabbar-list li.current a:hover,.b-post,.bbcode_container .bbcode_quote, .bbcode_container .bbcode_code, .topic-list-container .topic-list-header, .widget-tabs .widget-tabs-nav li.ui-tabs-active, .widget-tabs .widget-tabs-nav .ui-tabs-nav, .b-post-attachments, .profile-sidebar-widget .profile-menulist .profile-menulist-item > a > .subscriptions-count, .profile-sidebar-widget .profile-menulist .profile-menulist-item .subscriptions-count, #mediaList .item-album, .private-message-widget .message-list .message-item, .forum-list-container .forum-item, .forum-list-container .subforum-list, .blogmember-list .list-container .list-item, .sg-groups-list .sg-groups-list-container .list-item, .b-top-menu__background:before  {
background: #202020 !important;
}

.list-footer, .conversation-toolbar-wrapper .conversation-toolbar, .forum-list-container .forum-list-header, .b-post--first .b-post__body, .b-post__body,#channel-subtabbar, .topic-list-container .topic-list, .forum-list-container .subforum-list, .b-tabbed-pane__pane.b-tabbed-pane__pane--selected, .b-editor .cke_top, .topic-list-container .topic-list .topic-item.sticky, .private-message-widget .main-pane .main-pane-header, .private-message-widget .folder-link.selected, .private-message-vidget .folder-link.selected[href]:hover, .private-message-widget .folder-link.selected[data-href]:hover, .canvas-layout-container .private-message-toolbar, .canvas-layout-container .private-message-search, .profile_sidebar_content, .b-layout--contiguous .subscriptions-widget .widget-content .subscribeTabs .subscription-list-container .subscription-list-header, .private-message-widget .main-pane .message-list .message-item.message-unread, .private-message-widget .last-msg-unread  {
background: #303030 !important;
}

.profileTabs .widget-tabs-nav li a {
background: #202020 ;
}

#profileTabs.profileTabs, #profileTabs.profileTabs .conversation-list.stream-view .list-item, #profileTabs.profileTabs .widget-content, #profileTabs.profileTabs .post-content, .profileTabs .widget-tabs-nav li.ui-tabs-active a {
background: #303030 ;
}

#main-navbar, #main-navbar-wrapper,#footer-tabbar, .forum-list-container .category-header, .blockbody, .message-empty, .private-message-widget .nav-pane, #header .search-container .search-box .search-term, #channel-tabbar .search-term, .b-toolbar, .b-editor,.b-content-entry-panel, .dialog-container, .b-layout--contiguous .subscriptions-widget .widget-content .subscribeTabs .subscription-list-container .subscription-list .subscription-item, .conversation-empty, .profileTabs .section .section-header, .b-top-menu__background   {
background: #404040 !important;
}

#header .search-container .search-box.search-btn-inside-box .search-btn, #channel-tabbar .search-btn, .profileTabs .conversation-toolbar-wrapper .conversation-toolbar .b-button.b-button--secondary, .profileTabs .conversation-toolbar-wrapper .conversation-toolbar .b-button.b-button--secondary:hover, .profileTabs .conversation-toolbar-wrapper .conversation-toolbar a.b-button.b-button--secondary, .profileTabs .conversation-toolbar-wrapper .conversation-toolbar a.b-button.b-button--secondary:visited, .b-button--secondary, .b-button--secondary:link, .b-button--secondary:visited {
background: #505050 !important;
}

.textbox {
background: #909090 !important;
}

/* Color */

body, #breadcrumbs .crumb-link, .widget-content, .post-content, .widget-tabs.ui-tabs .ui-widget-content a{
color: #ddd;
}

.conversation-toolbar-wrapper .conversation-toolbar, .forum-list-container .forum-list-header, .page-title-widget .module-title, .page-title-widget .module-title h1, .b-tabbed-pane__tab--underline.b-tabbed-pane__tab--selected > .b-tabbed-pane__tab-link, .private-message-widget .folder-link, .private-message-widget .main-pane .main-pane-header, .topic-list-container .topic-list-header, .profile-sidebar-widget .profile-menulist .profile-menulist-item > a > label, .profileTabs .conversation-toolbar-wrapper .conversation-toolbar .b-button.b-button--secondary, .profileTabs .conversation-toolbar-wrapper .conversation-toolbar .b-button.b-button--secondary:hover, .profileTabs .conversation-toolbar-wrapper .conversation-toolbar a.b-button.b-button--secondary, .profileTabs .conversation-toolbar-wrapper .conversation-toolbar a.b-button.b-button--secondary:visited, .b-button--secondary, .b-button--secondary:link, .b-button--secondary:visited, .forum-list-container .forum-item, .forum-list-container .subforum-list, .blogmember-list .list-container .list-item, .sg-groups-list .sg-groups-list-container .list-item {
color: #fff !important;
}

#channel-tabbar .channel-tabbar-list li.current a, #channel-tabbar .channel-tabbar-list li.current a:hover, .edit-mode #channel-tabbar .channel-tabbar-list li.current a:hover, #channel-subtabbar .channel-subtabbar-list li a, #header .search-container .search-box .search-term, #channel-tabbar .search-term, .b-userinfo, .widget-tabs .widget-tabs-nav li.ui-tabs-active a, .widget-tabs .widget-tabs-nav li a, #profileTabs.profileTabs, #profileTabs.profileTabs .conversation-list.stream-view .list-item, #profileTabs.profileTabs .widget-content, #profileTabs.profileTabs .post-content {
color: #909090 !important;
}

.b-divider--post-controls {
color: #202020 !important;
border-style: solid;
}



/* Links */
a, a:active, a:visited, .b-link, .ui-widget-content a, .widget-tabs.ui-tabs .ui-widget-content a, .widget-tabs.ui-tabs .ui-widget-content a:active, .widget-tabs.ui-tabs .ui-widget-content a:visited, .sb-dialog.ui-dialog-content.ui-widget-content a, .sb-dialog.ui-dialog-content.ui-widget-content a:active, .sb-dialog.ui-dialog-content.ui-widget-content a:visited{
color: #999;
}

/* Other borders */
#header .search-container .search-box .search-term, #channel-tabbar .search-term {
border-top: 1px solid #404040;
border-left: 1px solid #404040;
border-bottom: 1px solid #404040;
box-shadow: none !important;
}
#header .search-container .search-box.search-btn-inside-box .search-btn, #channel-tabbar .search-btn {
border: 1px solid #404040;
}

.forum-list-container .category-header td, .subchannel-widget .forum-list-container .forum-item td, .profile-sidebar-widget .profile-menulist .profile-menulist-item  {
border-top: 1px solid #505050 !important;
}

.subchannel-widget .list-footer {
border-bottom: 1px solid #505050 !important;
}



/* No Borders */
.subchannel-widget .forum-list-container, .conversation-toolbar-wrapper .conversation-toolbar, .forum-list-container .forum-list-header, #channel-tabbar, .topic-list-container .topic-list-header .header-lastpost, .topic-list-container .topic-list-header .header-topic, .topic-list-container,.conversation-list.stream-view, .b-top-menu__background:before, .b-top-menu__item {
border: none !important;
}

/* Dark Borders */

.b-post, .restore th, .restore td, .b-tabbed-pane__pane.b-tabbed-pane__pane--selected, .widget-tabs .widget-tabs-nav li.ui-tabs-active, .view-mode .widget-tabs .widget-tabs-nav, .b-toolbar, .b-editor,.b-content-entry-panel,.b-post-attachments, .bbcode_container .bbcode_code, .view-mode .b-layout--contiguous .b-module--contiguous, .private-message-widget .main-pane .main-pane-header, .profile-sidebar-widget .profile-menulist .profile-menulist-item > a > .subscriptions-count, .profile-sidebar-widget .profile-menulist .profile-menulist-item .subscriptions-count, .galleryLink .thumbnail-photo, .dialog-container, .b-layout--contiguous .subscriptions-widget .widget-content .subscribeTabs .subscription-list-container .subscription-list-header, .b-layout--contiguous .subscriptions-widget .widget-content .subscribeTabs .subscription-list-container .subscription-list, .b-button--secondary, .b-button--secondary:link, .b-button--secondary:visited, .section .section-header, #mediaList .item-album  {
border: 1px solid #101010 !important;
}

.topic-list-container .topic-list .topic-item td, .b-editor .cke_bottom, #channel-tabbar .channel-tabbar-list li.current a, #channel-tabbar .channel-tabbar-list li.current a:hover, .edit-mode #channel-tabbar .channel-tabbar-list li.current a:hover, .profile-sidebar-widget .profile-menulist, .b-layout--contiguous .subscriptions-widget .widget-content .subscribeTabs .subscription-list-container .subscription-list .subscription-item .td, #footer-tabbar, .private-message-widget .main-pane .message-list .message-item  {
border-top: 1px solid #101010 !important;
}

.forum-list-container .forum-list-header th, #channel-subtabbar, .b-editor .cke_top, .profile-sidebar-widget .profile-menulist .profile-menulist-item, .dialog-container.ui-dialog .ui-dialog-titlebar {
border-bottom: 1px solid #101010 !important;
}

.forum-list-container .forum-list-header .header-forum {
border-left: 1px solid #101010 !important;
}

.forum-list-container .forum-list-header .header-lastpost, .b-toolbar__item {
border-right: 1px solid #101010 !important;
}

#header .site-logo img {
background-image: url("http://i.imgur.com/iyMwXzI.png");
}


.profileTabs .conversation-toolbar-wrapper .conversation-toolbar .b-button.b-button--secondary, .profileTabs .conversation-toolbar-wrapper .conversation-toolbar .b-button.b-button--secondary:hover, .profileTabs .conversation-toolbar-wrapper .conversation-toolbar a.b-button.b-button--secondary, .profileTabs .conversation-toolbar-wrapper .conversation-toolbar a.b-button.b-button--secondary:visited, .profileTabs .widget-tabs-nav li {
border-color: #101010;
}
}
