css:
@-moz-document domain("www.capfriendly.com"), domain("capfriendly.com") {
.wbg {
    background: #21415a;
    border-radius: 3px;
    border: 1px solid rgb(223,223,223);
    border-bottom: 1px solid rgb(204,204,204);
}

body {
    color: #FFF;
    background: rgb(33, 65, 90);
    background-image: url(https://i.imgur.com/kWZH1zg.jpg);
    background-position: 0 60px;
    background-repeat: no-repeat;
}

.column_head {
    background: #babbbd;
    font-size: 14px;
    color: #240407;
    border-bottom: 1px solid #1477ba;
}

a, a:link {
    color: #00ffaf;
    text-decoration: none;
}

a.hid_ul div {
    background-color: #2d516d !important; 
}

.odd td {
    background-color: #2d516d !important;
}

.even td {
    background-color: #1b364a !important;
}

.trade_d {
    color: #d4d4d4;
}

#sld_wrap .c {
    background-color:  #1b364a;
}

.bx-wrapper {
    border: 5px solid  #1b364a;
    background: #fff;
}

#rost_a div {
    background-color: #2d516d;
}

.column_head3 {
    background: #21415a;
    color: #fff;
}

.p_stats {
    color: #fff;
}

.td_stats_odd {
    color: #FFF;
	background-color: #2d516d;
}

.td_stats_even {
    color: #FFF;
    background-color: #1b364a;
}

.clause {
    padding: 7px 5px;
    background: #babbbd;
    color: #240407;
}

a.hid_anc {
    color: #e20090 !important;
}

.div_cent {
    background-color: #2d516d;
}

.for_tr_cont {
    background-color: #2d516d;
}

.pl5 {
    background-color: #1b364a !important;
}

.p_edtd {
    background: #2d516d;
}

.quote_t {
    border-bottom: 1px solid rgb(181,181,181);
    background: #1b364a;
}

.quote {
    background: #2d516d;
}

.text {
    background: #21415a;
}

.mb10 {
    background: #2d516d !important;
}

h3 {
    background-color: #21415a;
}
}
