css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("sootoday.com") {

body{background:#FFF8DC !important;}
body *{font:12px Verdana !important;}
table{background-color:transparent !important;}
a{color:#000000 !important;}
div#all{border:3px solid #000000 !important; margin-top:10px !important; padding:5px !important; background:#FFFFFF !important;}
div#h-t{background:#000000 !important; height:80px !important;}
div#h-t a{display:block !important; width:148px !important; height:80px !important; background:transparent url(data:image/png;base64,) no-repeat center left !important;}

div.bx-bg, div.sd-tt, div#ft, div.contenttitle{background:#000000 !important; color:#FFFFFF !important; background-image:none !important;}
div#ft a{color:#FFFFFF !important;}
a[target="_blank"]{display: none !important;}
div#m-sp-i{padding-right:0 !important;}
div#m-sp, div#m{width:659px !important;}
div#m-i{padding-right:0 !important;}
div.content{width:100% !important;}
div.sd{background-color:#FFFFFF !important; border-left:1px solid #000000 !important; border-right:1px solid #000000 !important;}
div#h-b *{background:none !important;}
em{font-weight:bold !important;}

div#h-t a img, div#r, div#h-ban, div.sd-tt, div#r-sp, div#m-i p a, object, object#widget, div[style$="margin-top: 8px;"] {display:none !important;}


}
