/*Thème Sombre [NightCat] CSS pour CommentCaMarche.net*/
/*Nom : CCM NightCat*/
/*Auteur: Snox5*/
/*Version: Beta/Release*/
/*Licence : CC BY-NC-SA*/
/*Leechez pas ! => Proposez moi des modifs sur le topic du theme sur CCM*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.commentcamarche.net/"),url-prefix(https://secure.commentcamarche.net/) {
    
    :root{
        --topbar-color: #303030;
        --bleu-color: #4177B7;
        --green-color: #37B947;
        --red-color: #e53735;
    }
@font-face {
  font-family: 'Roboto Slab';
  font-style: normal;
  font-weight: 400;
  src: local('Roboto Slab Regular'), local('RobotoSlab-Regular'), url(https://fonts.gstatic.com/s/robotoslab/v6/y7lebkjgREBJK96VQi37Zo4P5ICox8Kq3LLUNMylGO4.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215;
}
@font-face {
  font-family: 'Overpass Mono';
  font-style: normal;
  font-weight: 400;
  src: local('Overpass Mono Regular'), local('OverpassMono-Regular'), url(https://fonts.gstatic.com/s/overpassmono/v2/MarHoIqW2hy_po97b_wS9hEOS6Aq7hJAAidQNR3rgdU.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215;
}
@font-face {
  font-family: 'Roboto';
  font-style: normal;
  font-weight: 400;
  src: local('Roboto'), local('Roboto-Regular'), url(https://fonts.gstatic.com/s/roboto/v15/CWB0XYA8bzo0kSThX0UTuA.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215;
}
/****** Font-Awesome FONT-AWESOME ********/
@font-face {
	font-family: 'FontAwesome';
	src: url('https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.eot?v=4.7.0');
	src: url('https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.eot?#iefix&v=4.7.0') format('embedded-opentype'),url('https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.woff2?v=4.7.0') format('woff2'),url('https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.woff?v=4.7.0') format('woff'),url('https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.ttf?v=4.7.0') format('truetype'),url('https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.svg?v=4.7.0#fontawesomeregular') format('svg');
	font-weight: normal;
	font-style: normal
 }
/**************************/

/* => ENVIE D'ACTIVER LE MODE KIKOO ?
Décommenter les lignes suivantes pour activer le mode "pseudos de Jean Kevin" */
 /*
.member a.ftBlue.ftNone::before {
    content: 'Xx-Dark';
}
.member a.ftBlue.ftNone::after {
    content: '-Officiel-xX';
}
    */
/*animations*/
@keyframes bounce-anim {
    0%   {transform:translateX(0);}
    25%  {color:#fff;transform:translateX(0px);}
    30%  {transform:translateX(-5px);}
    32%  {transform:translateX(3px);}
    34%  {transform:translateX(0px);}
    50%  {transform:translateX(0px);}
    75%  {color:#fff;transform:translateX(0px);}
}
    
/*lien s bleus */
    .ftBlue {
        color:#3C6596;
    }
/*liens couleurs*/
.ftGreen,
.ftGreen a {
    color: var(--green-color) !important;
}
.ftRed, .ftRed a {
    color: var(--red-color) !important;
}
.ftGrey, .ftGrey a {
    color: #838383 !important;
}
.ftBlack, .ftBlack a {
    color: #8f8f8f !important;
}
a {
    color: #ccc;
    text-decoration: none;
}
a:hover, .a_hover {
    color: var(--red-color);
}
/**/
.bgGrey {
    background-color: #313131;
}    
/*DESACTIVATION DES ANIMATIONS MESSAGES*/
.answer .article[style] {
    background: transparent!important;
}
.commentLine[style] {
    background:#313131!important;
} 
.commentLine.bgFocus[style] {
    background:#313131!important;
}
.answer.bgFocus[style] {
    background:rgba(0,0,0,0)!important;
}
div.title[style] {
    background:rgba(0,0,0,.0);
}
.overlayDiv, .overlayDivBlack, .newLoading {
    background-color: rgba(0,0,0,.15) !important;
}
#newanswer_ctn[style] {
    background: transparent !important;
}
    
#best_answer_placeholder div.pad10.bgGreen.ftSize14.marT10.dontPrint {
    background-color: #313131;
    box-shadow: 0 2px 2px rgba(0,0,0,.1);
    border-radius: 3px;
}
    
#signup_ctn.bgAnswer {
    background: none;
}
/*TESTS TOPIC*/

div.questionInfos {
    font-size: 12px;
}
/*BODY*/

body {
    font-family: Open Sans, sans-serif;
    color: #ccc;
    background-color:#262626;
    
    
}
/*LARGEUR DES PAGES*/
header.ccm_header[role="banner"] .layout {
    min-width: 1070px;
    max-width:1250px;
    width:auto;
}
.app--rwd .layout.layout--offcanvas {
    width: auto;
    min-width: 1070px;
    max-width: 1250px;
}
/*BOX SHADOW SUR LA PAGE*/

.app--rwd .layout.layout--offcanvas {
}
.layout_ct {
    background-color:transparent;
}
/* CCM TOPBAR*/
header.ccm_header[role="banner"] .ccm_top_bar li#user a.newMsg span {
    position: absolute;
    top: 8px;
    right: -1px;
    box-shadow: -2px 2px 3px rgba(0,0,0,.4);
    padding: 0 5px;
    background: #E53735;
    border-radius: 3px;
    font-size: 11px;
    font-family: 'Roboto condensed';
    font-weight: 700;
}
header.ccm_header[role="banner"] .ccm_top_bar {
    background-color: #2f2f2f;
}
header.ccm_header[role="banner"] .ccm_top_bar li > a:hover, header.ccm_header[role="banner"] .ccm_top_bar #user:hover .boxButton, header.ccm_header[role="banner"] .ccm_top_bar .ccm_usermenu_language:hover .ccm_usermenu_language__btn,
header.ccm_header[role="banner"] .ccm_top_bar li.selected a {
    background: var(--red-color);
}
/*CCM HEADER*/

header.ccm_header.ccm_header--light[role="banner"] .ccm_middle_bar {
    background:var(--red-color);
}
header.ccm_header.ccm_header--light[role="banner"] nav[role="navigation"] .layout_ct > ul > li.selected a {
    background: rgba(0,0,0,.1);
}
header.ccm_header.ccm_header--light[role="banner"] nav[role="navigation"] .layout_ct > ul > li > a:hover {
    background: rgba(0,0,0,.055);
}
header.ccm_header.ccm_header--light[role="banner"] nav[role="navigation"] .layout_ct > ul > li > a {
    text-shadow: 0 -1px 0 rgba(0, 0, 0, 0.0);
}
/*Sous menu topbar*/
header.ccm_header[role="banner"] {
    color: #fff;
    background-color: #2f2f2f;
    box-shadow: 0 0 1px rgba(0,0,0,.1);
    border-bottom: 1px solid rgba(0,0,0,.2);
}
header.ccm_header[role="banner"] nav[role="navigation"] .layout_ct > ul > li .ccm_submenu {
    background: #2f2f2f;
}
header.ccm_header[role="banner"] nav[role="navigation"] .layout_ct > ul > li .ccm_submenu a, header.ccm_header[role="banner"] nav[role="navigation"] .layout_ct > ul > li .ccm_submenu a:hover, header.ccm_header[role="banner"] nav[role="navigation"] .layout_ct > ul > li .ccm_submenu a:focus {
    color: #ccc;
}
/*boite hover changement de thème*/
header.ccm_header[role="banner"] .ccm_top_bar #user select {
    border:1px solid rgba(0,0,0,.3);
}
/*changement de langue*/
header.ccm_header[role="banner"] .ccm_top_bar .ccm_box_hover {
    padding: 10px;
    border: 1px solid rgba(0,0,0,.3);
    border-width: 1px;
    background: #313131;
    box-shadow: 0 2px 2px rgba(0,0,0,.1);
    border-radius: 3px;
}
header.ccm_header[role="banner"] .ccm_top_bar .ccm_usermenu_language .ccm_box_hover li a {
    color: #ccc;
}
header.ccm_header.ccm_header--light[role="banner"] .ccm_middle_bar .ccm_header_form {
    background:var(--red-color);
}
.ccm_logo_ccm > a > img {
    transition-duration:1s;
}
.ccm_logo_ccm > a > img:hover {
    transform: scale(1.05);
}
/*BREADCRUMB LIKE BS*/
.ccmcss_breadcrumb {
    background-color: var(--red-color);
    padding: 5px 10px 2px 10px;
    border-radius: 20px;
    font-family: 'Open Sans',sans serif;
    font-weight: 700;
    }
div.ccm_breadcrumb > a {
    color:#fff !important;
    }
.ccmcss_breadcrumb div + div::before {
    color: #FFF;
    }
/* STATISTIQUES CHARTE RACCOURCIS*/
  #toolsLinks {
    float: right;
    background-color: var(--red-color);
    padding: 5px 10px 4px 10px;
    border-radius: 20px;
    font-family: 'Open Sans',sans serif;
    font-weight: 700;
}
    #toolsLinks > li > a {
    color: #fff;
}
    .app--rwd #toolsLinks {
    margin-top: 0px;
    }
    
/*formulaire de connexion :*/
form.genForm.newPost.jLoginFrom[style]{
    border:none!important;
}
/*correction marge bizzar*/

#ivgaabtm {
    border-left: 0px solid transparent;
}
/*S'APPLIQUE A TOUS LES BOUTTONS*/
.button.overlayW, .button.overlayB:hover {
    background-image:none!important;;
}
.button {
    border-radius:3px;
    border:none!important;
}
.ccmcss_btn {
    border-radius:3px;
    transition-duration:.3s;
    color: #fff;
    border: 1px solid rgba(0,0,0,.2);
    background-color: #313131;
}
.ccmcss_btn:hover {
    transition-duration: .05s;
    background-color: #2f2f2f;
    color:#fff;
}
.ccmcss_btn:focus {
    color:#ccc;
    background:#2f2f2f;
}
.ccm_tabs .ccm_.htmls--selected .ccmcss_btn, .ccm_tabs .ccm_tabs--selected .ccmcss_btn {
    background-color: #313131;
    border-bottom-color: var(--red-color);
    border-bottom-width: 3px;
}
.ccmcss_btn span.ccm_ico_os {
    filter: invert(1);
    }
/*BOUTTON TOP ET AVANCE*/

#gotop a,
#gonext .goLogo,
#goprev .goLogo {
    border-radius: 0px;
}
/*TOPIC LIST ZONE*/
/*TOPIC LIST TABLE*/
/*Boutons "Répondre" DANS LA TOPIC LIST !*/
a.ccmcss_btn--info::before {
	font-family: 'FontAwesome';
	content:"\f1cd   ";
	font-weight:normal;
}
a.ccmcss_btn--s::before {
	content:''!important;
}
.ccmcss_btn--info {
    border:none;
    font-weight: bold;
    background-color:var(--bleu-color);
}
.ccmcss_btn--info:hover {
    border:none;
    font-weight: bold;
    background-color:#3c6596;
}
/*boutons "répondre" généraux hover FX*/
.ccmcss_btn.ccmcss_btn--info.ccmcss_btn--s {
}
.ccmcss_btn.ccmcss_btn--info.ccmcss_btn--s:hover,
.ccmcss_btn.ccmcss_btn--info.ccmcss_btn--s:focus {
    background-color:var(--bleu-color)!important;
    color:#fff!important;
    
}
/* boutons "répondre" dans la topic list hover FX*/
.tableTemplate table {
    /*border: solid 1px #ddd !important;*/
}
.tableTemplate .noAnswers .answers:hover .ccmcss_btn {
    cursor: pointer;
    background: #4193c9;
}
.tableTemplate tbody td.author a, .tableTemplate tbody td.answers a {
    /*color: #787878;*/
}
/*bordure sujet courrant*/

.tableTemplate tbody tr td.subject {
    border-left: 3px solid transparent;
}
.tableTemplate tbody td.subject a, .tableTemplate tfoot td.subject a {
    color: #ccc;
}
/* sujets supprimés */
tr.delete > td.subject {
    border-left: 3px solid var(--red-color) !important;
}
.tableTemplate tbody tr.delete td.subject a,.tableTemplate tbody tr.delete td span.ftRed,.tableTemplate tbody tr.delete td a.ftBlue{
    color:#777!important;
}
.tableTemplate tbody tr.delete {
    background: #323232;
}
/*hover topic list*/
    .tableTemplate tbody tr:hover td {
        background-color:rgba(0,0,0,.03)!important;
        transition-duration: .2s;
    }
/**/

.tableTemplate tbody td.theme,
.tableTemplate tbody td.author,
.tableTemplate tbody td.answers,
.tableTemplate tfoot td.answers,
.tableTemplate tbody td.nbAnswers {
    font-family: 'Open Sans', sans-serif;
    font-size: 11px;
}
.tableTemplate tbody td {
    border-top: 0px solid #fffdfd;
    padding: 10px;
}
/*nombre*/
.tableTemplate tbody td.nbAnswers a {
    color: #999999;
}
/**/
.container .tableTemplate {
    color: #787878;
    border-collapse: collapse;
    box-shadow: 0px 2px 2px rgba(0, 0, 0, 0.2);
    margin-bottom: 20px;
    border-radius: 0 0 4px 4px;
}
.tableTemplate {
    font-family: 'Open Sans', sans-serif;
}
.tableTemplate .bgColorForumCol {
    background: transparent;
}
.unpair {
    background: transparent;
}
.pair, .even {
    background: transparent;
}
.container .tableTemplate tbody {
    background: #313131;
}
.ccmcss_btn--ico:hover {
    border: 1px solid rgba(0,0,0,.2);
}
#searchOpt {
    color: #bbb;
    padding: 5px;
    background-color:transparent!important;
}
.container .ccm_search_form {
    background-color: transparent;
}
.ccm_search_form form {
    background-color:transparent;
}

/*Boutton Répondre*/

td.answers {
    background-color: transparent !important;
}
td.answers:hover {
    background-color: #4C7996 !important;
}

/*BOUTONS QUAND PREVISUALISATION VALIDATION*/
    /*bouton répondre*/
.ccmwg_newpost input[type="submit"] {
    background: #497BB7;
    border: 1px solid rgba(0,0,0,.1);
    color: #fff;
    font-weight: bold;
}
.ccmwg_newpost input[type="submit"]:hover {
    text-shadow: 0px 0px 2px rgba(0, 0, 0, .3);
    background: #5089ce;
    border: 1px solid rgba(0,0,0,.1);
    font-size: 17px;
    color: #fff!important;
    font-weight: bold;
}
    /*prévisualisation*/
.ccmwg_newpost input[type="button"] {
    border: 1px solid rgba(0,0,0,.1);
    background: #A285E9;
}
.ccmwg_newpost input[type="button"]:hover {
    text-shadow: 0px 0px 2px rgba(0, 0, 0, .3);
    border: 1px solid rgba(0,0,0,.1);
    background: #ad8ff7;
    color:#fff!important;
}

/*BOUTON LISTE DES SUJETS*/

a.ccmcss_btn.ccmcss_btn--warning {
    background-image: none;
    text-shadow: 0px 0px 3px rgba(0, 0, 0, .3);
    font-weight: bold;
    border: none;
}
    
.tableTemplate tbody tr.noAnswers td.subject {
    border-left-color: var(--bleu-color);
}
/*HEAD TABLE TOPIC*/

.tableTemplate thead tr th.subject {
    border-left: 0px solid transparent;
}
.tableTemplate .noAnswers .subject a {
    color: var(--bleu-color);
}
.tableTemplate thead tr {
    background-color: #2F2F2F;
    color: #ccc;
    border-top: 3px solid var(--red-color);
}
/*s'applique au Head des colonnes "Forum" "Date" Et "Réponses"*/

.tableTemplate thead th {
    border: none;
    text-align: center;
}
/*Box Shadow derriere la topic list*/

.colMiddle .container {
    border: 0px solid transparent;
    background: transparent;
}
.ccmcss_form .customSelect .customSelectInner::before {
    content: "";
    border-left: 1px solid #272727;
    background-color: #313131;
    border-radius: 0 3px 3px 0;
}
.ccmcss_form .customSelect .customSelectInner::after {
    border-color: #ccc transparent transparent transparent;
}
div.gutter > div.grid_col.w50 > input, div.gutter > div.grid_col.w50 > span.customSelect {
    color:#ccc;
    border: 1px solid rgba(0,0,0,.3);
    background-color: #313131;
    box-shadow:0 0px 1px rgba(0,0,0,.1);
    border-radius:3px;
    font-family: 'Open Sans', sans serif;
    font-size: 13px;
}
.container .ccm_search_form {
    padding: 0px;
}
fieldset {
    margin-top: 20px;
    width: 100%;
}
.ccmwg_newpost .select {
    background-color: transparent;
}
.ccmwg_newpost select {
    background-color: #313131;
    border: 1px solid rgba(0,0,0,.3);
    border-radius: 3px;
    color: #ccc;
}
.ccmwg_newpost .textarea textarea, .ccmwg_newpost input[type="text"] {
    color: #ccc;
}
/*STATS DE RECHERCHE*/

.tabMenu {
    border: none;
}
.tabMenu span {
    height: 33px;
    margin-right: 0;
    border: none;
    top: 0px;
}
.tabMenu span > a:hover {
    background-color:transparent;
    color:#fff;
    /*border-left:0px solid #313131;*/
    border:none;
    border-top:3px solid rgba(229, 55, 53,.5);
    text-shadow: 0 0 5px rgba(0,0,0,.5);
    }
.tabMenu .selected {
    border-bottom: none;
    border-top: none;
    line-height:0;
}
.tabMenu .selected a {
    padding-top: 5px;
    line-height:24px;
    color: var(--red-color) !important;
    background-color:transparent;
    border-top:3px solid var(--red-color);
}
.tabMenu .selected a:hover {
    border-top:3px solid var(--red-color)!important;
    color:#fff;
}
.tabMenu > span > a {
    background-color:transparent;
    color:#ccc;
    height: 34.5px;
    font-weight: bold;
    color: #fdfdfd!important;
    font-size: 12px;
    text-decoration:none;
    border-top:3px solid #444;
    transition-duration: .5s;
    
    /*border-top: 1px solid rgba(0, 0, 0, 0);
    border-bottom: 1px solid rgba(0, 0, 0, 0);
    border-left: 1px solid rgba(0, 0, 0, 0.2);*/
    }
.tabMenu > span:first-child > a {
    /*border-radius: 0px 0 0 4px;
    border-left: 1px solid rgba(0, 0, 0, 0.2);
    border-top: 1px solid rgba(0, 0, 0, 0);
    border-bottom: 1px solid rgba(0, 0, 0, 0);*/
    }
.tabMenu > span:nth-last-child(-n+2) > a {
    /*border-radius:0 0px 4px 0;
    border-right: 1px solid rgba(0, 0, 0, 0.2);
    border-top: 1px solid rgba(0, 0, 0, 0);
    border-bottom: 1px solid rgba(0, 0, 0, 0);*/
    }
/*Pagination sous la topic list*/

.pagination2 {
    padding:0;
    border-top: none;
    background: transparent;
}
.pagination2 .pageNb a, .pagination2 .ico a {
    padding: 4px 6px;
    background: #313131;
    border:none;
    text-decoration: none;
    border-radius: 2px;
    color: #4A4A4A;
}
.pagination2 .pageNb a.selected {
    border:none;
    background: var(--red-color);
    font-weight: bold;
    color: #fff;
    }
.pagination2 .pageNb a:hover, .pagination2 .pageNb a.selected:hover{
    transition-duration:0s;
    border:none!important;
    background: var(--red-color);
    font-weight: bold;
    color: #fff;
}
    /*padding correction boutton avant / après*/
.pagination2 .next a {
    padding-right:15px!important;
}
.pagination2 .prev a {
    padding-left:15px!important;
}
.pagination2 .prev a::before {
		font-family:'FontAwesome';
	content:'\f060';
	font-weight:normal;
    margin-right:5px;
}
.pagination2 .next a::after {
		font-family:'FontAwesome';
	content:'\f061';
	font-weight:normal;
    margin-left:5px;
}
.pagination2 .pageNb a {
    transition-duration:.25s;
    padding: 5px 7px;
    background: #313131;
    color:#ccc;
}
.pagination2 .icoPrevious {
    display: none;
}
.pagination2 .ico {
    width: 0px;
    display:none;
}
/*Button Next et Previous*/

.bottomLink {
    padding-left: 0px;
}
.grid_line.gutter {
    margin-left: 0px;
}
.pagination2 .prev a,
.pagination2 .next a {
    font-weight: bold;
    text-decoration: none !important;
    background-color:#313131;
    border:1px solid rgba(0,0,0,.3);
    color:#fcfcfc;
    text-shadow:none;
    padding: 4px 20px;
    border-radius: 2px;
    transition-duration: .1s;
}
.pagination2 .prev a:hover,
.pagination2 .next a:hover {
    background-color: #313131;
    border:1px solid rgba(0,0,0,.3);
    color:#fff;
    text-shadow:none;
    transition-duration: .1s;
}
/*COULEUR TITRE SUJETS RESOLUES*/
.grid_left, .left {
    color: #bbb;
}
.tableTemplate tbody tr.resolved td.subject,
.tableTemplate tfoot tr.resolved td.subject {
    border-left-color: var(--green-color);
}
.tableTemplate .resolved .subject a {
    color: var(--green-color);
}
    tr.resolved td span
/*SUBMIT FORM FORUM*/

.container .ccm_search_form {
    margin: 0px 0 0;
    background: transparent;
}
/*boutton de recherche*/

.ccmcss_btn--primary {
    background-color: var(--red-color);
    border: none;
    border-width:0px;
    border-radius:3px;
}
