    color: #adadad;
}
/*CHANGER IMAGE PROFILE*/
#change_picture_btn{
    background-color: rgba(0, 0, 0, .7);
    border: none;
}
/*PROFILE TEXT COLOR*/

.ccm_page--profile .accountTab .ctCol h2 {
    color: #ccc;
    border-bottom: 1px solid rgba(255,255,255,.1);
}
/*PROFILE DETAIL TEXT COLOR*/

.ccm_page--profile .accountTab .infosTable th {
    color: #ccc;
}
.ccm_page--profile .accountTab .infosTable th::after {
    content:' :';
}
/*PROFILE INFOBOX TOOLTIPS STYLE*/

.tooltips {
    display: none;
    position: absolute;
    padding: 4px 8px;
    border: 0px solid rgba(0,0,0,.3);
    border-right: 0px solid rgba(0,0,0,.3);
    border-bottom: 0px solid rgba(0,0,0,.3);
    background-color: #353535!important;
    z-index: 100;
    font-family: 'Open Sans', sans-serif!important;
    font-size: 13px;
    color: #ccc;
    font-weight: normal;
    text-align: left;
    line-height: 18px;
    border-radius: 1px;
    box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.2);
}
.tooltips::after {
    border-style: solid;
    border-width: 8px;
    border-color: transparent;
    border-top-color: #353535;
    content: '';
    height: 0;
    left: 50%;
    margin-left: -177px;
    pointer-events: none;
    position: absolute;
    top: 100%;
    width: 0;
}

.tooltips .arrowDown {
    background: url(http://www.commentcamarche.net/_skin/_common/img/ico_sprite.gif?201310081700) -7px -140px;
    filter: brightness(.4);
}
span.arrowDown {
    background: url(http://www.commentcamarche.net/_skin/_common/img/ico_sprite.gif?201310081700) -7px -140px;
    filter: brightness(.4);
}
/************/
    
.ccm_page--profile .menuLeft ul li a span.arrowUp, .ccm_page--profile .menuLeft ul li a span.arrowDown {
    display:none;
}
.ccm_page--profile .menuLeft > ul > li:first-child, .ccm_page--profile .menuLeft > div > ul > li:first-child {
    border-top: 0px solid transparent;
}
.ccm_page--profile .menuLeft {
    border: none;
    border-radius: 0 0 2px 2px;
    border-top:3px solid var(--red-color);
    font-size: 12px;
    box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.15);
}
.ccm_page--profile .menuLeft ul {
    border: 0px solid transparent;
    background-color:#313131;
}
.ccm_page--profile .menuLeft ul li.selected {
    background: #313131;
}
.ccm_page--profile .menuLeft ul li.selected a {
    border-left:3px solid #ccc;
    color:#ccc;
}
.ccm_page--profile .menuLeft ul li a {
    color:#ccc;
}
.ccm_page--profile .menuLeft ul li {
    background: #313131;
    border: none;
    border-right: none;
}
.ccm_page--profile .menuLeft ul li a:hover {
    background: #2d2d2d;
    color: #ccc;
}
.ccm_page--profile .menuLeft ul li a {
    padding: 6px 10px 6px 15px;
    border-left: 0px solid transparent;
    border-bottom: 0px solid transparent;
}
.ccm_page--profile .menuLeft ul li.selected {
    border-top: 0px solid transparent;
}
/*DERNIERES QUESTIONS, TOPIC LIST DU PROFILE*/

.ccm_page--profile .tableTemplate {
    border: 0px solid transparent;
    background-color:#313131;
    color: #787878;
    box-shadow: 0px 2px 2px rgba(0, 0, 0, 0.2);
}
#topic_question .actionsMenu a,
.answer .actionsMenu a {
    color: #48738f;
    text-decoration: underline;
}
/*Couleur des ses propres commentaires sur les sujets*/

#topic_question .bgFocus,
.answer .bgFocus,
.commentBloc .commentLine.bgFocus {
    border-left:3px solid #cea127;
    background:#313131;
}
.answer .bgFocus {
    border-radius: 3px;
    border-top: none;}
/*Réponses commentaires*/

.commentBloc .commentLine {
    box-shadow: 0px 0px 2px rgba(0, 0, 0, 0.2);
    margin-top: 10px;
    background: #313131;
    border-top: none;
    border-left: none;
    border-radius: 2px;
}
/*Vote Box*/

.voting {
    box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.15);
}
div .ftUpp.ftAlignC.ftSize11.ftGrey2 {
    display: none;
}
.asideInfos .voting .votes {
    border-radius: 1px 1px 0 0;
    border: none;
    background-color: #313131;
    color: #696969;
}
.asideInfos .notation {
    border-radius: 0 0 1px 1px;
    background: #313131!important;
    border: none!important;
}
.asideInfos .notation a.more {
    border-left: none;
}
.asideInfos .notation a.less {
    border-left:1px solid transparent;
}
/*Ajouter une réponse à une réponse*/

.addComment {
    padding: 0px;
    background: none!important;
    margin-bottom: 10px;
}
.formAddComment .comment,
.formAddComment .small {
    border: 1px solid rgba(0,0,0,.3);
    border-radius:3px;
    /*box-shadow: 0px 0px 2px rgba(0, 0, 0, 0.2);*/
    width: 100%;
    padding: 8px 10px;
    backface-visibility: hidden;
    background-color:#333;
}
.formAddComment .comment:hover {
    animation-name: hvr-pulse-grow;
    animation-duration: 0.5s;
    animation-timing-function: linear;
    animation-iteration-count: infinite;
    animation-direction: alternate;
}
/*Couleur de ses propres messages*/

.answer.bgFocus {
