    border-radius: 5px;
    margin-top: 10px;
}
.codeSnippeted .line-numbers-rows > span:before {
	color:#ccc;
    background-color:#353535;
}
.codeSnippeted.line-numbers .line-numbers-rows {
	left:0px;
    height:100%;
	width:25px;
	letter-spacing:-0.5px;
	border-right:1px solid rgba(0,0,0,.1);
}
