css:
@-moz-document domain("openclassrooms.com") {

* {
    color: #e6e6e6;
	background-color: #151515;
}

p {
	color: #e6e6e6 !important;   
}

.course-header, .course-header__details-container{
    background-color: #050505;
}

.side-paginate__title{
    color: #e6e6e6 !important;
    background-color: #127fc4;
}

.side-paginate__previous, .side-paginate__next{
	background-color: #1069a1;
}

.side-paginate__previous:hover, .side-paginate__next:hover{
	background-color: #196493 !important;
}

.side-paginate__separator{
    background-color: #0e5582 !important;
}

.col-nav__block--summary-list, .col-nav__chapters, .col-nav__chapters.li, .col-nav__chapterTitle, li.selected, .tocExercise, .tocExercise__list, .tocExercise__link, .tocExercise__text, .tocExercise__item  {
     background-color: #222222 !important;
}

.col-nav__block.col-nav__block--button{
    margin-top: 2px;
}

a.button{
    
    background-color: #636363;
    color: #e6e6e6 !important;
}

h2.part-title{
    border-color: #009cff;
}

h1, .course-header__topPart{
    background-color: #050505; 
}

.pre-main-part {
 	background-color: #050505; 
}

h1,h2,h3,h4,h5,h6,th{
    color: #009cff !important;
}

h3, .foldable__button secondTitle js-foldable-button{
    border-color: #009cff !important;
}

a{
	color: #009cff !important;
}

header.course-header::after {
    background-color: black;
}

input{
    border-color: #232323 !important;
    color: e6e6e6;
	background-color : #232323 !important;    
}

a.button.course-pagination__btn.course-pagination__btn--previous, a.button.course-pagination__btn.course-pagination__btn--previous i, a.button.course-pagination__btn.course-pagination__btn--previous span, a.button.course-pagination__btn.course-pagination__btn--next, a.button.course-pagination__btn.course-pagination__btn--next i, a.button.course-pagination__btn.course-pagination__btn--next span{
    border: 0px;
 	background-color: #050505 !important;   
}

i, .topSearchBox__icon icon-search{
    background-color: #232323;
}

footer.alt-section{
	background-color: #050505;   
}

footer.footer{
    background-color: #151515;   
}
}
