css:
@-moz-document domain("www.leagueplusplus.net") {
#elLogo img {
    content: url("https://s.put.re/Kaf7oyN.png");
    filter: drop-shadow(8px 8px 10px rgba(0, 0, 0, 0.41));
}
#ipsLayout_header header {
    padding: 5px 0 130px 0;
}
#ipsLayout_header header .ipsLayout_container {
    padding-top: 10px;
}
body {
    background-image: url("https://s.put.re/Pi9PaNa.png");
    background-size: cover;
    background-attachment: fixed;
    background-repeat: no-repeat;
}
#ipsLayout_sidebar {
    min-width: 250px;
    max-width: 315px;
    padding: 10px 0 15px 0;
    overflow: hidden;
    border-radius: 0;
}
html[dir="ltr"] #elUserNav {
    right: 20px;
}
#elUserNav {
    background-color: rgba(0, 0, 0, 0.31);
    padding-left: 10px;
    padding-right: 10px;
    padding-bottom: 2px;
}
.ipsNavBar_primary,
.ipsTabs,
.ipsType_sectionTitle,
.ipsWidget.ipsWidget_vertical .ipsWidget_title,
.ipsItemStatus.ipsItemStatus_large {
    background: #463f34!important;
    border-radius: 3px 3px 0px 0px;
}
.ipsNavBar_primary.ipsNavBar_noSubBars > ul > li.ipsNavBar_active > a {
    background: #f5f5f5
}
.ipsApp .ipsButton_primary {
    background: #463f34;
    color: #ffffff;
}
#elChatbox {
    margin-bottom: 0px !important;
    border-radius: 3px 3px 0px 0px;

}
.ipsToolList.ipsToolList_horizontal > li.ipsToolList_primaryAction .ipsButton:not( .ipsButton_link ) {
    min-width: 200px;
    background: #463f34;
}
/* client area */

#header-navbar {
    background-color: #463f34;
    color: #fff;
    max-width: 1000px;
    border-radius: 3px 3px 0px 0px;
}
#main-content {
    background-color: #F9F9F9;
    max-width: 1000px;
    /* margin-top: 25px; */
    position: relative;
}
.w3-theme-header {
    height: 60px;
    color: #fff;
    background-color: #463f34;
    box-shadow: 0 0 5px rgba(0, 0, 0, 0.41);
}
.w3-theme {
    color: #fff !important;
    background-color: #463f34 !important;
}
.w3-theme-body {
    color: #000;
    /* background-color: #f0f0f0; */
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
    background-image: url("https://s.put.re/Pi9PaNa.png");
    background-repeat: repeat-x;
    background-position: center top;
    background-color: #080808;
}
#under-header-bar-logo {
    max-width: 1366px;
    margin-top: 60px;
    content: url("https://s.put.re/C7bVxgG.png");
    text-align: center;
    filter: drop-shadow(8px 8px 10px rgba(0, 0, 0, 0.41));
}
}
