css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("madman.com.au") {

#contentArea, .innerBreadSlice, .innerPageCrumb,
.standardGroup, .listView, .detailedView,
.bookshelfView, .browse-header,
#mdd_anime, #mdd_manga, #mdd_films, #mdd_adultswim,
#mdd_kids, #mdd_television, #mdd_sportslifestyle,
#mdd_natgeo, input, .site-wrapper
{
background: #000000 !important;
}
.card-container-wrapper
{
background: #111111 !important;
}
.grid-row, .column-navigation, .content-chunk, #search-results,
.inner-chunk-header, .inner-chunk-body, .inner-chunk-footer,
.card .card-content, .form-item.itemSelector, .search-drawer,
.search-results-list li a
{
background-color: #222222 !important;
}
.btn-grey-lightest, .btn-grey-to-blue
{
background-color: #555555 !important;
}
.success, .message-area
{
background-color: #006600 !important;
}
.cart-item, 
.column-navigation .selected a, .column-navigation a:hover
{
background-color: #0B70C1 !important;
}
.label
{
background-color: #FF0000 !important;
}
.innerBreadSlice a
{
background: transparent !important;
}
.inner-chunk-header
{
border-top: 1px #C72738 solid !important;
}
.inner-chunk-header, .inner-chunk-body, .inner-chunk-footer
{
border-left: 1px #C72738 solid !important;
}
.grid-row, .inner-chunk-footer
{
border-right: 1px #C72738 solid !important;
}
.grid-row
{
border-bottom: 1px #C72738 solid !important;
}
#contentArea, .innerBreadSlice, .innerPageCrumb,
.standardGroup, .card .card-content,
#mdd_anime, #mdd_manga, #mdd_films, #mdd_adultswim,
#mdd_kids, #mdd_television, #mdd_sportslifestyle,
#mdd_natgeo, input, .column-navigation,
.form-item .form-input, .form-item.itemSelector
{
border: 1px #C72738 solid !important;
}
.card .card-content
{
box-shadow: none !important;
}
.productListItem .informationColumn hr,
.standardGroup hr
{
border-top: 1px #C72738 solid !important;
}
a, p, small, input, .sub-label,
h1, h2, h3, h4, h5, h6, label, .label,
.radio-card-info, .list-item-title,
.cart-item-format, .cart-items-quantity-info,
.list-item-detail
{
color: #FFFFFF !important;
}
.price, .product-price
{
color: #00FF00 !important;
}
}
