css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("https://www.readthekanji.com/user/study") {
  
  #study #main
  {min-width: 600px !important; min-height: 20px !important;}

  * {text-shadow: none !important; border-radius: 0px !important; box-shadow: 0px 0px 0px 0px !important;}
  
  
  #study #main #container #question #jp
  {color: #00BFFF !important; font-size: 60px; font-family: "Meiryo", "MS Mincho";}
  
  #study #main #container #action form input#answer
    {color: #00BFFF !important; font-size: 40px; font-family: "Meiryo", "MS Mincho";}
  
  #study #main #container #action form
  {padding: 0px; border: 0px;}
  
      
#study #main #container #action #icons
  {display: none;}
  
  #study #main #container #question #en
  {opacity: 1; padding: 0px; color: #D7B52D !important; font-size: 40px;}
  
  #study #bar #history #log .item .details .answers .response
  {border-top: 0px !important;}
  
  #study #bar #history #log .item:first-child .details .meaning, #study #bar #history #log .item .details .answers .answer
  {font-size: 30px !important;}
  
  #study #bar #history #log .item .details .answers .answer
  {color: #D7B52D !important;}
  
  #study #main #container #question
  {min-height: 10px; padding-top: 50px;}
  
  #study #main #container #action form #check-answer
  {background: none; color: #D7B52D !important;}
  
  #study #bar #history #log .item:first-child .details .meaning
  {color: #00BFFF !important;}
  
  #session-bar
  {bottom: 45px;}
  
  header.study #navbar
  {top: 0px;}
  
  ul#navbar.right
  {float: left !important; background-image: none !important; border: 0px !important;}
  
  #study #main #container #action form input#answer
  {margin-right: 0px;}
  
  header.study #navbar li a, header #navbar li:last-child a
  {color: #D7B52D !important; }
}
