css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.internet-radio.com") {

/* remove sidebar */
    
.col-md-5 {
    height: 0px;
    width: 0px;
    overflow: hidden;
    display: none;
}
    
.col-md-7 {
    width: 100%;
}

/* navigation bar colors */    
    
.navbar-default {
    background-image: linear-gradient(to bottom, #555 0px, #000 100%);
    box-shadow: 0px 1px 0px rgba(255, 255, 255, 0.15) inset, 0px 1px 5px rgba(0, 0, 0, 0.075);
    background-color: #555;
    border-color: #000;
}
    
.btn-default:hover, .btn-default:focus {
    background-position: 0px -15px;
    background-color: #000;
    color: #333;
    border-color: #000;
    background-color: #000;
}    

.btn-default {
    color: #888;
    text-shadow: 0px 1px 0px #AAA;
    border-color: #333;
    background-image: linear-gradient(to bottom, #555 0px, #111 100%);
    box-shadow: 0px 1px 0px rgba(255, 255, 255, 0.15) inset, 0px 1px 1px rgba(0, 0, 0, 0.075);
    background-color: #000;
}
    
.navbar-default .navbar-nav > .active > a, .navbar-default .navbar-nav > .active > a:hover, .navbar-default .navbar-nav > .active > a:focus {
    color: #888;
    background-color: #555;
    background-image: linear-gradient(to bottom, #644 0px, #000 100%);
    box-shadow: 0px 1px 0px rgba(255, 255, 255, 0.15) inset, 0px 1px 5px rgba(0, 0, 0, 0.075);
}
.navbar-default .navbar-nav > .open > a, .navbar-default .navbar-nav > .open > a:hover, .navbar-default .navbar-nav > .open > a:focus {
    color: #888;
    background-color: #444;
    background-image: linear-gradient(to bottom, #111 0px, #444 100%);
    box-shadow: 0px 3px 9px rgba(0, 0, 0, 0.075) inset;
}

    
/* menu colors */    
      
.dropdown-menu {
    background-color: #000;
    border: 1px solid rgba(0, 0, 0, 0.15);
    box-shadow: 0px 6px 12px rgba(0, 0, 0, 0.176);
}
    
.dropdown-menu > li > a {
    color: #555;
}       

.dropdown-menu .divider {
    background-color: #222;    
}
    
.dropdown-menu > li > a:hover, .dropdown-menu > li > a:focus {
    background-color: #833;
    color: #999;
    background-image: none;
}    

    
/* breadcrumbs colors */    
    
    
.pagination > .disabled > span, .pagination > .disabled > span:hover, .pagination > .disabled > span:focus, .pagination > .disabled > a, .pagination > .disabled > a:hover, .pagination > .disabled > a:focus {
    color: #555;
    background-color: #222;
    border-color: #151515;
}

.pagination > .active > a, .pagination > .active > span, .pagination > .active > a:hover, .pagination > .active > span:hover, .pagination > .active > a:focus, .pagination > .active > span:focus {
    border-color: #151515;
}    
    
.pagination > li > a, .pagination > li > span {
    color: #888;
    background-color: #111;
    border: 1px solid #151515;
}
    
.pagination > li > a:hover, .pagination > li > span:hover, .pagination > li > a:focus, .pagination > li > span:focus {
    background-color: #222;
    border-color: #222;
}    

/* main page */        
.panel-default > .panel-heading {
    background-image: linear-gradient(to bottom, #555 0px, #111 100%);
    background-repeat: repeat-x;
    color: #888;
    background-color: #111;
    border-color: #333;
    border-bottom: 0px;
}
    
.panel-body .btn-default {
    color: #888;
    text-shadow: none;
    border: 0px;
    background-image: none;
    box-shadow: 0px 1px 0px rgba(255, 255, 255, 0.15) inset, 0px 1px 1px rgba(0, 0, 0, 0.075);
    background-color: #222;
}    
    
.panel-body .btn-default:hover, .btn-default:focus {
    background-color: #533;
}    

.panel {
    background-color: #111;
    border: 1px solid #333;
    box-shadow: 0px 1px 1px rgba(0, 0, 0, 0.05);
}
    
.jumbotron {
    color: inherit;
    background-color: #222;
}    

.list-group-item {
    background-color: #222;
    border: 1px solid #333;
}
    
/* other colors */      
    
body {
    background-color: #111;
    color: #888;
}  

footer {
    background-color: #000;
}
    
.form-control {
    color: #888;
    background-color: #151515;
    border: 1px solid #333;
    box-shadow: 0px 1px 1px rgba(0, 0, 0, 0.075) inset;
}    
    
.table-striped > tbody > tr:nth-child(2n+1) {
    background-color: #272727;
}    

.table > thead > tr > th, .table > tbody > tr > th, .table > tfoot > tr > th, .table > thead > tr > td, .table > tbody > tr > td, .table > tfoot > tr > td {
    border-top: 1px solid #242424;
}    
    
}
