css:
@-moz-document domain("formation.u-psud.fr"), domain("dokeos.u-psud.fr") {
body {
    background-color: #000;
    color: white;
}
a:link {
    color: white;
    font-weight: bolder;
}
#header1 {
    font-size: 12px;
    padding: 4px;
    background-color: #000000;
    color: #fff;
    border-bottom: 1px solid white;
}
#header2 {
    position: relative;
    font-size: 1.0em;
    font-weight: 100;
    padding: 0.2em 0.5em;
    padding-bottom: 0.2em;
    background: #292929;
    color: white;
    border-bottom: 1px solid white;
}
#header3 {
    font-size: 1em;
    padding: 1.0em 0.5em 1.0em 0.5em;
    background: #000000;
    color: #000;
    border-bottom: 1px solid white;
    height: auto;
    margin-bottom: 1px;
}
#header3 #current A {
    BACKGROUND: #000000;
    COLOR: white;
    background-color: rgba(20, 20, 20, 1);
}
#header3 LI {
    PADDING-RIGHT: 0px;
    PADDING-LEFT: 0px;
    BACKGROUND: #000;
    FLOAT: left;
    PADDING-BOTTOM: 0px;
    MARGIN: 10px;
    PADDING-TOP: 0px;
    border: 0;
    border-bottom: 1px solid #000000;
}
#header3 A {
    PADDING-RIGHT: 15px;
    DISPLAY: block;
    PADDING-LEFT: 6px;
    FONT-WEIGHT: bold;
    BACKGROUND: #000;
    PADDING-BOTTOM: 5px;
    COLOR: rgb(255, 255, 255);
    PADDING-TOP: 5px;
    border: 0;
}
#header3 UL {
    PADDING-RIGHT: 0px;
    PADDING-LEFT: 0px;
    PADDING-BOTTOM: 1px;
    MARGIN: 0px;
    LIST-STYLE-TYPE: none;
    display: flex;
    color: #0a0a0a;
    background-color: #171717;
    border: 1px solid white;
}
#header3 #current {
    border-bottom: 0px solid #000;
    color: black;
}
#header4 {
    background-color: #000;
    color: #000;
    padding: 4px;
    padding-left: 21px;
    margin-bottom: 2px;
    float: left;
    clear: right;
}
table {
    display: table;
    border-collapse: separate;
    border-spacing: 2px;
    border-color: white;
    background-color: black;
}
td {
    border: solid 1px white !important;
    padding: 5px;
    background-color: #171717 !important;
}
th {
    border: solid 1px #ffffff;
    padding: 5px;
    background-color: #000000;
}
#main {
    position: relative;
    margin: 10px auto;
    margin-top: 10px;
    width: 98%;
    padding: 10px 10px 10px 10px;
    background-color: #101010;
    min-height: 320px;
    border: 1px solid white;
}
#footer {
    height: 1.5em;
    border-top: 1px solid white;
    background-color: #292929;
    font-size: 12px;
    border-bottom: 1px solid white;
}
#header3 #current {
    border-bottom: 1px solid #000;
    color: black;
}
a:visited {
    text-decoration: none;
    font-weight: bold;
    color: white;
}
#header3 a {
    PADDING-RIGHT: 15px;
    DISPLAY: block;
    PADDING-LEFT: 6px;
    FONT-WEIGHT: bold;
    BACKGROUND: #000;
    PADDING-BOTTOM: 0px;
    COLOR: rgb(255, 255, 255);
    PADDING-TOP: 5px;
    TEXT-DECORATION: underline;
    border: 0;
}
.Authoringview,
.courseadminview,
.platformadminview {
    position: relative;
    width: 80%;
    margin: 25px auto 10px;
    padding: 10px;
    border: 1px solid white;
}
#header4 a {
    color: white;
}
.actions {
    background: #000000;
    border: 1px solid white;
    height: 35px;
    padding: 3px;
    margin-bottom: 4px;
    vertical-align: middle;
    margin-top: 10px;
}
select,
input[type=checkbox],
input[type=radio],
input[type=button],
input[type=submit] {
    cursor: default;
    font-size: 14px;
}
select,
textarea {
    color: #fff;
    border: 1px solid #fff;
    background-color: #2b2b2b;
}
.data_table th {
    padding-right: 20px;
    border: 1px solid white;
    background-color: #000000;
}
.maincontent {
    float: left;
    width: 78%;
    padding: 0px;
    background-color: #101010;
}
.menu {
    float: right;
    width: 20%;
    padding: 0 0 6px 0;
    background-color: #000000;
    border: 1px solid white;
}
.menusection {
    width: auto;
    margin: 24px 6px 0 6px;
    padding-left: 10px;
    border: 1px solid white;
}
.menusectioncaption {
    position: relative;
    top: -9px;
    background-color: #000000;
    font-size: 12px;
    padding: 0 8px 0 4px;
}
.data_table th {
    padding-right: 20px;
    border: 1px solid white;
    background-color: #000000;
}
.data_table td {
    padding: 5px;
    vertical-align: middle;
    border-bottom: 1px solid #fb0303;
    border-right: 1px dotted white;
    border-left: 1px dotted #fd0000;
}
p,
blockquote,
ol,
ul {
    font-size: 12px;
    color: #fff4f4;
}
fieldset {
    border: solid 0px white !important;
    padding: 5px;
    background-color: #171717 !important;
}
}
