css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.dobreprogramy.pl") {

  html {
    background: #efefef url("https://static.dpcdn.pl/2017_04_06/assets/img/skin_green/page-background.png") repeat-y fixed center center;
  }
  
  body {
    background: url("http://gowain.pl/pliki/dp-background.png") no-repeat top center;
  }
  
  a {
    color: #88ae04;
  }
  
  .container.posts {
    background: #efefef url("https://static.dpcdn.pl/2017_04_06/assets/img/skin_green/page-background.png") repeat-y fixed center center;
    box-shadow: 0px 0px 10px #cccccc;
    padding: 10px;
  }

  .nav-pills > li.active > a, .nav-pills > li > a.active {
    background-color: #88ae04;
  }
  
  .nav-pills > li > a {
    color: #111;
  }
  
  .nav-pills > li > a:hover {
    background-color: #668300;
    color: #ffffff;
  }
  
  
  .btn-primary {
    background-color: #88ae04;
  }

  .topic-list, .category-list {
    background: #efefef url("https://static.dpcdn.pl/2017_04_06/assets/img/skin_green/page-background.png") repeat-y fixed center center;
    box-shadow: 0px 0px 5px #cccccc;
  }
  
  .category-list tbody tr:first-of-type, .topic-list > tbody > tr:first-of-type {
    border-top-color: #cccccc;
  }
  
  .category-list td, .category-list th {
    color: #444444;
  }
  
  .category-list tbody tr, .topic-list > tbody > tr {
    border-bottom: 1px solid #cccccc;
  }
  
  .badge-wrapper.bullet span.badge-category {
    color: #111111!important;
  }
  
  .category-list tbody .category h3 a[href], .topic-list a.title {
    color: #000000;
  }
  
  .list-controls .category-breadcrumb li.bullet > .badge-category {
    background-color: #88ae04!important;
    color: #ffffff!important;
  }
  
  .btn {
    background-color: #88ae04;
    color: #ffffff;
  }
  
  .btn[href] {
    color: #ffffff;
  }
  
  .more-topics td .btn {
    margin-right: 5px;
  }
  
  .category-dropdown-menu {
    max-height: 350px;
    min-width: 169px;
    padding: 5px;
  }
  
  .list-controls .category-dropdown-menu .home, #reply-control a {
    color: #88ae04;
  }
  
  img.avatar {
    border-radius: 0%;
    padding: 1px;
    border: 1px solid #dddddd;
  }
  
  html {
    color: #111;
    direction: ltr;
    font-family: Open Sans;
    font-size: 14px;
    line-height: 21px;
  }
  .category-list tbody .category h3 {
    font-weight: 500;
    font-size: 1.4em;
  }
  
  .categories-and-latest .topic-list-latest {
    margin-left: 2%;
  }
  
  .categories-and-latest .category-list, .categories-and-latest .topic-list-latest {
    width: 49%;
  }
  
  .badge-wrapper.bullet {
    line-height: inherit;
  }
  
  .topic-list .posters a:first-child .avatar.latest:not(.single) {
    border: 2px solid #88ae04;
    box-shadow: 0 0 3px 1px #88ae04;
    left: -2px;
    position: relative;
    top: -2px;
}
  
  .nav-link-container a:hover {
    color: #88ae04;
  }
  
  .menu-panel, .list-controls .category-dropdown-menu {
    border: 1px solid #dddddd;
    box-shadow: 0px 2px 4px #666666;
    background: #efefef url("https://static.dpcdn.pl/2017_04_06/assets/img/skin_green/page-background.png") repeat-y fixed center center;
  }
  
  a, a.latest-topics-link, a.groups-link, a.top-topics-link, a.categories-link, a.about-link, a.faq-link {
    color: #88ae04;
  }
  
  hr {
    border-color: #444444;
  }
  
  .menu-links.columned li a:hover {
    background-color: #111111!important;
  }
  
  div.topic-body {
    width: 870px;
    background: none;
    color: #222222;
  }
  
  #topic-title h1 a {
    color: #222222;
  }
  
  nav.post-controls a, nav.post-controls button {
    color: #444444;
  }
  
  .names span a {
    color: #88ae04; 
  }
  
  aside.onebox {
    border: 5px solid #88ae04;
    font-size: 1em;
    padding: 12px 25px 12px 12px;
  }
  
  .timeline-container .topic-timeline .timeline-handle {
    background-color: #88ae04;
  }
  
  .timeline-container {
    margin-left: 950px;
  }
  
  .timeline-container .topic-timeline .timeline-scrollarea {
    border-left: 1px solid #bbbbbb!important;
  }
  
  .timeline-container .topic-timeline .timeline-replies {
    color: #88ae04;
  }
  
  .fa.tracking, .fa.watching {
    color: #ffffff;
  }
  
  .topic-map {
    border: none;
    background: none;
  }
  
  .topic-map section {
    border-top: 1px solid #444444;
  }
  
  .topic-map .map .number, .topic-map .map i {
    color: #88ae04;
  }
  
  aside.onebox .onebox-body a[href], .cooked a, .d-editor-preview a {
    color: #88ae04;
    font-weight: 600;
  }
  
  .topic-map .buttons .btn {
    background-color: #88ae04;
    border: none;
    color: #ffffff;
  }
  
  blockquote, p > code, li > code, pre > code, pre code {
    background-color: #e0e0e0;
    border-left: 5px solid #88ae04;
    color: #222222;
  }
  
  #topic-footer-buttons p {
    color: #444444;
  }
  
  #topic-status-info {
    border-top: 1px solid #dddddd;
    height: 20px;
    max-width: 897px;
    padding-top: 10px;
}
  
  #topic-footer-button {
  width: 897px;
}
  #suggested-topics h3 {
    color: #444444;
  }
  
  aside.quote .title {
    -moz-user-select: none;
    background-color: #eeeeee;
    color: #646464;
    padding: 12px 12px 12px;
    border-left: none;
  }
  
  .small-action .topic-avatar i {
    color: #88ae04;
  }
  
  .small-action {
    border-top: 1px solid #444444;
    max-width: 897px;
  }
  
  #reply-control {
     border-top: 3px solid #88ae04;
    background: #efefef url("https://static.dpcdn.pl/2017_04_06/assets/img/skin_green/page-background.png") repeat-y fixed center center;
    opacity: 0.95;
  }
  
  #topic-progress .bg {
    background-color: #88ae04;
    border-right: none;
  }
  
  #topic-progress {
    color: #ffffff;
    background-color: #222222;
    border: none;
  }
  
  .topic-list .topic-list-item-separator td span {
    background-color: #88ae04;
    color: #ffffff;
  }
  
  .topic-list .topic-list-item-separator td {
    border-bottom: 1px solid #88ae04;
  }
  
  .user-menu .notifications .read {
    background: none;
  }
  
  .user-menu .notifications .read a {
    color: #88ae04!important;
  }
  
  .user-menu .notifications li span {
    color: #444444;
  }
  
  .user-menu .notifications li:hover {
    background-color: #dddddd;
  }
  
  .badge-notification, .badge-group {
    border-radius: 0;
  }
  
  .d-header .icons .unread-notifications {
    background-color: #88ae04;
    color: #ffffff;
  }
  
  .badge-notification.new-posts, .badge-notification.unread-posts {
    background-color: #88ae04;
    color: #ffffff;
  }
  
  .user-main .user-content, .user-table {
    background: #efefef url("https://static.dpcdn.pl/2017_04_06/assets/img/skin_green/page-background.png") repeat-y fixed center center;
    box-shadow: 0px 0px 10px #cccccc;
  }
  
  .user-preferences-page .user-table {
    color: #444444!important;
  }
  
  .uploaded-image-preview {
    background-color: #dddddd;
  }
  
  .user-preferences .static {
    color: #444444;
  }
  
  .muting .controls .ac-wrap .item span {
    color: #222222;
  }
  
  .user-main .user-stream .item {
    background-color: #444444;
    color: #dddddd;
    border-bottom: 1px solid #444444;
  }
  
  
  .user-main .user-stream .excerpt {
    color: #dddddd;
  }
  
  .user-activity-page .ember-view a {
    color: #88ae04;
  }
  
  body .avatar-wrapper {
    border-radius: 0;
  }
  
  .user-main .nav-stacked li > a.active {
    color: #eeeeee;
  }
  
   .user-main .about.collapsed-info .details {
    background-color: #dddddd;
    border-bottom: 1px solid #444444;
  }
  
  .user-main .about.collapsed-info {
    color: #222222;
  }
  
  .user-main .about .details {
    background: none;
    color: #222222;
  }
 
  .user-main .about .secondary {
    background-color: #88ae04;
    color: #222222;
    border: none;
  }
  
  .user-main .about .secondary dt, .user-main .about .groups .group-link {
    color: #ffffff;
  }
  
  .user-main .about.collapsed-info .controls ul a, .user-main .about .controls a, .user-main .about .secondary dd {
    color: #ffffff;
    text-decoration: underline;
  }
  
  a.mention, a.mention-group {
    border-radius: 0;
    background-color: #88ae04;
    color: #ffffff!important;
    
  }
  
}
