css:
@-moz-document domain("www.ensage.io") {
.Button,
.NavButton,
.PageControls a,
.Previous,
.Next,
.MorePager a,
.ProfileButtons,
.SignInOrRegister a,
button,
input[type="submit"],
input[type="reset"],
input[type="button"] {
    border: 1px solid white;
    color: white;
}

.Button:hover,
.NavButton:hover,
.PageControls a:hover,
.Previous:hover,
.Next:hover,
.MorePager a:hover,
button:hover,
.ProfileButtons:hover,
.SignInOrRegister a:hover,
input[type="submit"]:hover,
input[type="reset"]:hover,
input[type="button"]:hover,
.Button:focus,
button:focus,
input[type="submit"]:focus,
input[type="reset"]:focus,
input[type="button"]:focus {
    color: #333;
    border-color: white;
    background-color: white;
}

.WarningMessage {
    background-color: #920303;
    color: white
}

body {
    background-color: #101010;
    color: #ccc
}

.DataList.Discussions,
.DataList.CategoryList,
.DataList.Notifications,
.GuestBox,
.BoxFilter,
.CategoryFilter,
.BoxCategories,
.MessageList,
dl.About,
.Section-Profile .PhotoWrapLarge,
.DataList.Conversations,
.DataList.SearchResults,
.Profile.EditMode #Content form,
.SideMenu,
.UserBox,
.DataList-Connections,
.DataList.Activities,
#Status {
    background-color: #444444
}

a {
    text-decoration: none;
    color: #2c90a7
}

.Role_Administrator .Author a {
    color: #ff0000!important
}

.Count {
    background: #313131
}

.DataList.Discussions,
.DataList.CategoryList,
.DataList.Notifications,
.GuestBox,
.BoxFilter,
.BoxCategories,
.MessageList,
dl.About,
.Section-Profile .PhotoWrapLarge,
.DataList.Conversations,
.DataList.SearchResults,
.Profile.EditMode #Content form,
.SideMenu,
.UserBox,
.DataList-Connections,
.DataList.Activities,
#Status,
#LatestPostList,
.Box {
    background-color: #444444
}

.BoxNewDiscussion {
    /* margin-top: 29px!important; */
    
    text-align: center;
    background-color: #444444;
    display: block;
    padding: 20px;
    border-radius: 2px;
    box-shadow: 0 2px 3px rgba(10, 10, 10, .1), 0 0 0 1px rgba(10, 10, 10, .1);
    position: relative;
    margin-bottom: 15px!important;
    padding-bottom: 10px;
}

.CasualMessage {
    background-color: #435869;
    color: #fff
}

.InfoMessage,
.Hero,
.Tag-Announcement {
    background-color: #327334!important;
    color: #fff
}

.Button.Primary,
button.Primary,
.SignInOrRegister a.Popup,
input[type="submit"].button-primary,
input[type="reset"].button-primary,
input[type="button"].button-primary {
    color: #fff;
    background-color: #327334;
    border-color: #327334
}

.CommentCount:before {
    color: #2c90a7
}

.RoleSpan_Administrator {
    color: #ff0000!important;
    font-weight: bold
}

.DataList .Title a {
    color: #ffffff
}

.Head {
    background-color: #0f0c13!important;
    background-image: url('https://s.put.re/d9Uxrbb.png')!important;
    background-repeat: no-repeat!important;
    background-position: top center!important
}

.MeBox {
    margin-top: 0px;
    text-align: center;
    background-color: rgba(0, 0, 0, 0.2);
    -moz-border-radius: 10px 10px 10px 10px;
    border-radius: 10px 10px 10px 10px;
    /* standards-compliant: (IE) */
}

#Head a {
    color: #fff;
    text-shadow: 0px 0px 5px black
}

.HasNew {
    background: #c17b2f;
    color: #fff
}

.Item .Author a {
    color: #fff
}

a:hover,
a:hover .TextColor {
    color: #50d0c5
}

.MenuItems a,
.MenuItems a:link,
.MenuItems a:visited,
.MenuItems a:active {
    color: rgb(0, 0, 0)!important
}

blockquote.Quote,
blockQuote.UserQuote {
    padding: 5px 10px;
    margin: 10px 15px;
    background: #2d2d2d;
    border-left: 4px solid rgba(0, 236, 51, 0.44);
    font-style: italic;
    font-size: 14px
}

.ProfilePhotoLarge {
    border: 15px solid rgb(115, 115, 115)
}

label,
.Methods b {
    color: #ccc
}

.Item.Activity a {
    color: #2c90a7
}
}
