css:
@-moz-document regexp(".*console\\.aws\\.amazon\\.com.*") {
/* Global */
* {
    text-rendering: optimizeLegibility !important;
}
/* Resource Groups menu and the little black pipe seperator between nav shortcuts */
#nav-resourceGroupsMenu,
.nav-menu-separator {
	display: none !important;
}

/* Main "Services" menu arrow icon */
.nav-menu-arrow {
	display: none;   
}

/* Main "Services" menu */
#nav-shortcutBar, #nav-servicesMenu {
    padding-left: 1px !important;   
    padding-right: 1px !important;   
}
/* Main "Services" menu container */
#nav-servicesMenu > div.nav-elt-label {
    padding-left: 0px!important;
    padding-right: 0px!important;
}

/* Nav menu Service Shortcuts */
li.nav-shortcut a {
	padding-left: 1px !important;
    padding-right: 1px !important;
}
/* Nav menu Service Shortcuts */
#nav-shortcutBar > li.service-link.nav-elt.nav-shortcut > a > span.service-label {
    font-size: 12pt;
    letter-spacing: -0.1px;
}
#nav-shortcutBar > li.nav-shortcut > a,
#nav-shortcutBar > li.service-link.service-link-ddb.nav-elt.nav-shortcut > a {
    margin-left: 0px;
    margin-right: 0px;
    border-left-width: 0px;
    padding-left: 0px;
    padding-right: 0px;
    border-right-width: 0px;
}
/* Custom Shortcut menu Pin Link */
/*
a#nav-shortcutMenu {
    position: absolute !important; 
    left: 98% !important;   
}*/
/* Custom Shortcut menu clickable div */
/*#nav-shortcutMenu > div.nav-elt-label.ico-pin.awsc-ico-menu {
    background-color: transparent!important;
    position: fixed;
    float: right;
    
}*/
/* Orange Cube, Top Left of Nav Bar */
#nav-logo {
	margin-left: 0px!important;
    margin-right: 0px!important;
}
/* Identification Container and Label */
#nav-usernameMenu, #nav-usernameMenu > div.nav-elt-label {
    text-overflow: initial !important;
    max-width: none !important;
    padding: 0px !important;
    margin: 0px !important;
}
/* Identification Container */
#nav-usernameMenu {
    display:flex !important;
    align-items:center !important;
}
/* Identification Label */
/* #nav-usernameMenu > div.nav-elt-label {
    overflow-x: visible;
} */
/* Entire right side of nav menu - Notification/Bell, Identification, Region, Support */
#nav-menu-right {
    display:flex;
    align-items:center;
    text-overflow: initial;
    max-width: none;
}

}
