css:
@-moz-document regexp(".*tinyz.*"), regexp(".*tinymoviez.*") {
@font-face {
  font-family: Vazir;
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir.eot');
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir.eot?#iefix') format('embedded-opentype'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir.woff') format('woff'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir.ttf') format('truetype');
  font-weight: normal;
}

@font-face {
  font-family: Vazir;
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Bold.eot');
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Bold.eot?#iefix') format('embedded-opentype'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Bold.woff') format('woff'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Bold.ttf') format('truetype');
  font-weight: bold;
}

@font-face {
  font-family: Vazir;
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Light.eot');
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Light.eot?#iefix') format('embedded-opentype'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Light.woff') format('woff'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Light.ttf') format('truetype');
  font-weight: 300;
}

body {
	background: #fff;
}

body,
input,
select,
#links a,
.g-button,
#options select {
	font-family: Roboto, Vazir, Iranian Sans, Tahoma, sans-serif !important;
    font-weight: 400;
}
 
input,
select,
#options select {
    font-size: 10px;
	border-radius: 5px !important;
}

#wrapper {
	box-shadow: none;
    width: 625px;
}

#content {
	padding: 11px 0;
}

#page > img:first-of-type[style*="width:620px;margin-bottom:10px; margin-right:3px;"] {
	margin-right: 0 !important;
	margin-bottom: 0 !important;
    width: 625px !important;
}

#hot-new {
	margin-right: 0 !important;
   	margin-bottom: 30px !important;
}

#hot-new img.new-movie-pic,
#hot-new td div {
	width: 207px;
}

#news-table > tbody > tr:nth-child(1) > td > h3[style="white-space:nowrap;"] {
	margin-bottom: 25px !important;
}

#topbar, #nav {
	background: #333;
}

#search {
	width: 235px;
    border: none;
    position: relative;
    top: 7px;
    font-size: 10px;
}

#page {
	width: 100%;
}

#links {
	margin-top: 40px;
}

.links-box #links {
	margin-top: 0;
}

.dubb {
	background: #fff;
    color: #388D5C;
    text-shadow: none;
    font-weight: bold;
    font-size: 12px;
    padding: 10px 0;
}

table.info td.title,
.post #awards {
	background: #fff;
    color: #000;
    text-shadow: none;
    font-weight: bold;
    font-size: 12px;
    padding: 10px 0;
}

.post #awards a {
	color: #136cb2;
}

div.list,
#news-table,
#searchresults,
#searchresults a,
*[style*="border-left:1px dashed #CCC"],
*[style*="border-top:dashed 1px grey"],
p[style*="border-top:dashed 1px grey"] {
	border: none !important;
}

.lit {
	margin-bottom: 50px;
}

.lit.find {
	margin-bottom: 0px;
}

#searchresults {
	box-shadow: 0 1px 5px #333;
}

#searchresults a,
.lit:nth-child(odd) {
	background: #fff;
}

hr,
a[href="invite.php"] img,
#search-submit,
.report_link,
#sidebar,
#comments,
#content + div,
#links + br + h2,
table#sug + br + h2,
#page-links > li:nth-child(2),
#page-links > li:nth-child(3),
#jsddm > li:nth-child(7),
#jsddm > li:nth-child(8),
#jsddm > li:nth-child(6),
#jsddm > li:nth-child(5),
#page > h2:nth-child(7)[style="font-size:14px; margin-bottom:5px;"],
#page > h2:nth-child(3)[style="font-size:14px; margin-bottom:5px;"],
#page > h2:nth-child(6)[style="font-size:14px; margin-bottom:5px;"],
#page > h2:nth-child(7)[style="text-align:center; margin-bottom:5px;"],
#page > h2:nth-child(11)[style="text-align:center; margin-bottom:5px;"],
#page > p:nth-child(4)[style="float:left; margin-left:20px;"] a.g-button,
#page > div[style*="background:#e5ffcb; border:1px dashed #8bc652;"] + p:nth-child(3),
#page > div:nth-child(4)[style="width:100%; display:block; color:#fa7705; font-size:12px; margin-bottom:10px; text-align:right; font-weight:bold; position:relative;"],
#page > div:nth-child(8)[style="width:100%; display:block; color:#fa7705; font-size:12px; margin-bottom:10px; text-align:right; font-weight:bold; position:relative;"] {
	display: none !important;
}

#page > p:nth-child(2) {
	visibility: hidden;
}

#movie_picture,
#sug img.movie-pic {
	box-shadow: 0 1px 4px rgba(0, 0, 0, 0.4);
}

table#sug {
	margin-top: 30px;
}

#sug td:hover {
	background: none;
}

h3,
.subtitle,
#jsddm li a,
#awards-list li,
#page-links li a,
.backdrop-inner h3,
#page-links li a:link,
.person-list-b .person h2.movies-list,
.backdrop-inner span {
	font-weight: normal;
}

td[style="position:relative; background:#f1e281; color:#373737;"] {
	background: none !important;
}

.post {
	border: none;
    position: relative;
}

#page > div.post > table.info.single {
    position: absolute;
    right: 245px;
    top: 75px;
}

#page > div.post > table.info.single + br + br + p {
	float: none;
    clear: both;
    padding-top: 60px;
    padding-right: 10px;
}

#page > div.post > table.info.single .add_to_watchlist img {
	left: 0 !important;
    right: 330px !important;
    top: 5px !important;
}

#page > div.post > a:nth-child(2)[href^="trailer.php?id="] img {
	top: 100px !important;
    left: -2px !important;
}

.post #awards,
table.info td.title {
	padding: 2px 0;
}

b,
strong,
.post label,
.post #awards,
#page .text h1,
.quality-box div label,
[style*="font-weight:bold"],
[style*="font-weight: bold"],
#page > div:nth-child(1) > h2,
#page > div:nth-child(2) > h2,
table.info td.title {
	font-weight: 500 !important;
}

td[style*="background:#e9edeb"],
td[style*="background:#f5e3ed"],
td[style*="background:#f8edea"] {
	background: none !important;
}

#page {
    margin-bottom: 60px;
}

#page > div.post > table > tbody > tr:nth-child(10) > td > a > img:not(.show-img),
#page > table > tbody > tr:nth-child(1) > td:nth-child(2) > a > img:not(.show-img),
#page > table > tbody > tr:nth-child(1) > td:nth-child(1) > a > img:not(.show-img) {
	position: relative;
    top: 3px;
}

.-dl .-dl-title {
    color: #444;
	background-color: #f1f1f1;
    border: none;
    border-top: 1px solid #fff;
}

.-dl .-dl-body {
	padding-top: 0;
}

.links-box,
.quality-box {
	border: none !important;
    border-top: 1px solid #fff;
    width: 94%;
}

#links .links-box p[style="font-weight:bold; font-size: 13px; float:right;"],
#links .links-box p[style="font-size: 12px; float:right; width: 400px; margin-bottom:5px; text-align:justify; white-space:normal;"] {
	font-size: 10px !important;
}

#explain {
	background: none;
    border-radius: 0;
}

ul.nom[style="border-right: 1px dashed #999; padding-right:20px;"] {
	border-right: 1px solid #eee !important;
}

.links-box {
	background-color: #f2fdd7;
    margin-bottom: 1px;
}

#links a,
#links a:active,
#links a:visited {
	color: #8ebd43;
}

.g-button,
.links-box #links a,
.links-box #links a:active,
.links-box #links a:visited,
a[style="color:white; background:#A5282F; text-shadow:none;"] {
	padding: 2px 10px 4px !important;
    background: #8ebd43 !important;
    color: #fff !important;
    border-radius: 3px !important;
  	opacity: 0.7 !important;
    text-decoration: none !important;
    box-shadow: none !important;
    border: 1px solid #fff !important;
    margin-left: 2px !important;
    font-weight: normal !important;
    text-shadow: none !important;
}

.g-button:hover,
.links-box #links a:hover,
a[style="color:white; background:#A5282F; text-shadow:none;"]:hover {
	opacity: 1 !important;
}

#page > div:nth-child(1)[style*="border:1px solid #fa7705"],
*[style="background:#e5ffcb; border:1px dashed #8bc652; padding:5px 10px; margin:10px 0; margin-top:0; text-align:right; overflow:auto; line-height:1.5em;"] {
	border: none !important;
    margin-bottom: 1px !important;
    background: #f2fdd7 !important;
}

#awards-list {
	background: #fafafa url("https://image.flaticon.com/icons/svg/207/207033.svg") no-repeat 6% 50% !important;	
	background-size: 10% !important;    
}

#awards-list,
#page > div:nth-child(1)[style*="text-align:center; direction:ltr"],
#page > div:nth-child(2)[style*="text-align:center; direction:ltr"] {
    width: 625px;
}

#awards-top,
#awards-bottom {
	display: none;
}

#awards-list li {
	font-size: 13px;
}

.post[style="overflow:hidden; position:relative;"] {
	margin-top: -20px;
}

.post h1[style="color: #3c3d3f;"] {
	margin-bottom: 10px;
    white-space: nowrap;
    text-overflow: ellipsis;
    direction: ltr;
    padding-left: 10px !important;
    padding-right: 10px !important;
}

.post h1[style="color: #3c3d3f;"] span {
	display: none;
}

#page .text h1,
#page h1.list-title,
#user-menu + hr + h2,
#page > h1:first-of-type,
#page > h2:first-of-type,
#page > h3:first-of-type,
.post h1[style="color: #3c3d3f;"],
#page > h2:nth-child(7)[style="text-align:center; margin-bottom:5px;"],
#page > div:nth-child(1)[style="text-align:center; direction:ltr"] > h2,
#page > div:nth-child(2)[style="text-align:center; direction:ltr"] > h2,
h3[style="display:block; width:100%; text-align:center; background:#555; color:white; padding-top:5px; padding-bottom:5px;"] {
	padding: 15px 0 !important;
    background: #f7f7f7 !important;
    text-align: center !important;
    margin-bottom: 2px !important;
    font-weight: 500 !important;
    color: #333 !important;
}

.backdrop-inner {
    background: rgba(0, 0, 0, .3);
	padding: 0;
    left: 0;
    bottom: 0;
    width: 626px;
    box-shadow: 0 1px 3px #aaa;
}

.backdrop-inner h3 {
	right: 10px;
    bottom: 5px;
}

.backdrop-inner span {
	top: 120px;
}

#news-table > tbody > tr:nth-child(1) > td[style="border-bottom:1px dashed #999; padding-bottom:5px;"] {
	border-bottom: 1px solid #eee !important;
}

.person-list-b .person {
	background: #f3f3f3;
    padding: 0;
}

.person-list-b a.person-movie {
	line-height: 0;
}

#tv_table {
    border-spacing: 8px;
}

#tv_table td {
	background: #f3f3f3;
    border: none;
}

#tv_table .show-details,
#tv_table td img.show-img {
	border: none;
}

#tv_table .show-details td.l_s {
	background: #aaa;
}

#options {
	width: auto;
    border: none;
    background: #f7f7f7;
    margin-bottom: 1px;
}

#options > table > tbody > tr:nth-child(1) > td > p {
	display: none;
}

#options a img[src="images/3D-HSBS.png"] {
	left: 25px !important;
    bottom: 0 !important;
}

#options .g-button {
	padding: 0px 8px !important;
    position: relative;
    bottom: 1px;
}

#options .g-button[href="watchlist.php?t=sr"] {
	top: 21px;
}

#options label {
	width: 75px;
    display: inline-block;
    text-align: left;
}

#options select,
#options select[style="border:1px solid red;"] {
	border: 1px solid #ddd !important;
}

#user-menu li {
	background: #fff;
    border-bottom: 1px solid #eee;
    padding-bottom: 5px;
}

#user-menu + br + br + br + br + hr,
#user-menu + br + br + br + br + hr + br + h2 {
	display: none;
}

#page > div:nth-child(2) > hr:nth-child(4) {
	display: none;
}

#page > div:nth-child(2) > div:nth-child(3)[style="float:left; border:1px dashed #CCC; position:relative; padding:5px; width:300px; text-align:center; margin-top:20px;"] {
	border: none !important;
    background: #fafafa;
}

#page > div:nth-child(2) > div:nth-child(3)[style="float:left; border:1px dashed #CCC; position:relative; padding:5px; width:300px; text-align:center; margin-top:20px;"] h4 {
	display: none;
}

#page a[href^="dl"] + br,
#page a[href^="/dl"] + br,
#page > div:nth-child(1)[style="text-align:center; direction:ltr"] > h2 + br,
#page > div:nth-child(2)[style="text-align:center; direction:ltr"] > h2 + br {
	display: none;
}

#page a[href^="dl"]:hover,
#page a[href^="/dl"]:hover {
	text-decoration: none;
    background-color: #f3f3f3;
    border-right-color: #ccc;
    background-image: url("data:image/svg+xml;base64,");
}

#page a[href^="dl"],
#page a[href^="/dl"] {
    display: block;
    padding: 10px 10px 10px 60px;
    color: #555;
    text-align: left;
    margin-bottom: 2px;
    border-right: 2px solid #fcfcfc;
    background: #fcfcfc url("data:image/svg+xml;base64,") 10px 1px no-repeat;
}
}
