@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hdfilme.cc") {
  

	
body {
	overflow-x: hidden;
	background-color: black;
}	

.box {
    background: #000000 url("http://www.hotpoint.co.uk/digitalassets/Colours/web16/black.png") repeat scroll 0px 0px;
	  font-size: 14px;
	  font-family: 'Roboto',sans-serif;
	  font-weight: 400;
  	color: #0de03f;
}
  

.box-product {
	padding: 5px;
	padding-bottom: 0;
	background-color: #000;
	position: relative;
	margin-bottom: 20px;
	vertical-align: top;
}

.products .box-product {
	box-shadow: #FF1806 0px 2px 3px 3px;
}

.products .box-product:hover {
  box-shadow: #12FF03 0px 2px 3px 3px;
}

.title-section {
	padding: 19px 28px 25px 30px;
	color: #fff;
	text-transform: uppercase;
	display: inline-block;
	background-repeat: no-repeat;
	background-size: 100% 100%;
}


.title-section span {
	display: inline-block;
	background-color: #000000;
	padding: 2px 5px;
	line-height: 19px;
}

.navbar {
	background-color: #000;
	border-radius: 0;
	margin-bottom: 0;
	border: none;
}

Element {
	background: #6c6c6c1a;
	padding: 6px;
	border-radius: 6px;
	margin: 2px;
}

.nav-menu .navbar-collapse.in .nav-inner {
	left: 0;
	transition: all 0.6s;
	z-index: 9999;
	opacity: 1;
	visibility: visible;
	background-color: #000000;
}


.btn-xemnow {
	background-color: #009CFF;
	color: #fff;
	padding: 6px 19px 5px;
	font-size: 16px;
	text-transform: uppercase;
}



.nav-tabsxx > li.active a:hover, .nav-tabsxx > li.active a:focus {
	border: none;
	background-color: #68C939;
	color: #fff;
}



.nav-tabsxx > li a:hover, .nav-tabsxx > li a:focus {
	background-color: #68C939;
	color: #fff;
	transition: all 0.2s;
}

.nav-tabsxx > li.active a {
	background-color: #000;
	color: #fff;
	border: none;
	font-size: 15px;
	transition: all 0.2s;
}

.popover {
         background-clip: padding-box;
         background-color: #000000;
         border: 1px solid rgba(0, 0, 0, 0.2);
         border-radius: 6px;
         box-shadow: 0 5px 10px rgba(0, 0, 0, 0.2);
         display: none;
         font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
         font-size: 14px;
         font-weight: normal;
         left: 0;
         line-height: 1.42857;
         max-width: 276px;
         padding: 1px;
         position: absolute;
         text-align: left;
         top: 0;
         white-space: normal;
         z-index: 1060;
         box-shadow: #00b7ff 0px 0px 4px 4px;
  
}

.title-product a {
	color: #0de03f;
	display: block;
	display: -webkit-box;
	height: 2.4em;
	line-height: 1.2em;
	-webkit-line-clamp: 2;
	-webkit-box-orient: vertical;
	overflow: hidden;
	text-overflow: ellipsis;
}


.navbar .navbar-collapse .nav-search .btn, .navbar .navbar-collapse .nav-search input {
	background-color: #000000;
	border-radius: 0;
	border-color: #FF0B0B;
}

.navbar .navbar-collapse .nav-search input::-moz-placeholder {
	color: #00ff4b;
	font-size: 14px;
}

.form-control {
  color: #00b7ff;
}


.buttonStyle, #headerSearchSuggestion .linkShowMore a {
	text-align: center;
	display: block;
	padding: 6px 2px;
	background-color: #000;
	text-transform: uppercase;
	color: #05f866 !important;
	box-shadow: #8808FF 0px 0px 2px 2px;
}

#headerSearchSuggestion {
	display: none;
	position: absolute;
	top: 100%;
	right: 0;
	left: -1px;
	margin-top: 7px;
	background-color: #000;
	padding: 0 10px 10px;
	-webkit-animation: fadein 1s;
	-moz-animation: fadein 1s;
	-ms-animation: fadein 1s;
	-o-animation: fadein 1s;
	animation: fadein 1s;
	border: 1px solid #39AF39;
	box-shadow: #46FC03 0px 0px 2px 2px;
}


.navbar .navbar-collapse li a {
	display: block;
	padding: 8px;
	border-bottom: 1px solid #d8d8d8;
	color: #5d5d5d;
	text-transform: uppercase;
	font-weight: normal;
	line-height: 1.8em;
}


.navbar .navbar-collapse #headerSearch a {
	color: #00FF03;
}

#headerSearchSuggestion .suggestList li a:hover {
	color: #ff9000;
}


.widget-sidebar {
	margin-bottom: 10px;
	background-color: #000;
	padding: 0 10px 10px;
}

.tab-session .nav-tabs > li.active a {
	color: #44fb00;
	background-color: #000;
}

.nav-tabs > li a:hover, .nav-tabs > li a:focus {
	border: none;
	color: #06ff00;
	background-color: black;
	transition: all 0.2s;
}


article, aside, details, figcaption, figure, footer, header, hgroup, main, menu, nav, section, summary {
	display: block;
	background-color: #000000;
}




tap-film clearfix .movie_episode {
  background-color: #000000;
}

.tap-film .list-film {
	overflow: hidden;
	background-color: #000000;
	box-shadow: #FF6808 0px 0px 7px 7px;
}


.tap-film .list-film a {
	border: 3px solid #ff0606;
	background-color: #000;
	display: block;
	padding: 8px;
	margin: 2px 1px;
	text-align: center;
	color: #ff0d0d;
	width: 50px;
	box-shadow: #ff0606 0px 0px 4px 4px;
}

.tap-film .list-film a:hover {
	background-color: #000;
	color: #69ff00;
	box-shadow: #69ff00 0px 0px 4px 4px;
	border: 3px solid #69ff00;
}

#footer {
	font-family: Helvetica;
	color: #717171;
	font-size: 12px;
	line-height: 25px;
	box-shadow: #ba00ff 1px -6px 16px -6px;
	padding: 20px 0 10px;
	background-color: #000;
}


a {
	color: #00d2ff;
	text-decoration: none;
}


.btn:hover, .btn:focus, .btn.focus {
	color: #333;
	text-decoration: none;
	background-color: #009CFF;
	box-shadow: #F11111 0px 0px 4px 4px;
}

.popover-title {
  color: #06FF00;
  background-color: #000000;
}

.popover-content {
  color: #00ff3d;
}

select.orderby {
	width: 100%;
	height: 36px;
	margin-bottom: 15px;
	padding-left: 8px;
	box-shadow: none;
	text-transform: capitalize;
	border: 1px solid #000;
	background-repeat: no-repeat;
	background-position: right;
	padding-right: 30px;
	background-color: #000000;
	box-shadow: #E7FF00 0px 0px 4px 4px;
}

option:nth-child(2n+1) {
	background-color: #000;
}

option {
	color: #00aeff;
}

.pagination > li > a:hover, .pagination > li > span:hover, .pagination > li > a:focus, .pagination > li > span:focus {
	color: #108eff !important;
	background-color: #000 !important;
	border-color: #00b7ff;
	box-shadow: #00b7ff 0px 0px 4px 4px;
	font-size: 14px !important;
}

.pagination li.active > a:focus, .pagination li:hover > a, .pagination li:focus > a {
	background-color: #000 !important;
}

.pagination > .active > a, .pagination > .active > span, .pagination > .active > a:hover, .pagination > .active > span:hover, .pagination > .active > a:focus, .pagination > .active > span:focus {
	z-index: 2;
	color: #e7ff00 !important;
	cursor: default;
	background-color: #000 !important;
	border-color: #e7ff00 !important;
	box-shadow: #ff00 0px 0px 6px 6px !important;
	font-size: 14px !important;
}

.pagination li a {
	color: #f00 !important;
	font-family: Cursive;
	background-color: #000;
	border-color: #000;
}


.btn-search:hover, .btn-search.active {
	color: red;
	background-color: black;
	box-shadow: #18ff00 0px 0px 5px 5px;
	border-color: #18ff00;
}

.btn-search {
	color: #00c0ff;
	text-transform: uppercase;
	background-color: #000;
	border-radius: 0;
	font-size: 14px;
	font-weight: 700;
	height: 36px;
	margin-bottom: 15px;
	padding: 6px 10px 6px 12px;
	box-shadow: #00c0ff 0px 0px 4px 4px;
}

body.plugin {
	display: none;
}

.Yfd {
	display: none;
}

.widget-sidebar.widget-face {
	display: none;
}

.title-widget-sidebar {
	margin-left: 1px !important;
	font-size: 14px;
	padding: 0 7px 23px;
	margin: 49px;
}


.title-widget-sidebar span {
	display: inline-block;
	background-color: #68c939;
	padding: 4px 9px;
	line-height: 19px;
	margin-bottom: -55px;
}



.navi-favorited {
	width: 100%;
	background: #68c939;
	cursor: pointer;
	padding: 4px 11px 7px 29px;
	margin-bottom: -8px;
	color: #fff;
	text-align: center;
	text-transform: uppercase;
	margin-top: 101px;
}

.carousel {
	position: relative;
	box-shadow: #0264f6 0px 0px 20px 2px;
}

}
