/*// Dark Youtube - Material Design Compatible ////////////////////////////*/

/*// This userstyle uses excerpts of both DUSKY GRAY FACEBOOK BY PIERO'NMU and "YouTube - Custom colors video progress bar" by TokHaar////////////////////////////*/


@-moz-document regexp("http(s)?://.*\\.youtube\\.com/(?!(v[0-9].[0-9]/)).*") {    

html, body {
    background-color: #181818 !important;
}

body * {
 border-color: #222 !important; 
 color: #888 !important;
 background-color: transparent !important;
}
   
.yt-uix-clickcard-card-body, .yt-uix-hovercard-card-body {
    background-color: #222 !important;
}
   
#masthead-positioner {
    background-color: #222 !important;
}
   
.masthead-search-terms-border {
    box-shadow: inset 1px 1px 1px 1px #333;
}
   
.guide-pinned .guide-pinning-enabled #appbar-guide-menu {
    background-color: #222 !important;
}
   
#guide-container {
    background-color: #222 !important;
}
   
.gssb_e {
    background-color: #222 !important;
}
   
.yt-uix-menu-content {
    background-color: #222 !important;
}

.ytp-popup {
    background-color: #222 !important;
}
    
.comment-section-renderer-paginator {
    background-color: #222 !important;
    background-image: none !important;
}
   
.ytp-progress-bar-container:not([aria-disabled="true"]) {
    background-color: #444 !important;
}
   
.ytp-progress-bar {
    background-color: #444 !important;
}
   
.html5-scrubber-button:hover, .ytp-scrubber-button:hover, .ytp-swatch-background-color, .ytp-swatch-background-color-secondary {

   background: #FF0000 !important;

}

/* progress bar */

.html5-play-progress, .ytp-play-progress {

   background: #FF0000 !important; 

}

.ytp-volume-slider-track, .ytp-volume-slider-handle:before {

   background: #FF0000 !important;

   z-index: -117;

}

.ytp-settings-button.ytp-hd-quality-badge::after, .ytp-settings-button.ytp-4k-quality-badge::after, .ytp-settings-button.ytp-5k-quality-badge::after, .ytp-settings-button.ytp-8k-quality-badge::after, .ytp-settings-button.ytp-3d-badge::after {

   background-color: #FF0000 !important;

}

.ytp-swatch-color {

   color: #FF0000 !important;

}

.ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox {

   background: #FF0000 !important;

}

.ytp-chrome-controls .ytp-button.ytp-youtube-button:hover:not([aria-disabled="true"]):not([disabled]) .ytp-svg-fill-logo-tube-lozenge {

   fill: #FF0000 !important;

}

.ytp-cued-thumbnail-overlay:hover .ytp-large-play-button-bg, .ytp-large-play-button.ytp-touch-device .ytp-large-play-button-bg {

   fill: #FF0000 !important;

}
    
body .addto-watch-later-button, .addto-watch-later-button-sign-in, .addto-watch-later-button-loading, .addto-watch-later-button-error, .addto-watch-later-button-success, .addto-watch-later-button-remove {
    background-color: #e62117 !important;
}

}
