css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("soundcloud.com") {

.l-fluid-fixed {
    box-shadow: #00ff0c 0px 0px 22px 1px;
	position: relative;
	background-color: black;
}
  

.sound__content {
	-webkit-flex: 1;
	-ms-flex: 1;
	flex: 1;
	overflow-x: hidden;
	box-shadow: #00ff0c 0px 0px 22px 1px;
}
  
  
.gritter-item-wrapper {
	color: #00b7ff;
	background-color: #000;
	border-radius: 6px;
	box-shadow: #00b7ff 0px 0px 4px 4px;
	border: 1px solid #00b7ff;
}
  
.error .gritter-top {
	background: #45ff00 url(https://a-v2.sndcdn.com/assets/images/notifications/error-18e777c.png) center center no-repeat;
	background-image: url(https://a-v2.sndcdn.com/assets/images/notifications/error-18e777c.png),linear-gradient(#030303,#00b7ff 40%,#000);
}
  
  
.error .gritter-top, .gritter-item-wrapper.big-success .gritter-top {
	width: 40px;
	height: 100%;
	background-position: center center,left top;
	box-shadow: inset 0 1px 0 rgb(0, 0, 0);
	border: 1px solid #000;
}
  
  
.header {
	background: #000;
}

  
  
  
.header__navMenu > li > a:hover, .header__navMenu > li > a:focus {
    box-shadow: #039dff 0px 0px 21px 9px;
    border-radius: 13px !important;
}
  
  
.header__logoLink {
	background: url(http://fs5.directupload.net/images/170418/8f3svji7.png) no-repeat 12px 11px;
	background-size: 59px 53px;
	display: block;
	background-position: 5px -3px;
	background-color: black !important;
}
  
  
  
.g-dark textarea, .g-dark select, .g-dark input[type="text"], .g-dark input[type="password"], .g-dark input[type="search"] {
	color: #7bff00;
	background: #000;
	box-shadow: #72ff00 0px 0px 4px 4px;
}
  
  
.g-dark input[type="search"]:focus {
	background: #000;
	box-shadow: #ff0303 0px 0px 4px 4px;
}


  
  
.sc-border-light-bottom {
	border-bottom: 1px solid #000;
	box-shadow: #0885ff 0px 0px 7px 3px;
}  
  
  
  
.trackItem.hover, .trackItem.active {
	background-color: #000;
	margin: 3px -14px;
	padding: 1px 12px;
	box-shadow: #f00 0px 0px 7px 5px;
}
  
  
.sc-border-light-bottom {
	border-bottom: 1px solid #000;
	box-shadow: #5cff08 0px 0px 7px 4px;
	background-color: #000;
}
  
  
.trackItem.hover:not(.m-disabled) .trackItem__additional, .trackItem.active:not(.m-disabled) .trackItem__additional {
	background: #000;
	background: linear-gradient(to right,rgba(0, 0, 0, 0.9),#000 17px);
	right: 12px;
}
  
  
a.sc-link-light {
	color: #06ff00;
}
  
  
a.sc-link-dark:hover, a.sc-link-medium:hover {
	color: #00a5ff;
}
  
  
a.sc-link-light:hover {
	color: #f00;
}
  
  
a.sc-link-dark {
	color: #fcac08;
}
  
  
.sc-button-small.sc-button-like::before {
	background-size: 22px 25px;
	background-image: url(http://fs5.directupload.net/images/170418/btym7dmz.png);
	background-color: black;
}  
  
  
  .sc-button-small.sc-button-like:hover {
    background-size: 22px 25px;
    background-color: black;
  }
  
  
.sc-button-small.sc-button-repost::before {
	background-size: 16px 16px;
	background-color: black;
    background-image: url(http://fs5.directupload.net/images/180921/lkgyq759.png);
}
  
.sc-button-small.sc-button-repost:hover {
	background-size: 16px 16px;
	background-color: black;
  }

  
.sc-button-small.sc-button-addtoset::before {
	background-size: 22px 22px;
    background-color: black;
	box-shadow: #0078ff 0px 0px 3px 3px;
}  
  
  
.sc-button-small.sc-button-addtoset:hover {
  background-size: 22px 22px;
  background-color: black;
  }  
  
  
  
.sc-button-play:hover, .sc-button-play:focus, .sc-button-play.sc-button-focus, .sc-button-pause:hover, .sc-button-pause:focus, .sc-button-pause.sc-button-focus, .sc-button-prev:hover, .sc-button-prev:focus, .sc-button-prev.sc-button-focus, .sc-button-next:hover, .sc-button-next:focus, .sc-button-next.sc-button-focus {
	background-color: #008aff;
}
  
  
.sc-button-small.sc-button-play::before {
	background-image: url(data:image/svg+xml;base64,PHN2ZyB3aWR0aD0iNiIgaGVpZ2h0PSIxMCIgdmlld0JveD0iMCAwIDYgMTAiIHhtbG5zPSJodHRwOi8vd3d3LnczLm9yZy8yMDAwL3N2ZyI+PHRpdGxlPlBsYXkgMjA8L3RpdGxlPjxwYXRoIGQ9Ik0wIDEwbDEuMzg1LTVMMCAwbDYgNS02IDV6IiBmaWxsPSIjRkZGIiBmaWxsLXJ1bGU9ImV2ZW5vZGQiLz48L3N2Zz4=);
	background-color: #9efe004d;
}
  
  
.header__link.header__goUpsell:hover, .header__link.header__goUpsell:focus, .header__link.header__goUpsell:active {
	color: #039dff;
}
  
.g-dark a:hover, .g-dark a:focus {
	color: #039dff;
}  

.g-dark a {
	color: #039dff;
}  

.l-container.l-content {
	padding-top: 46px;
	background-color: black;
}
  
  
body {
	overflow-y: scroll;
	background: #000;
	padding-bottom: 75px;
}
  
  
.listenEngagement {
	position: relative;
	border: 1px solid #000;
	box-shadow: 0 1px 0 0 #000;
	padding: 19px 0 4px;
	margin-bottom: 1px;
}
  
  
.sc-button-medium.sc-button-like::before {
	background-size: 22px 25px;
	background-image: url(http://fs5.directupload.net/images/170418/btym7dmz.png);
	background-color: black;
	box-shadow: #0078ff 0px 0px 4px 5px;
}
 
.sc-button-medium.sc-button-like:hover {
	background-size: 22px 25px;
	background-image: url(http://fs5.directupload.net/images/170418/btym7dmz.png);
	background-color: black;
	box-shadow: #ff6c00 1px 1px 1px 1px;
}
  
  
  
.sc-button-medium.sc-button-share::before {
	background-size: 16px 16px;
    background-color: black;
    background-image: url(http://fs1.directupload.net/images/180921/ie9pa8ir.png);
}

.sc-button-small.sc-button-share::before {
	background-size: 16px 16px;
    background-color: black;
    background-image: url(http://fs1.directupload.net/images/180921/ie9pa8ir.png);
}


.sc-button-medium.sc-button-share:hover {
	background-size: 16px 16px;
	box-shadow: #ff6c00 1px 1px 1px 1px;
}
  
  
  
.sc-button-small.sc-button-more::before  {
	background-size: 16px 16px;
	background-image: url(http://fs5.directupload.net/images/170418/h2nwmpsp.png);
	background-color: black;
}
  
.sc-button-medium.sc-button-more:hover {
	background-size: 16px 16px;
	background-color: black;
	box-shadow: #ff6c00 1px 1px 1px 1px;
}  

.queueItemView__more.sc-button-small.sc-button-more::before {
	background-image: url(http://fs5.directupload.net/images/170418/h2nwmpsp.png);
	background-size: 16px 16px;
}
  
  
  
.gritter-close {
	display: none;
	background: url(http://fs5.directupload.net/images/170418/qmblvb52.png) center center no-repeat;
	background-size: 29px 19px;
}
  
  
.l-footer {
	padding: 11px 0;
	font-size: 11px;
	background: #000;
	z-index: 1;
}
  

	.playControls__inner {
	background-color: #000;
}
	
.sceneLayer, .sc-button-play, .playbackTimeline, .heroPlayButton {
    filter: hue-rotate(180deg);
}
  
  .waveformCommentsNode {
    filter: hue-rotate(0deg) !important;
}

  
.playControls__bg {
	background-color: #000;
	box-shadow: #5500f9 0px 0px 4px 4px;
}
 
.playControls__control {
	background-color: #000000;
}	
  
.playbackSoundBadge__title:hover, .playbackSoundBadge__title:focus {
	color: #ffad06;
}
  
a.sc-link-light:hover {
	color: #ffb400;
}
  
a.sc-link-light {
	color: #00a5ff !important;
}
  
.playbackSoundBadge__title, .playbackSoundBadge__title:visited {
	color: #ffc600 !important;
}
  
.volume.expanded .volume__sliderWrapper {
	box-shadow: 0 2px 4px rgba(0,0,0,.1);
	background-color: black;
}
  
 
.volume__sliderHandle {
	background-color: #fff300;
}
  
.volume__sliderProgress {
	background: #00b7ff;
}
	
	
.playControl.playing {
	background-image: url(http://fs5.directupload.net/images/170701/6k6d3phe.png);
	background-position: center center;
}
	
.skipControl__previous {
	background-image: url(http://fs5.directupload.net/images/170701/j7tl8tj2.png);
	background-position: 40% center;
}

.playControl {
	background: url(http://fs5.directupload.net/images/170701/2jtserub.png) no-repeat 55% center;
	padding: 0;
}
	
	
.skipControl__next {
	background-image: url(http://fs5.directupload.net/images/170701/wkl6ka6q.png);
	background-position: 60% center;
}
	
.shuffleControl::before {
	background-image: url(http://fs5.directupload.net/images/170701/cketib9b.png);
}

	
.repeatControl.m-new.m-none {
	background-image: url(http://fs5.directupload.net/images/170701/stq8bxar.png);
}

.volume__button::before, .volume__button::after {
	background-image: url(http://fs5.directupload.net/images/170701/anpva7o4.png);
}

.volume[data-level="1"] .volume__button, .volume[data-level="2"] .volume__button, .volume[data-level="3"] .volume__button, .volume[data-level="4"] .volume__button, .volume[data-level="5"] .volume__button {
	background-image: url(http://fs5.directupload.net/images/170701/anpva7o4.png);
    background-color:#000;
}

.volume[data-level="6"] .volume__button, .volume[data-level="7"] .volume__button, .volume[data-level="8"] .volume__button, .volume[data-level="9"] .volume__button, .volume[data-level="10"] .volume__button {
	background-image: url(http://fs1.directupload.net/images/180922/orhercim.png);
    background-color:#000;
}

.commentForm__wrapper {
	background: #000;
	border: 1px solid #00ff27;
}

.commentForm__input {
	background: #000;
	border: 1px solid #171717;
}

.sound__soundActions {
	background-color: #000;
}

.sc-button-small.sc-button-icon:not(.sc-button-stretch-icon), .sc-button-small.sc-button-responsive:not(.sc-button-stretch-icon) {
	background-color: #0000;
	border-bottom-color: #000 !important;
}

.moreActions__button, .moreActions__link {
	background-color: #000;
	color: #0093ff;
}

.moreActions__button, .moreActions__link :hover {
	background-color: #000;
	color: #0093ff;
}

.tabs__tabs, .tabs__headingContainer {
	background: #000;
}

.g-modal-section {
	background: #181818;
}

textarea, select, input {
	background: #000;
	border: 1px solid #ccc;
	color: #0078ff;
}

.sc-button {
	border: 1px solid #00a7ff;
	background-color: #000;
	color: #007bff;
}


.sc-button:hover {
	color: #333;
	outline: 0;
	border-color: #fa0;
}

.dialog {
    background: #000
}

.sound__content {
	border-radius: 16px;
}

.soundBadge__additional {
	/* background: rgba(255,255,255); */
	background: linear-gradient(to right,rgb(0, 0, 0),#000 17px);
}

.commentForm.m-active .commentForm__wrapper {
	background: #000;
	border-color: #0081ff;
}

.queue {
	height: 100%;
	box-shadow: 0 0 4px rgb(0, 0, 0);
	background-color: #000;
    
}

.queue__panel {
	border-bottom: 1px solid #000;
	border-radius: 3px;
}

.queue__itemWrapper {
	background-color: #000;
}

.queueItemView:hover, .queueItemView.m-active {
	background: #000;
	/* box-shadow: #f00 1px 1px; */
}

.queueItemView:hover.m-active {
	background: #000;
}

.header__navMenu > li > a.selected, .header > li > a:focus {
	background-color: #000;
    border-color: #0081ff;
}

.header__userNavItem.selected, .header__userNavButton.selected {
	background-color: #000;
}

.profileMenu__list {
	background-color: #000;
	box-shadow: #06f 0px 0px 24px 1px;
    border-radius: 6px;
}

.profileMenu__link:hover, .profileMenu__link:focus {
	background: #000;
    border-radius: 6px;
}
}
