css:
/* i really want this to be global */

a:link { color: #9393ef ! important;}
a:visited {color: #7a7aad ! important;}

body {
    
    background: #525252 !important;
    color: #fffffd;
}

.book .titlepage {
    background: #484848;
    color: #fffffd;
    margin: 0px auto;
    padding: 0 1em;
}

.book hr {
    background: #484848;
    height: .3em;
    border: 0px;
    margin: 0 -1em;
    padding: 0;
}

.book h2.subtitle {
    text-align: center;
    background: #404040;
}

h1, h2, h3, h4, h5, h6, b, .strong {
    color: #fffffd;
    /*background: #484848;*/
}

div.navheader * {
    background: #484848;
    color: #fffffd
}

div.navheader, div.navfooter {
    background: #484848;
    color: #fffffd
}

div.navheader {
    
    border-bottom: 3px solid #525252;
}

div.navheader ul, div.navfooter ul {
    background: #484848;
    color: #fffffd
}

div.navfooter {
    border-top: 1px solid #525252;
}

.sect1 h1, .appendix h1 {

    background: #313131;
    color: #fffffd
}

pre {
    background-color: #282828;
    border: 1px solid #050505;
    padding: .5em 1em;
    margin: 0 2em .5em 2em;
    font-weight: bold;
}

.preface h1, .part h1, .chapter h1, .appendix h1, .index h1, .sect1 h1 {
    background: #404040;
    color: #fffffd;
    border-bottom: .1em solid #525252;

}

pre.userinput {
    color: #fffffd;
    background: #282828;
}

pre.screen {
    color: #fffffd;
    background-color: #282828;
    font-weight: normal;
}

.wrap h1, h2, h3, h4, h5, h6, b, .strong {
    color: #fffffd;
    background: #484848;
}

.lfs .package {
    background: #484848;
    border-bottom: 0.2em solid #484848;

}

.lfs .configuration {
    background: #525252;
    border-top: 0.2em solid #525252;
}

.lfs .content {
    background: #525252;
    border-top: 0.2em solid #525252;
    padding-bottom: .1em;
    margin-bottom: 0;
}

div.note, div.tip {
    background-color: #737373;
    border: 2px solid #000;
    width: 90%;
    margin: .5em auto;
}

div.admon h3 {
    background-color: #737373;
    display: inline;
    margin-left: 2em;
}

div.important, div.warning, div.caution {
    background-color: #737373;
    border: medium solid #400;
    width: 90%;
    margin: .5em auto;
    color: #fffffd;
}

div.important em, div.warning em, div.caution em {
    color: #fffffd;
    font-weight: bold;
}
