@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ebay-kleinanzeigen.de") {


body {
	color: #06fb2c;
	background-color: #000;
}
  
.itemtile, .itemtile-condensed {
	width: 165px;
	height: 190px;
	display: block;
	background-color: #211c1c;
	box-shadow: #0998f3e6 0px 0px 4px 1px !important;
	cursor: pointer;
	border-radius: 2px;
}
  
.l-homepage-navigation-col {
	width: 194px;
	float: left;
}	
	
	
  .itemtile:hover, itemfile-condensed:hover, itemfile-link {
  width: 165px;
	height: 190px;
	background-color: #211c1c;
	box-shadow: #ff0b0b 0px 0px 4px 2px !important;
  color: #ff0b0b;
}  
  
.itemfile-link {
	color: #0165D2;
	outline: none;
	cursor: pointer;
	text-decoration: none;
	font-size: small;
}
  
.itemfile:hover, a:hover {
    color: #ff0b0b;
    text-decoration: none;
  font-size: small;
  	letter-spacing: 0.2px;
  }
 
  
.contentbox, .contentbox-unpadded, .contentbox-info, .contentbox-landingpage, .info-contentbox {
	box-sizing: border-box;
	background: #151010;
	padding: 12px;
	box-shadow: #00ff1e 0px 0px 12px 4px;
	border-radius: 3px;
}
  
.itemtile-title > a {
	color: #f97200;
}  
  
.tilesbox-featured {
	background-color: #000;
	box-shadow: orange 0px 0px 16px 8px;
}
	
.itemtile-body {
	color: #7bff00;
}
	
.a-green.bar {
	background-color: #1b1b1b;
	box-shadow: #8703fa 0px 0px 9px 2px;
}
  
#site-header {
	margin-bottom: 25px;
	background: black;
}
	
input.splitfield-input {
	background-color: #1a1a1a;
	text-overflow: ellipsis;
	padding: 7px 3px;
	height: auto;
	margin: 0;
	border: none;
	-moz-box-sizing: border-box;
	-webkit-box-sizing: border-box;
	box-sizing: border-box;
	width: 113%;
	min-height: 32px;
	display: inline-block;
	box-shadow: #ffcf00 0px 0px 6px 0px;
	vertical-align: middle;
}
	
*::-moz-placeholder {
	color: #00d1fe;
	font-family: cusrive;
	letter-spacing: 1.2px;
	vertical-align: middle;
}
	
.splitfield-right {
	background-color: #000;
	border-radius: 0 3px 3px 0;
	border: solid #cbcbcb;
	border-width: 0 0 0 1px;
	display: table-cell;
	vertical-align: middle;
	width: 45%;
	color: #f63d00;
	vertical-align: middle;
}
 
.dropdown-list {
	position: absolute;
  list-style: none;
	padding: 5px;
	margin: 10px 0 0 0;
  background-color: black;
	min-width: 100%;
	box-sizing: border-box;
	box-shadow: #f00 0px 0px 3px 3px;
	z-index: 100;
	vertical-align: middle;
}
	
.selectbox-option {
		background-color:black;
	}
	
.dropdown-list .multiselectbox-option:hover, .selectbox-option:hover {
	background-color: black;
	box-shadow: #008ff8 0px 0px 4px 4px;
}	
	
	
.dropdown-list .selectbox-option:hover {
		background-color: black;
	color:red;
	}
	
input.splitfield-input {
	background-color: #1a1a1a;
	text-overflow: ellipsis;
	padding: 6px 5px;
	height: auto;
	margin: 0px;
	border: none;
	-moz-box-sizing: border-box;
	-webkit-box-sizing: border-box;
	box-sizing: border-box;
	width: 115%;
	min-height: 32px;
	display: inline-block;
	box-shadow: #ffcf00 0px 0px 6px 0px;
	color: #deff00;
	vertical-align: middle;
}
	
.dropdown-list > li {
	padding: 5px 5px 5px 20px;
	color: #1bff03;
	cursor: pointer;
	white-space: nowrap;
	background-color: black;
}
	
.dropdown-list >li:hover {
	padding: 5px 5px 5px 20px;
	color: #f00;
	cursor: pointer;
	background-color: black;
	box-shadow: #008ff8 0px 0px 4px 4px;
	}
	
button, input, optgroup, select, .splitfield-dropdown, textarea {
	color: black;
	font: inherit;
	margin-right: 2px !important;
	width: 137px !important;
	background-color: black;
	vertical-align: middle;
}	
	
	
button:hover, .button:hover, .button-secondary:hover, .button-feature:hover, .button-browse-next:hover, .button-browse-previous:hover, .button-toggle:hover, .is-toggled:hover {
	background-color: #00ff03;
	text-decoration-color: #242424;
	box-shadow: #ff9000 0px 0px 46px 18px;
	color: #f00;
	letter-spacing: 3.5px;
	font-size: larger;
	font-family: impact;
	font-style: italic;
	text-align: -moz-left;
	text-indent: inherit;
	vertical-align: middle;
}
	
	
	
button, .button, .button-secondary, .button-feature, .button-browse-next, .button-browse-previous, .button-toggle, .is-toggled {
	vertical-align: middle;
	font-weight: 400;
	padding: 6px 13px;
	outline: none;
  border: none;
	background-color: #0165D2;
  box-sizing: border-box;
	color: #ffb00e;
	box-shadow: inset 0px -2px 0px 0px #014d9f;
	min-height: 32px;
	line-height: 1.4em;
	font-family: impact;
	font-size: larger;
	letter-spacing: 3.5px;
	vertical-align: middle;
}
	
.formcontrol:disabled, textarea:disabled, select:disabled, .splitfield-dropdown:disabled, .recaptchatable input[type="text"]:disabled {
	background-color: #000;
	color: #deff00;
	border-color: #000;
}
	
.mainnav-item-inner:hover {
	color:limegreen;
	text-decoration-style: dashed;
	}	
	


.mainnav-sub {
	background-color: #000;
	white-space: nowrap;
	vertical-align: middle;
	border: solid #F00;
	border-width: 0 1px 1px;
	border-radius: 0 0 3px 3px;
	padding: 10px 10px 0px 10px;
	box-shadow: #f00 0px 0px 4px 3px;
}

.button-browse-next, .button-browse-previous {
	background-color: #979797;
	box-shadow: inset 0px -1px 0px 0px #7e7d7d;
	background-image: url(data:image/svg+xml;base64,);
	background-repeat: no-repeat;
	background-position: center center;
	min-width: 30px;
	min-height: 23px;
	margin: -6px -23px -3px -20px;
	position: relative;
	right: 438px;
}

}
