@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.hardware.fr") {
  html, .container, body > * {
    background-color: rgba(0, 0, 0, 0.63);
  }
  body > div, .hfrheadmenu, #mesdiscussions .citation, #mesdiscussions .oldcitation, #mesdiscussions .quote, #mesdiscussions .oldquote, #mesdiscussions .fixed, #mesdiscussions .code, #mesdiscussions .spoiler, #mesdiscussions .oldspoiler {
    width: 100% !important;
  }
  body > br {
    display: none;
  }
  #md_arbo_top a, .Topic, #mesdiscussions div.onglet_menu div a:hover, .onglet_menu_sel, .profil, .s1Topic, .s2Topic, .citation, .oldcitation, .quote, .oldquote, .message, .reponse {
    color: rgba(255, 255, 255, 0.83);
  }
  table tr.ligne_booleen {
    border: 1px solid rgb(143, 143, 143);
  }
  #mesdiscussions, #mesdiscussions table.main, #mesdiscussions div.onglet_menu div, #mesdiscussions td, #mesdiscussions th, #mesdiscussions div.onglet_menu div a, #mesdiscussions div.onglet_menu div span {
    border: 0px solid #FFFFFF;
  }
  .cBackCouleurTab1 {
    background-color: rgba(0, 0, 0, 0.43);
  }
  .cBackCouleurTab2 {
    background-color: rgba(0, 0, 0, 0.23);
  }
  table {
    border-collapse: collapse;
  }
  #mesdiscussions .citation, #mesdiscussions .quote, #mesdiscussions .spoiler, #mesdiscussions .oldspoiler {
    background-color: rgba(255, 255, 255, 0.40);
  }
  
  .cat.cBackCouleurTab1 .catCase1 .cCatTopic {
    color: #8af1ff !important;
  }
  .cat.cBackCouleurTab1 .catCase1 .Tableau {
    color: #8ac6ff !important;
  }
  #mesdiscussions a, .cLink, #mesdiscussions a.cLink, .Tableau, .s1Tableau, .s2Tableau, .cat, .sujet, .cat.cBackCouleurTab1, .sujet, #mesdiscussions a.cCatTopic, .legende, .Ext, .seachhelp, .searchCase, .cExt, .s1Ext, .s2Ext, .rightbutton, .titreforum, .toberead, .menu, .nombres, .spoiler, .oldspoiler, .menuExt a {
    color: rgb(223, 223, 223);
    text-decoration-color: rgb(223, 223, 223);
  }
  
  .mesdiscussions > table:not(.main):nth-child(odd) > tbody > tr, .cBackCouleurTab1 .catCase2, .cBackTab1, .cat.cBackCouleurTab1, .sujet, .profil, .cBackTab2, #mesdiscussions div.onglet_menu div a:hover, .onglet_menu_sel, .searchCase, .sujetCase1, .sujetCase6, .sujetCase6bis, .sujetCase9, .catCase2, .reponse, .profilCase2, .profilCase5, .profilCase4, .modoCase0, .modoCase1, .modoCase1bis, .modoCase3 {
    background-color: rgb(53, 53, 53) !important;
  }
  .mesdiscussions > table:not(.main):nth-child(even) > tbody > tr, .cBackHeader, .cat, .catCase2 {
    background-color: rgb(43, 43, 43) !important;
  }
  
  .mesdiscussions > table:not(.main):nth-child(even) > tbody > tr {
    background-color:rgb(43, 43, 43) !important;
  }
  .sf-download-bar {display: none;}
}
