css:
@-moz-document domain("80.lv") {
.index-section{
background: #1d1d1d;
}
.container-item-inn{
background: #1d1d1d;
}
.container-item.iso-elem, .container-item.single_block, .container-item.this-iso-elem {
background: #1d1d1d;
}
.container-item.with_border{
border-color: #1d1d1d;
}
.header{
background: #232323;
}
.additiononal-h-block{
background: #2f2f2f;
}
#wpcomm .wc-comment .wc-comment-right {
    background: #1d1d1d;
}
#wpcomm .wc-comment-footer{
    border-bottom: 1px solid #101010;
}
#wpcomm .wc-form-wrapper {
    background: #1d1d1d;
}
#wpcomm .wc-form-wrapper{
    background: #1d1d1d;
    border: 1px solid #1d1d1d;
}
#wpcomm{
    border-top: #101010 solid 1px;
}
#wpcomm .wc-field-textarea textarea{
    background-color: #1d1d1d;
}
#wpcomm .wpdiscuz-textarea-wrap{
    border: #1d1d1d 1px solid;
}
#wpcomm .wpd-form-row .wpdiscuz-item input[type="text"], #wpcomm .wpd-form-row .wpdiscuz-item input[type="email"], #wpcomm .wpd-form-row .wpdiscuz-item input[type="url"], #wpcomm .wpd-form-row .wpdiscuz-item input[type="color"], #wpcomm .wpd-form-row .wpdiscuz-item input[type="date"], #wpcomm .wpd-form-row .wpdiscuz-item input[type="datetime"], #wpcomm .wpd-form-row .wpdiscuz-item input[type="datetime-local"], #wpcomm .wpd-form-row .wpdiscuz-item input[type="month"], #wpcomm .wpd-form-row .wpdiscuz-item input[type="number"], #wpcomm .wpd-form-row .wpdiscuz-item input[type="time"]{
    background-color: #1d1d1d;
}
#wpcomm select, #wpcomm input[type="text"], #wpcomm input[type="email"], #wpcomm input[type="url"], #wpcomm input[type="date"], #wpcomm input[type="color"]{
    border: #1d1d1d 1px solid;
}
#wpcomm .by-wpdiscuz{
    border-top: #1d1d1d solid 1px;
}
button, input, select, textarea{
    background-color: #404040;
    color: white;
}
.form-input_green{
    background: #191818;
}
.form-input_green:focus, .form-input_green:hover{
    background: #383838;
}
.share-header-icon{
    filter: brightness(0.134);
}
body .subscribe-block-wrapper input{
    background: #1d1d1d;
    box-shadow: 0 1px 0 #4c4c4c inset;
}
.green_blockquote_text{
    background: #1d1d1d;
}
}
