 body, div, dl, dt, dd, ul, ol, h1, h2, h3, h4, h5, h6, pre, code, form, fieldset, legend, input, button, textarea, p, blockquote, th, td {
    margin: 0;
    padding: 0;
    background-color: #151515;
    color: #fff;
  }
    
  .XenBase .navTabs .navTab.selected .tabLinks {
     background-color: #151515;
    margin-top: 1px;
    border-right: 1px solid rgb(202, 202, 202);
    border-left: 1px solid rgb(202, 202, 202);
    box-sizing: border-box;
}
  .xbBoxed {
    background-color: #151515;
    padding: 20px;
    margin: 20px auto;
    border: 1px solid rgb(202, 202, 202);
    max-width: 80%;
    color:#fff;
}
  
  li .nodeList .nodeInfo.primaryContent, li .nodeList .nodeInfo.categoryForumNodeInfo, li .nodeList .nodeInfo.pageNodeInfo, li .nodeList .nodeInfo.linkNodeInfo {
    background-color: rgb(21, 21, 21);
}
    .node .subForumList {
    background: rgb(21, 21, 21);
    padding: 5px 0px;
    margin: 10px;
    border: 1px solid rgb(21, 21, 21);
    clear: left;
}
  .nodeList .categoryStrip {
    font-size: 11pt;
    color: rgb(250, 250, 250);
    background-color: rgb(21, 21, 21);
    background-repeat: repeat-x;
    background-position: center;
    padding: 5px 10px;
    margin: 3px auto 0;
    border-radius: 0px;
    box-shadow: inset 0px 1px 0px 0px rgb(8, 94, 169);
    text-shadow: 0px 0px 4px rgb(60, 60, 60);
    padding: 10px;
    margin: 0;
    border-radius: 0;
    min-height: 6px;
}
  .breadcrumb {
    font-size: 11px;
    background-color: rgb(21, 21, 21);
    background-repeat: repeat-x;
    background-position: top;
    border: 1px solid rgb(202,202,202);
    border-radius: 0;
    overflow: hidden;
    max-width: 100%;
    height: 24px;
}
  .navTabs {
    font-size: 12pt;
    color: rgb(254, 254, 254);
    background-color: rgb(21, 21, 21);
    padding: 0 25px;
    box-shadow: inset 0px 1px 0px 0px rgb(8, 34, 66);
    height: 48px;
}
  #content .pageContent {
    background-color: rgb(21, 21, 21);
    padding: 10px;
    margin-top: -1px;
    border-color: rgb(21, 21, 21);
    border-right: 1px solid rgb(21, 21, 21);
    border-left: 1px solid rgb(21, 21, 21);
    border-radius: 0;
}
  .footer .pageContent {
    font-size: 11px;
    color: rgb(200, 200, 200);
    background-color: rgb(21, 21, 21);
    padding-top: 5px;
    padding-bottom: 5px;
    overflow: hidden;
}
 .sidebar .secondaryContent, .xengallerySideBar .secondaryContent {
    background: rgb(21, 21, 21) none;
    border-bottom-style: none;
    border-radius: 0;
} 
  .navTabs .navTab.selected .tabLinks a:hover, .navTabs .navTab.selected .tabLinks a:focus {
    color: rgb(24, 117, 187);
    text-decoration: none;
    background-color: rgb(21, 21, 21);
    padding: 0 9px;
    border: 1px solid rgb(21, 21, 21);
    border-radius: 2px;
    outline: 0;
}
  .blockLinksList a:hover, .blockLinksList a:focus, .blockLinksList li.kbSelect a, .blockLinksList label:hover, .blockLinksList label:focus, .blockLinksList li.kbSelect label {
    color: rgb(250, 250, 250);
    text-decoration: none;
    background-color: rgb(21, 21, 21);
    border-radius: 2px;
}
  .xbUserPanelBox {
    font-weight: bold;
    font-size: 14px;
    color: rgb(250, 250, 250);
    background: rgb(21, 21, 21);
    padding: 12px 10px;
    margin-top: 10px;
    margin-right: 20px;
    border: 1px solid rgb(21, 21, 21);
    border-radius: 5px;
    display: block;
    float: right;
}
/*Nikola Bobetic*/
/*Gaming Niksy*/
/*nikola-bobetic.iz.hr*/
/*/c)2017*/
/*Contact me:*/
/*info@nikola-bobetic.iz.hr */
  .textCtrl {
    font-size: 13px;
    font-family: Calibri,'Trebuchet MS',Verdana,Geneva,Arial,Helvetica,sans-serif;
    color: #fff;
    background-color: rgb(74, 74, 74);
    padding: 8px 5px;
    margin-bottom: 2px;
    border: 1px solid rgb(213,213,213);
    border-radius: 1px;
    outline: 0;
}
 .navBarHelper {
    background-color: rgb(19, 86, 148);
    margin-top: -1px;
    border: 1px solid rgb(21, 21, 21);
    box-sizing: border-box;
    position: absolute;
    box-shadow: inset 0px 1px 0px 0px rgb(8, 34, 66);
    width: 100%;
    height: 50px;
}
 .nodeList li.level_2:nth-child(2n+1) .primaryContent:hover, .nodeList li.level_2:nth-child(2n+1) .linkNodeInfo:hover, .nodeList li.level_2:nth-child(2n+1) .pageNodeInfo:hover {
    background: rgb(21, 21, 21);
    box-shadow: inset 0px 1px 0px 0px #151515;
} 
  .nodeList li.level_2:nth-child(2n) .primaryContent:hover, .nodeList li.level_2:nth-child(2n) .linkNodeInfo:hover, .nodeList li.level_2:nth-child(2n) .pageNodeInfo:hover {
    background: rgb(21, 21, 21);
    box-shadow: inset 0px 1px 0px 0px #fff;
}
.XenBase .discussionListItem .posterAvatar {
    background: #151515 none;
}  
  .XenBase .discussionListItem .stats {
    background: #151515 none;
}
/*Nikola Bobetic*/
/*Gaming Niksy*/
/*nikola-bobetic.iz.hr*/
/*/c)2017*/
/*Contact me:*/
/*info@nikola-bobetic.iz.hr */
  .message .messageInfo {
    background-color: #151515;
    padding: 10px;
    margin-left: 155px;
    border-bottom: 1px none black;
}
  .XenBase .attachedFiles .attachmentList {
    background-color: rgb(21, 21, 21);
    background-image: none;
}
  .attachedFiles .attachmentList {
    background: rgb(21, 21, 21);
}
.attachedFiles .attachmentList {
    overflow: hidden;
    padding: 10px 10px 0;
    border-radius: 5px;
}
  .messageList .message {
    background-color: rgb(21, 21, 21);
    padding: 1px;
    margin-bottom: 20px;
    border: 1px solid rgb(202, 202, 202);
}
  .messageUserBlock .extraUserInfo.xbBoxedFA .xbMessages, .messageUserBlock .extraUserInfo.xbBoxedFA .xbLikes, .messageUserBlock .extraUserInfo.xbBoxedFA .xbTrophyPoints {
    font-size: 120%;
    background-color: rgb(21, 21, 21);
    margin: 2px 0px;
    border: 1px solid rgb(21, 21, 21);
    border-radius: 2px;
    display: inline-block;
    box-sizing: border-box;
    min-width: 29%;
}
/*Nikola Bobetic*/
/*Gaming Niksy*/
/*nikola-bobetic.iz.hr*/
/*/c)2017*/
/*Contact me:*/
/*info@nikola-bobetic.iz.hr */
  .bbCodeQuote .quoteContainer {
    overflow: hidden;
    position: relative;
    font-style: italic;
    font-size: 9pt;
    background-color: rgb(21, 21, 21);
    padding: 10px;
    border-top: 1px solid rgb(213, 213, 213);
}
  .redactor_toolbar {
    font-size: 0;
    background-color: rgb(21, 21, 21);
    padding: 3px 2px;
    margin: 0;
    border-top: 1px solid rgb(21, 21, 21);
    border-bottom: 1px solid rgb(21, 21, 21);
    position: relative;
    left: 0;
    top: 0;
    line-height: 0;
    list-style: none;
    overflow: hidden;
}
  .redactor_toolbar li.redactor_btn_group ul {
    background-color: rgb(213, 213, 213);
    padding: 1px;
    margin: 0;
    border: 1px solid rgb(202, 202, 202);
    border-radius: 3px;
    overflow: hidden;
}
/*Nikola Bobetic*/
/*Gaming Niksy*/
/*nikola-bobetic.iz.hr*/
/*/c)2017*/
/*Contact me:*/
/*info@nikola-bobetic.iz.hr */
  .redactor_toolbar li a {
    font-size: 0;
    color: rgb(225, 24, 24);
    text-decoration: none;
    border: 1px solid transparent;
    border-radius: 3px;
    box-sizing: border-box;
    display: block;
    line-height: 0;
    cursor: pointer;
    outline: none;
    text-indent: -9999px;
    width: 24px;
    height: 24px;
}
  .quickReply {
    background-color: rgb(21, 21, 21);
    padding: 1px;
    margin-bottom: 20px;
    border: 1px solid rgb(21, 21, 21);
}
  .primaryContent {
    background-color: rgb(21, 21, 21);
    padding: 10px;
    border-bottom: 1px solid rgb(21, 21, 21);
}
  .profilePage .mast .followBlocks .primaryContent.avatarHeap, .profilePage .mast .section.infoBlock {
    background: rgb(21, 21, 21);
    border: 1px solid rgb(21, 21, 21);
    border-radius: 0;
}
 .secondaryContent {
    color: rgb(243, 243, 243);
    background-color: rgb(21, 21, 21);
    padding: 10px;
    border-bottom: 1px solid rgb(213, 213, 213);
  }
  .profilePage .tabs.mainTabs {
    padding: 0 20px;
    margin-top: -23px;
    min-height: 23px;
    height: auto;
    position: relative;
    background: rgb(21, 21, 21);
}
  .Menu .primaryContent {
    background-color: rgb(21, 21, 21);
    color: rgb(250, 250, 250);
    border-bottom: 2px solid rgba(154, 154, 154, 0);
}
  .blockLinksList {
    font-size: 10pt;
    background-color: rgb(21, 21, 21);
    padding: 2px;
    color: #fff;
}
  .Menu .secondaryContent {
    background: rgb(21, 21, 21);
    color: rgb(24, 117, 187);
}
  /*Nikola Bobetic*/
/*Gaming Niksy*/
