css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("codingbat.com") {
  
  body {
    background-color: #ccc;
  }
  
  #selected {
    background: #ccc;
  }
  
  div.summ {
    background-color: #dedede;
  }
  
  /* - Images -
  
     The default images are replaced using only CSS,
     by images with transparent backgrounds by hiding the image,
     giving the element a padding of the original image size and
     updating its background.
  */
  
  img[src="/c1.jpg"] { /* Check 1 */
    width: 0;
    height: 0;
    padding: 8px 8px 8px 7px;
    background: url('data:image/png;base64,');
  }
  
  img[src="/c2.jpg"] {  /* Check 2 */
    width: 0;
    height: 0;
    padding: 8px 8px 8px 7px;
    background: url('data:image/png;base64,');
  }
  
  img[src="/c3.jpg"] {  /* Check 3 */
    width: 0;
    height: 0;
    padding: 8px 8px 8px 7px;
    background: url('data:image/png;base64,');
  }
  
  img[src="/s1.jpg"] { /* Star 1 */
    width: 0;
    height: 0;
    padding: 10px;
    background: url('data:image/png;base64,');
  }
  
  img[src="/s2.jpg"] { /* Star 2 */
    width: 0;
    height: 0;
    padding: 10px;
    background: url('data:image/png;base64,');
  }
  
  img[src="/s5j.png"],
  img[src="/s10j.png"],
  img[src="/s25j.png"],
  img[src="/s5p.png"],
  img[src="/s10p.png"],
  img[src="/s25p.png"],
  img[src="/c2big.jpg"] {  /* Give the big images a border */
    border: 1px solid #666;
  }
  
  /* - Ace editor -
     
     The editor which Codingbat currently uses.
     Give it a modified version of the 'Roboticket' color theme.
  */
  .ace_line {
    color: #585858;
  }
  
  .ace-chrome .ace_print-margin {
    background: #F5F5F5;
  }
  
  .ace-chrome .ace_indent-guide {
    background: url('data:image/false;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAACCAYAAACZgbYnAAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAADsQAAA7EAZUrDhsAAAAQSURBVBhXYwCC/0wMDAwMAAYOAQKxeSF9AAAAAElFTkSuQmCC') right repeat-y;
    opacity: 0.25;
  }
  
  .ace_content {
    color: #585858;
    background: #F5F5F5;
  }
  
  /* Keywords */
  .ace-chrome .ace_storage, .ace-chrome .ace_keyword, .ace-chrome .ace_meta.ace_tag {
    color: #295F94;
    font-weight: bold;
  }
  
  .ace-chrome .ace_support.ace_function {
    color: #AB2525;
  }
  
  /* Constants - Numbers */
  .ace-chrome .ace_constant.ace_numeric {
    color: #AF0F91;
  }
  
  /* Constants - Other */
  .ace-chrome .ace_constant.ace_language {
    color: #0A0B0C;
  }
  
  /* Comments (Single and multiline) */
  .ace-chrome .ace_comment.ace_doc,
  .ace-chrome .ace_comment{
    color: #AD95AF;
  }
  
  .ace-chrome .ace_comment.ace_doc.ace_tag {
    color: #CD8B00;
    font-weight: bold;
  }
  
}
