css:
@-moz-document domain("friends.nico") {

.column {
    flex-grow: 1;
    flex-shrink: 1;
}
body {
    font-family: Avenir, Open Sans, Helvetica Neue, Helvetica, Arial, Verdana, Roboto, ヒラギノ角ゴ Pro W6, Hiragino Kaku Gothic Pro, Meiryo UI, メイリオ, Meiryo, MS\ Ｐゴシック, MS PGothic, sans-serif;
}
@media screen and (min-width: 1024px) {
    .column, .drawer {
        padding: 0;
        margin: 2px;
    }
}
@media screen and (min-width: 1024px) {
    .column:last-child, .drawer:last-child {
        padding-right: 0;
        margin-right: 0;
    }
}
@media screen and (min-width: 1024px) {
    .column:first-child, .drawer:first-child {
        padding-left: 0;
        margin-left: 0;
    }
}
.column>.scrollable {
    background: white;
}

.drawer__inner.darker {
    background-color: #fff;
    color: #272727;
}
.drawer__tab:hover,
.drawer__tab:active,
.drawer__tab:focus {
    background-color: #b2bac2
}
.drawer__inner {
    background-color: #f4f4f4;
    color: #000;
}
.drawer__header,
.account__action-bar {
    background-color: #272727;
}
.drawer__tab {
    color: #F5F8FA;
}
.drawer__header a:hover {
    background-color: #555;
}
.navigation-bar strong {
    color: #000;
}
@media screen and (min-width: 1024px) {
    .column-icon-clear {
        top: 0;
    }
}
@media screen and (min-width: 1024px) {
    .column {
        box-shadow: 0 1px 2px rgba(0, 0, 0, 0.50);
    }
}
.button {
    background-color: #b2bac2;
    color: #fff;
    border: 1px transparent solid !important;
}
.button:hover {
    color: #fff;
    background-color: #007cff
}
.button:focus {
    text-decoration: underline !important;
}
.button:disabled {
    background-color: #069 !important;
    color: #fff !important;
    cursor: default;
}
.compose-form__buttons,
.icon-button.inverted,
.text-icon-button {
    color: #fff;
    background-color: #b2bac2;
    border-bottom: 2px transparent solid;
}
.text-icon-button:hover,
.icon-button.inverted:hover {
    color: #fff;
    background-color: #b2bac2;
    border-bottom: 2px #fff solid;
}
.text-icon-button:focus,
.icon-button.inverted:focus {
    color: black;
    border-bottom: 2px transparent solid;
}
.text-icon-button.active {
    background-color: rgba(255, 255, 255, 0.5);
    color: black;
    border-bottom: 2px #fff solid;
}
.column-header,
.column-icon,
.column-back-button {
    color: #666;
    background-color: #e7e7e7;
}
.collapsable {
    background-color: #393f4f;
    color: #fff
}
.column-back-button:hover,
.column-icon:hover,
.collapsable:hover {
    color: #007cff;
}
.status .status__display-name {
    color: #999;
}
.status__display-name strong {
    color: #666;
}
.status .status__relative-time {
    color: #999;
}
.status__content a,
.reply-indicator__content a {
    color: #069 !important;
}
.collapsable {
    background: #666;
}
.column-settings--outer {
    background: #666;
}
.setting-toggle {
    color: #fff;
}
.column-settings--section {
    color: #fff;
}
.account__header>div {
    background: -moz-linear-gradient(top, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 0.35) 70%, rgba(0, 0, 0, 0.7) 100%);
    background: -webkit-linear-gradient(top, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 0.35) 70%, rgba(0, 0, 0, 0.7) 100%);
    background: linear-gradient(to bottom, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 0.35) 70%, rgba(0, 0, 0, 0.7) 100%);
}
.getting-started p {
    color: #272727;
}
.status__content a,
.reply-indicator__content a {
    color: #007cff
}
.status__content .status__content__spoiler-link,
.reply-indicator__content .status__content__spoiler-link,
.muted a.status__content__spoiler-link {
    transition: 150ms;
    background-color: #007cff;
    color: #fff;
    border: 1px #007cff solid
}
.report__target,
.report__target strong {
    color: #272727;
}
.report__target,
.status-check-box {
    border-bottom: 1px solid #e1e8ed;
}
.load-more:hover {
    background-color: #007cff;
    color: #fff;
}
.report__textarea {
    color: #272727;
}
.status__content .status__content__spoiler-link:hover,
.muted a.status__content__spoiler-link:hover,
.load-more,
.status-card {
    color: #007cff;
    background-color: #fff;
    -webkit-touch-callout: none;
    -webkit-user-select: none;
    -khtml-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
}
.account__header .account__header__username {
    color: #F5F5F5;
}
.status,
.detailed-status,
.account,
.status-check-box .status__content {
    border-bottom: 1px solid #e1e8ed;
    background-color: #fff;
    color: #272727;
}
.detailed-status__action-bar {
    background-color: #fff;
    color: #272727;
    border-top: transparent;
    border-bottom: 1px solid #e1e8ed;
}
.status__prepend,
.notification__message,
.column,
.notification {
    background-color: #fff;
    color: #272727;
}
.search-results__hashtag:hover,
.search-results__hashtag:active,
.search-results__hashtag:focus {
    color: #272727
}
.search-results__hashtag {
    color: #272727;
}
.ui {
    background: #e1e8ed !important;
}
.search__input {
    transition: 300ms;
    background: #272727;
}
.search__input:focus {
    box-shadow: 0 8px 16px rgba(0, 0, 0, 0.20);
}
.search-results__header {
    color: #272727;
    background-color: #eaeaea;
    border: none;
}
.detailed-status__display-name strong,
.detailed-status__display-name strong,
.detailed-status__display-name span {
    color: #272727;
}
.status__display-name:hover strong,
.reply-indicator__display-name:hover strong,
.detailed-status__display-name:hover strong,
.account__display-name:hover strong,
.notification__display-name:hover,
.status__relative-time:hover {
    color: #007cff;
}
.status__display-name strong,
.account__display-name strong {
    color: #272727;
}
.notification__message .fa {
    color: #007cff;
}
.icon-button.active {
    color: #007cff;
}
.account .account__display-name {
    color: #BBB;
}
.muted .status__content p,
.muted .status__content a {
    color: #666;
}
.muted .status__display-name strong {
    color: #666;
}
::-webkit-scrollbar-thumb {
    background: #999;
}
::-webkit-scrollbar-thumb:hover {
    background: #999;
}
::-webkit-scrollbar-track:hover {
    background: #DDD;
}
.column-link {
    background: #272727;
}
::-webkit-input-placeholder {
    color: #DDD;
}
:-ms-input-placeholder {
    color: #DDD;
}
::-moz-placeholder {
    color: #DDD;
}
.setting-text {
    border-bottom: 2px solid #BBB;
}
.search__input {
    color: #fff;
}
.search__icon .fa {
    color: #DDD;
}
.search__icon .fa.active {
    opacity: 1;
}
.account__action-bar__tab>span {
    color: #FFF;
}
.account__action-bar__tab abbr {
    color: #FFF;
}
.getting-started a {
    color: #069;
}
.drawer__pager a {
    color: #069 !important;
}
.drawer__inner {
    background: linear-gradient(#f4f4f4, #DDD);
}

}
