css:
@-moz-document url-prefix('https://dlm.carsforsale.com/forum/View.aspx') {
/* Change Post Title */
.feedtable td .posttable tr:nth-of-type(1) td:nth-of-type(1) {
	background-color:#4F76B9;
    color:#fff;
    padding:10px 10px;
    margin-top:10px;
    font-size:14px;
    border-radius: 8px;
    padding-left:20px;
}

/* Hide Line */
.posttable tr:nth-of-type(2){
	display:none;
}

/* Fix Tables in postcontent */
.feedtable td .posttable tr:nth-of-type(1) td:nth-of-type(1) table tr {
 	display:inline;
}

/* Clean up other things that title CSS put a blue box on */
#UpdateRegion tr td {
	background-color:transparent;
}

/*Make Hide lines in updates content */
#UpdateRegion img {
	display:none;
}

/* Make updates title red */
#UpdateRegion label {
	background-color:#D80000;
    color:#fff;
    padding:10px 10px;
    margin-top:10px;
    font-size:14px;
    border-radius: 8px;
    padding-left:20px;
    width:100%;
}

/* Fix updates title width*/
#UpdateRegion {
	width:93%;
    margin:0 40px 0 40px;
}

/* Fix where hide line removes update content */
#UpdateRegion table tr:nth-of-type(2) {
	display:block;
}

/* Fix alingment of updates content*/
#UpdateRegion table tr:nth-of-type(2) {
	    text-align: left;
}

/* Set the font-size for the date and time */
.posttable td:nth-of-type(2) .poststamp, #UpdateRegion table td:nth-of-type(2) .poststamp {
	font-size:12px;
}

/* Set the width for the date and time */
.posttable td:nth-of-type(2) {
    width:120px;
}

/* Fix the width for the update date and time */
#UpdateRegion table td:nth-of-type(2) {
    width:200px;
    position:relative;
    left:70px;
}

/* Make poster name background */
.posttable td:nth-child(1) .poststamp {
    background-color:#325083;
    color:#fff;
    padding:5px 20px;;
    font-size:14px;
    border-radius: 8px;
    position:relative;
    right:-20px;
}

/* Fix poster name background color in updates */
#UpdateRegion tr td:nth-of-type(2) .poststamp {
	background-color:transparent;
    color:#999999;
}

/* Fix poster name to a fixed width and move up into title */
.posttable tr:nth-child(3) td:nth-of-type(1) {
    width:890px;
    display:block;
	text-align:right;
    top:-38px;
    position:relative;
    background-color:transparent;
}

/* Fix poster name in updates to a fixed width and move up into title */
#UpdateRegion table tr td:nth-of-type(1) {
    width:710px;
    display:block;
	text-align:right;
    top:-58px;
    position:relative;
    background-color:transparent;
}
/* Fix position of update poster */
#UpdateRegion table tr td:nth-of-type(1) span {
    right:-250px;
    position:relative;

}
/* Fix alignment of update content text */
#UpdateRegion table tr:nth-of-type(2) td:nth-of-type(1) {
	text-align:left;
}
    
/*fix padding on PostContent*/
#PostContent {
	margin:-28px 0 -30px 0;
}

/* Add padding to images in posts */
#PostContent a img {
	padding:0 20px 20px 0;
}
}
