css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("trimps.github.io") {

.battleCell.cellColorNotBeaten.Corruption {
    background: -webkit-linear-gradient(top, #33095b 0%,#23063f 100%) !important;
}

.battleCell.cellColorBeaten {
    background: -webkit-linear-gradient(top, #008d00 0%, #006c00 100%) !important;
}

#grid .battleRow {
    background: -webkit-linear-gradient(top, #999999 0%,#686868 90%) !important;
}

#mapGrid {
    background: rgba(0,0,0,0) !important;
}

#mapGrid .cellColorNotBeaten {
    background: -webkit-linear-gradient(top, #b9b9b9 0%,#686868 100%) !important;
}

#innerWrapper {
    background: -webkit-linear-gradient(left, #323341 12%,#626368 29.5%,#727275 47%,#727275 53%,#4c4d56 70.5%,#323341 88%) !important;
}

.cellColorOverkill {
    background: -webkit-linear-gradient(top, #74e800 0%,#5ab500 100%) !important;
}

#mapGrid .cellColorNotBeaten.voidCell {
    background: -webkit-linear-gradient(top, #33095b 0%,#23063f 100%) !important;
}

.spireCell.cellColorNotBeaten {
    background: -webkit-linear-gradient(top, #9b96b7 0%,#85819e 100%) !important;
}

.battleCell.Magma.cellColorNotBeaten {
    background: -webkit-linear-gradient(top, #960000 0%,#7c0000 100%) !important;
}

.battleCell.empoweredCellPoison {
    background: -webkit-linear-gradient(top,rgba(60, 180, 60, 0.75) 0%, rgba(45, 135, 45, 0.75) 100%) !important;
}

.battleCell.empoweredCellWind {
    background: -webkit-linear-gradient(top,rgba(72, 90, 156, 0.75) 0%, rgba(54, 68, 117, 0.75) 100%) !important;
}

.battleCell.empoweredCellIce {
    background: -webkit-linear-gradient(top,rgba(60, 60, 240, 0.75) 0%, rgba(45, 45, 180, 0.75) 100%) !important;
}

#grid.liquid ul#row9 li {
    background: -webkit-linear-gradient(top, #ffff00 0%,#f7f700 100%) !important;
}

#grid.liquid ul#row8 li {
    background: -webkit-linear-gradient(top, #f7f700 0%,#f0f000 100%) !important;
}

#grid.liquid ul#row7 li {
    background: -webkit-linear-gradient(top, #f0f000 0%,#e8e800 100%) !important;
}

#grid.liquid ul#row6 li {
    background: -webkit-linear-gradient(top, #e8e800 0%,#e0e000 100%) !important;
}

#grid.liquid ul#row5 li {
    background: -webkit-linear-gradient(top, #e0e000 0%,#d9d900 100%) !important;
}

#grid.liquid ul#row4 li {
    background: -webkit-linear-gradient(top, #d9d900 0%,#d1d100 100%) !important;
}

#grid.liquid ul#row3 li {
    background: -webkit-linear-gradient(top, #d1d100 0%,#c9c900 100%) !important;
}

#grid.liquid ul#row2 li {
    background: -webkit-linear-gradient(top, #c9c900 0%,#c1c100 100%) !important;
}

#grid.liquid ul#row1 li {
    background: -webkit-linear-gradient(top, #c1c100 0%,#baba00 100%) !important;
}

#grid.liquid ul#row0 li {
    background: -webkit-linear-gradient(top, #baba00 0%,#b2b200 100%) !important;
}

.battleCell.cellColorNotBeaten.Healthy
{
  background: -webkit-linear-gradient(top, #493519 0%,#312311 100%) !important;
}

#mapGrid .battleCell.cellColorNotBeaten.mapPumpkimp {
    background: -webkit-linear-gradient(top, #BF6000 0%,#8C4600 100%) !important;
}

.battleCell.cellColorNotBeaten.Pumpkimp {
    background: -webkit-linear-gradient(top, #F27900, #BF5F00) !important;
}

#row9 .battleCell.cellColorNotBeaten.Pumpkimp {
    background: -webkit-linear-gradient(top, #008000, #004D00) !important;
}

.battleCell.cellColorCurrent, .battleCell.empoweredCellIce.cellColorCurrent, .battleCell.empoweredCellPoison.cellColorCurrent, .battleCell.empoweredCellWind.cellColorCurrent {
    background: -webkit-linear-gradient(top, #ffff00 0%,#b2b200 100%) !important;
}

.battleCell.cellColorNotBeaten.Pumpkimp.Corruption {
    background: -webkit-linear-gradient(top, #803345, #4D1E2A) !important;
}

.battleCell.cellColorNotBeaten.Pumpkimp.Healthy {
    background: -webkit-linear-gradient(top, #A65B0D, #733F09) !important;
}

.battleCell.cellColorNotBeaten.Pumpkimp.Magma {
    background: -webkit-linear-gradient(top, #BF2903, #8C1E03) !important;
}

#grid.liquid ul#row0 li.battleCell.cellColorNotBeaten.Pumpkimp, #grid.liquid ul#row1 li.battleCell.cellColorNotBeaten.Pumpkimp, #grid.liquid ul#row2 li.battleCell.cellColorNotBeaten.Pumpkimp, #grid.liquid ul#row3 li.battleCell.cellColorNotBeaten.Pumpkimp, #grid.liquid ul#row4 li.battleCell.cellColorNotBeaten.Pumpkimp, #grid.liquid ul#row5 li.battleCell.cellColorNotBeaten.Pumpkimp, #grid.liquid ul#row6 li.battleCell.cellColorNotBeaten.Pumpkimp, #grid.liquid ul#row7 li.battleCell.cellColorNotBeaten.Pumpkimp, #grid.liquid ul#row8 li.battleCell.cellColorNotBeaten.Pumpkimp {
    background: -webkit-linear-gradient(top, #F27900, #BF5F00) !important;
}

#grid.liquid ul#row9 li.battleCell.cellColorNotBeaten.Pumpkimp {
    background: -webkit-linear-gradient(top, #008000, #004D00) !important;
}

}
